00890         // Sets the Culling mode of the material
00891         protected override void SetCulling()
00892         {
00893             if (m_isCullingEnabled)
00894             {
00895                 m_renderer.material.SetFloat("_CullMode", 2);
00896 
00897                 for (int i = 1; i < m_subTextObjects.Length && m_subTextObjects[i] != null; i++)
00898                 {
00899                     Renderer renderer = m_subTextObjects[i].renderer;
00900 
00901                     if (renderer != null)
00902                     {
00903                         renderer.material.SetFloat(ShaderUtilities.ShaderTag_CullMode, 2);
00904                     }
00905                 }
00906             }
00907             else
00908             {
00909                 m_renderer.material.SetFloat("_CullMode", 0);
00910 
00911                 for (int i = 1; i < m_subTextObjects.Length && m_subTextObjects[i] != null; i++)
00912                 {
00913                     Renderer renderer = m_subTextObjects[i].renderer;
00914 
00915                     if (renderer != null)
00916                     {
00917                         renderer.material.SetFloat(ShaderUtilities.ShaderTag_CullMode, 0);
00918                     }
00919                 }
00920             }
00921         }
