00952         protected override float GetPaddingForMaterial()
00953         {
00954             ShaderUtilities.GetShaderPropertyIDs();
00955 
00956             if (m_sharedMaterial == null) return 0;
00957 
00958             m_padding = ShaderUtilities.GetPadding(m_sharedMaterial, m_enableExtraPadding, m_isUsingBold);
00959             m_isMaskingEnabled = ShaderUtilities.IsMaskingEnabled(m_sharedMaterial);
00960             m_isSDFShader = m_sharedMaterial.HasProperty(ShaderUtilities.ID_WeightNormal);
00961 
00962             return m_padding;
