00965 
00966         // This function parses through the Char[] to determine how many characters will be visible. It then makes sure the arrays are large enough for all those characters.
00967         protected override int SetArraySizes(int[] chars)
00968         {
00969             //Debug.Log("*** SetArraySizes() ***");
00970 
00971             int tagEnd = 0;
00972             int spriteCount = 0;
00973 
00974             m_totalCharacterCount = 0;
00975             m_isUsingBold = false;
00976             m_isParsingText = false;
00977             tag_NoParsing = false;
00978             m_style = m_fontStyle;
00979 
00980             m_fontWeightInternal = (m_style & FontStyles.Bold) == FontStyles.Bold ? 700 : m_fontWeight;
00981             m_fontWeightStack.SetDefault(m_fontWeightInternal);
00982 
00983             m_currentFontAsset = m_fontAsset;
00984             m_currentMaterial = m_sharedMaterial;
00985             m_currentMaterialIndex = 0;
00986 
00987             m_materialReferenceStack.SetDefault(new MaterialReference(m_currentMaterialIndex, m_currentFontAsset, null, m_currentMaterial, m_padding));
00988 
00989             m_materialReferenceIndexLookup.Clear();
00990             MaterialReference.AddMaterialReference(m_currentMaterial, m_currentFontAsset, m_materialReferences, m_materialReferenceIndexLookup);
00991 
00992             if (m_textInfo == null) m_textInfo = new TMP_TextInfo();
00993             m_textElementType = TMP_TextElementType.Character;
00994 
00995             // Clear Linked Text object if we have one.
00996             if (m_linkedTextComponent != null)
00997             {
00998                 m_linkedTextComponent.text = string.Empty;
00999                 m_linkedTextComponent.ForceMeshUpdate();
01000             }
01001 
01002             // Parsing XML tags in the text
01003             for (int i = 0; i < chars.Length && chars[i] != 0; i++)
01004             {
01005                 //Make sure the characterInfo array can hold the next text element.
01006                 if (m_textInfo.characterInfo == null || m_totalCharacterCount >= m_textInfo.characterInfo.Length)
01007                     TMP_TextInfo.Resize(ref m_textInfo.characterInfo, m_totalCharacterCount + 1, true);
01008 
01009                 int c = chars[i];
01010 
01011                 // PARSE XML TAGS
01012                 #region PARSE XML TAGS
01013                 if (m_isRichText && c == 60) // if Char '<'
01014                 {
01015                     int prev_MaterialIndex = m_currentMaterialIndex;
01016 
01017                     // Check if Tag is Valid
01018                     if (ValidateHtmlTag(chars, i + 1, out tagEnd))
01019                     {
01020                         i = tagEnd;
01021 
01022                         if ((m_style & FontStyles.Bold) == FontStyles.Bold) m_isUsingBold = true;
01023 
01024                         if (m_textElementType == TMP_TextElementType.Sprite)
01025                         {
01026                             m_materialReferences[m_currentMaterialIndex].referenceCount += 1;
01027 
01028                             m_textInfo.characterInfo[m_totalCharacterCount].character = (char)(57344 + m_spriteIndex);
01029                             m_textInfo.characterInfo[m_totalCharacterCount].spriteIndex = m_spriteIndex;
01030                             m_textInfo.characterInfo[m_totalCharacterCount].fontAsset = m_currentFontAsset;
01031                             m_textInfo.characterInfo[m_totalCharacterCount].spriteAsset = m_currentSpriteAsset;
01032                             m_textInfo.characterInfo[m_totalCharacterCount].materialReferenceIndex = m_currentMaterialIndex;
01033                             m_textInfo.characterInfo[m_totalCharacterCount].elementType = m_textElementType;
01034 
01035                             // Restore element type and material index to previous values.
01036                             m_textElementType = TMP_TextElementType.Character;
01037                             m_currentMaterialIndex = prev_MaterialIndex;
01038 
01039                             spriteCount += 1;
01040                             m_totalCharacterCount += 1;
01041                         }
01042 
01043                         continue;
01044                     }
01045                 }
01046                 #endregion
01047 
01048 
01049                 bool isUsingFallback = false;
01050                 bool isUsingAlternativeTypeface = false;
01051 
01052                 TMP_Glyph glyph;
01053                 TMP_FontAsset tempFontAsset;
01054                 TMP_FontAsset prev_fontAsset = m_currentFontAsset;
01055                 Material prev_material = m_currentMaterial;
01056                 int prev_materialIndex = m_currentMaterialIndex;
01057 
01058 
01059                 // Handle Font Styles like LowerCase, UpperCase and SmallCaps.
01060                 #region Handling of LowerCase, UpperCase and SmallCaps Font Styles
01061                 if (m_textElementType == TMP_TextElementType.Character)
01062                 {
01063                     if ((m_style & FontStyles.UpperCase) == FontStyles.UpperCase)
01064                     {
01065                         // If this character is lowercase, switch to uppercase.
01066                         if (char.IsLower((char)c))
01067                             c = char.ToUpper((char)c);
01068 
01069                     }
01070                     else if ((m_style & FontStyles.LowerCase) == FontStyles.LowerCase)
01071                     {
01072                         // If this character is uppercase, switch to lowercase.
01073                         if (char.IsUpper((char)c))
01074                             c = char.ToLower((char)c);
01075                     }
01076                     else if ((m_fontStyle & FontStyles.SmallCaps) == FontStyles.SmallCaps || (m_style & FontStyles.SmallCaps) == FontStyles.SmallCaps)
01077                     {
01078                         // Only convert lowercase characters to uppercase.
01079                         if (char.IsLower((char)c))
01080                             c = char.ToUpper((char)c);
01081                     }
01082                 }
01083                 #endregion
01084 
01085 
01086                 // Handling of font weights.
01087                 #region HANDLING OF FONT WEIGHT
01088                 tempFontAsset = GetFontAssetForWeight(m_fontWeightInternal);
01089                 if (tempFontAsset != null)
01090                 {
01091                     isUsingFallback = true;
01092                     isUsingAlternativeTypeface = true;
01093                     m_currentFontAsset = tempFontAsset;
01094                     //m_currentMaterialIndex = MaterialReference.AddMaterialReference(m_currentMaterial, tempFontAsset, m_materialReferences, m_materialReferenceIndexLookup);
01095                 }
01096                 #endregion
01097 
01098 
01099                 // Lookup the Glyph data for each character and cache it.
01100                 #region LOOKUP GLYPH
01101                 tempFontAsset = TMP_FontUtilities.SearchForGlyph(m_currentFontAsset, c, out glyph);
01102 
01103                 // Search for the glyph in the Sprite Asset assigned to the text object.
01104                 if (glyph == null)
01105                 {
01106                     TMP_SpriteAsset spriteAsset = this.spriteAsset;
01107 
01108                     if (spriteAsset != null)
01109                     {
01110                         int spriteIndex = -1;
01111 
01112                         // Check Default Sprite Asset and its Fallbacks
01113                         spriteAsset = TMP_SpriteAsset.SearchForSpriteByUnicode(spriteAsset, c, true, out spriteIndex);
01114 
01115                         if (spriteIndex != -1)
01116                         {
01117                             m_textElementType = TMP_TextElementType.Sprite;
01118                             m_textInfo.characterInfo[m_totalCharacterCount].elementType = m_textElementType;
01119 
01120                             m_currentMaterialIndex = MaterialReference.AddMaterialReference(spriteAsset.material, spriteAsset, m_materialReferences, m_materialReferenceIndexLookup);
01121                             m_materialReferences[m_currentMaterialIndex].referenceCount += 1;
01122 
01123                             m_textInfo.characterInfo[m_totalCharacterCount].character = (char)c;
01124                             m_textInfo.characterInfo[m_totalCharacterCount].spriteIndex = spriteIndex;
01125                             m_textInfo.characterInfo[m_totalCharacterCount].fontAsset = m_currentFontAsset;
01126                             m_textInfo.characterInfo[m_totalCharacterCount].spriteAsset = spriteAsset;
01127                             m_textInfo.characterInfo[m_totalCharacterCount].materialReferenceIndex = m_currentMaterialIndex;
01128 
01129                             // Restore element type and material index to previous values.
01130                             m_textElementType = TMP_TextElementType.Character;
01131                             m_currentMaterialIndex = prev_materialIndex;
01132 
01133                             spriteCount += 1;
01134                             m_totalCharacterCount += 1;
01135 
01136                             continue;
01137                         }
01138                     }
01139                 }
01140 
01141                 // Search for the glyph in the list of fallback assigned in the TMP Settings (General Fallbacks).
01142                 if (glyph == null)
01143                 {
01144                     if (TMP_Settings.fallbackFontAssets != null && TMP_Settings.fallbackFontAssets.Count > 0)
01145                         tempFontAsset = TMP_FontUtilities.SearchForGlyph(TMP_Settings.fallbackFontAssets, c, out glyph);
01146                 }
01147 
01148                 // Search for the glyph in the Default Font Asset assigned in the TMP Settings file.
01149                 if (glyph == null)
01150                 {
01151                     if (TMP_Settings.defaultFontAsset != null)
01152                         tempFontAsset = TMP_FontUtilities.SearchForGlyph(TMP_Settings.defaultFontAsset, c, out glyph);
01153                 }
01154 
01155                 // TODO: Add support for using Sprite Assets like a special Emoji only Sprite Asset when UTF16 or UTF32 glyphs are requested.
01156                 // This would kind of mirror native Emoji support.
01157                 if (glyph == null)
01158                 {
01159                     TMP_SpriteAsset spriteAsset = TMP_Settings.defaultSpriteAsset;
01160 
01161                     if (spriteAsset != null)
01162                     {
01163                         int spriteIndex = -1;
01164 
01165                         // Check Default Sprite Asset and its Fallbacks
01166                         spriteAsset = TMP_SpriteAsset.SearchForSpriteByUnicode(spriteAsset, c, true, out spriteIndex);
01167 
01168                         if (spriteIndex != -1)
01169                         {
01170                             m_textElementType = TMP_TextElementType.Sprite;
01171                             m_textInfo.characterInfo[m_totalCharacterCount].elementType = m_textElementType;
01172 
01173                             m_currentMaterialIndex = MaterialReference.AddMaterialReference(spriteAsset.material, spriteAsset, m_materialReferences, m_materialReferenceIndexLookup);
01174                             m_materialReferences[m_currentMaterialIndex].referenceCount += 1;
01175 
01176                             m_textInfo.characterInfo[m_totalCharacterCount].character = (char)c;
01177                             m_textInfo.characterInfo[m_totalCharacterCount].spriteIndex = spriteIndex;
01178                             m_textInfo.characterInfo[m_totalCharacterCount].fontAsset = m_currentFontAsset;
01179                             m_textInfo.characterInfo[m_totalCharacterCount].spriteAsset = spriteAsset;
01180                             m_textInfo.characterInfo[m_totalCharacterCount].materialReferenceIndex = m_currentMaterialIndex;
01181 
01182                             // Restore element type and material index to previous values.
01183                             m_textElementType = TMP_TextElementType.Character;
01184                             m_currentMaterialIndex = prev_materialIndex;
01185 
01186                             spriteCount += 1;
01187                             m_totalCharacterCount += 1;
01188 
01189                             continue;
01190                         }
01191 
01192                     }
01193                 }
01194 
01195                 //Check if Lowercase or Uppercase variant of the character is available.
01196                 // Not sure this is necessary anyone as it is very unlikely with recursive search through fallback fonts.
01197                 //if (glyph == null)
01198                 //{
01199                 //    if (char.IsLower((char)c))
01200                 //    {
01201                 //        if (m_currentFontAsset.characterDictionary.TryGetValue(char.ToUpper((char)c), out glyph))
01202                 //            c = chars[i] = char.ToUpper((char)c);
01203                 //    }
01204                 //    else if (char.IsUpper((char)c))
01205                 //    {
01206                 //        if (m_currentFontAsset.characterDictionary.TryGetValue(char.ToLower((char)c), out glyph))
01207                 //            c = chars[i] = char.ToLower((char)c);
01208                 //    }
01209                 //}
01210 
01211                 // Replace missing glyph by the Square (9633) glyph or possibly the Space (32) glyph.
01212                 if (glyph == null)
01213                 {
01214                     // Save the original unicode character
01215                     int srcGlyph = c;
01216 
01217                     // Try replacing the missing glyph character by TMP Settings Missing Glyph or Square (9633) character.
01218                     c = chars[i] = TMP_Settings.missingGlyphCharacter == 0 ? 9633 : TMP_Settings.missingGlyphCharacter;
01219 
01220                     // Check for the missing glyph character in the currently assigned font asset.
01221                     tempFontAsset = TMP_FontUtilities.SearchForGlyph(m_currentFontAsset, c, out glyph);
01222 
01223                     if (glyph == null)
01224                     {
01225                         // Search for the missing glyph character in the TMP Settings Fallback list.
01226                         if (TMP_Settings.fallbackFontAssets != null && TMP_Settings.fallbackFontAssets.Count > 0)
01227                             tempFontAsset = TMP_FontUtilities.SearchForGlyph(TMP_Settings.fallbackFontAssets, c, out glyph);
01228                     }
01229 
01230                     if (glyph == null)
01231                     {
01232                         // Search for the missing glyph in the TMP Settings Default Font Asset.
01233                         if (TMP_Settings.defaultFontAsset != null)
01234                             tempFontAsset = TMP_FontUtilities.SearchForGlyph(TMP_Settings.defaultFontAsset, c, out glyph);
01235                     }
01236 
01237                     if (glyph == null)
01238                     {
01239                         // Use Space (32) Glyph from the currently assigned font asset.
01240                         c = chars[i] = 32;
01241                         tempFontAsset = TMP_FontUtilities.SearchForGlyph(m_currentFontAsset, c, out glyph);
01242                         if (!TMP_Settings.warningsDisabled) Debug.LogWarning("Character with ASCII value of " + srcGlyph + " was not found in the Font Asset Glyph Table. It was replaced by a space.", this);
01243                     }
01244                 }
01245 
01246                 // Determine if the font asset is still the current font asset or a fallback.
01247                 if (tempFontAsset != null)
01248                 {
01249                     if (tempFontAsset.GetInstanceID() != m_currentFontAsset.GetInstanceID())
01250                     {
01251                         isUsingFallback = true;
01252                         isUsingAlternativeTypeface = false;
01253                         m_currentFontAsset = tempFontAsset;
01254                     }
01255                 }
01256                 #endregion
01257 
01258 
01259                 m_textInfo.characterInfo[m_totalCharacterCount].elementType = TMP_TextElementType.Character;
01260                 m_textInfo.characterInfo[m_totalCharacterCount].textElement = glyph;
01261                 m_textInfo.characterInfo[m_totalCharacterCount].isUsingAlternateTypeface = isUsingAlternativeTypeface;
01262                 m_textInfo.characterInfo[m_totalCharacterCount].character = (char)c;
01263                 m_textInfo.characterInfo[m_totalCharacterCount].fontAsset = m_currentFontAsset;
01264 
01265                 if (isUsingFallback)
01266                 {
01267                     // Create Fallback material instance matching current material preset if necessary
01268                     if (TMP_Settings.matchMaterialPreset)
01269                         m_currentMaterial = TMP_MaterialManager.GetFallbackMaterial(m_currentMaterial, m_currentFontAsset.material);
01270                     else
01271                         m_currentMaterial = m_currentFontAsset.material;
01272 
01273                     m_currentMaterialIndex = MaterialReference.AddMaterialReference(m_currentMaterial, m_currentFontAsset, m_materialReferences, m_materialReferenceIndexLookup);
01274                 }
01275 
01276                 if (!char.IsWhiteSpace((char)c) && c != 0x200B)
01277                 {
01278                     // Limit the mesh of the main text object to 65535 vertices and use sub objects for the overflow.
01279                     if (m_materialReferences[m_currentMaterialIndex].referenceCount < 16383)
01280                         m_materialReferences[m_currentMaterialIndex].referenceCount += 1;
01281                     else
01282                     {
01283                         m_currentMaterialIndex = MaterialReference.AddMaterialReference(new Material(m_currentMaterial), m_currentFontAsset, m_materialReferences, m_materialReferenceIndexLookup);
01284                         m_materialReferences[m_currentMaterialIndex].referenceCount += 1;
01285                     }
01286                 }
01287 
01288                 m_textInfo.characterInfo[m_totalCharacterCount].material = m_currentMaterial;
01289                 m_textInfo.characterInfo[m_totalCharacterCount].materialReferenceIndex = m_currentMaterialIndex;
01290                 m_materialReferences[m_currentMaterialIndex].isFallbackMaterial = isUsingFallback;
01291 
01292                 // Restore previous font asset and material if fallback font was used.
01293                 if (isUsingFallback)
01294                 {
01295                     m_materialReferences[m_currentMaterialIndex].fallbackMaterial = prev_material;
01296                     m_currentFontAsset = prev_fontAsset;
01297                     m_currentMaterial = prev_material;
01298                     m_currentMaterialIndex = prev_materialIndex;
01299                 }
01300 
01301                 m_totalCharacterCount += 1;
01302             }
01303 
01304             // Early return if we are calculating the preferred values.
01305             if (m_isCalculatingPreferredValues)
01306             {
01307                 m_isCalculatingPreferredValues = false;
01308                 m_isInputParsingRequired = true;
01309                 return m_totalCharacterCount;
01310             }
01311 
01312             // Save material and sprite count.
01313             m_textInfo.spriteCount = spriteCount;
01314             int materialCount = m_textInfo.materialCount = m_materialReferenceIndexLookup.Count;
01315 
01316             // Check if we need to resize the MeshInfo array for handling different materials.
01317             if (materialCount > m_textInfo.meshInfo.Length)
01318                 TMP_TextInfo.Resize(ref m_textInfo.meshInfo, materialCount, false);
01319 
01320             // Resize SubTextObject array if necessary
01321             if (materialCount > m_subTextObjects.Length)
01322                 TMP_TextInfo.Resize(ref m_subTextObjects, Mathf.NextPowerOfTwo(materialCount + 1));
01323 
01324             // Resize CharacterInfo[] if allocations are excessive
01325             if (m_textInfo.characterInfo.Length - m_totalCharacterCount > 256)
01326                 TMP_TextInfo.Resize(ref m_textInfo.characterInfo, Mathf.Max(m_totalCharacterCount + 1, 256), true);
01327 
01328 
01329             // Iterate through the material references to set the mesh buffer allocations
01330             for (int i = 0; i < materialCount; i++)
01331             {
01332                 // Add new sub text object for each material reference
01333                 if (i > 0)
01334                 {
01335                     if (m_subTextObjects[i] == null)
01336                     {
01337                         m_subTextObjects[i] = TMP_SubMesh.AddSubTextObject(this, m_materialReferences[i]);
01338 
01339                         // Not sure this is necessary
01340                         m_textInfo.meshInfo[i].vertices = null;
01341                     }
01342                     //else if (m_subTextObjects[i].gameObject.activeInHierarchy == false)
01343                     //    m_subTextObjects[i].gameObject.SetActive(true);
01344 
01345                     // Check if the material has changed.
01346                     if (m_subTextObjects[i].sharedMaterial == null || m_subTextObjects[i].sharedMaterial.GetInstanceID() != m_materialReferences[i].material.GetInstanceID())
01347                     {
01348                         bool isDefaultMaterial = m_materialReferences[i].isDefaultMaterial;
01349 
01350                         m_subTextObjects[i].isDefaultMaterial = isDefaultMaterial;
01351 
01352                         // Assign new material if we are not using the default material or if the font asset has changed.
01353                         if (!isDefaultMaterial || m_subTextObjects[i].sharedMaterial == null || m_subTextObjects[i].sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID() != m_materialReferences[i].material.GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID())
01354                         {
01355                             m_subTextObjects[i].sharedMaterial = m_materialReferences[i].material;
01356                             m_subTextObjects[i].fontAsset = m_materialReferences[i].fontAsset;
01357                             m_subTextObjects[i].spriteAsset = m_materialReferences[i].spriteAsset;
01358                         }
01359                     }
01360 
01361                     // Check if we need to use a Fallback Material
01362                     if (m_materialReferences[i].isFallbackMaterial)
01363                     {
01364                         m_subTextObjects[i].fallbackMaterial = m_materialReferences[i].material;
01365                         m_subTextObjects[i].fallbackSourceMaterial = m_materialReferences[i].fallbackMaterial;
01366                     }
01367 
01368                 }
01369 
01370                 int referenceCount = m_materialReferences[i].referenceCount;
01371 
01372                 // Check to make sure our buffers allocations can accommodate the required text elements.
01373                 if (m_textInfo.meshInfo[i].vertices == null || m_textInfo.meshInfo[i].vertices.Length < referenceCount * (!m_isVolumetricText ? 4 : 8))
01374                 {
01375                     if (m_textInfo.meshInfo[i].vertices == null)
01376                     {
01377                         if (i == 0)
01378                             m_textInfo.meshInfo[i] = new TMP_MeshInfo(m_mesh, referenceCount + 1, m_isVolumetricText);
01379                         else
01380                             m_textInfo.meshInfo[i] = new TMP_MeshInfo(m_subTextObjects[i].mesh, referenceCount + 1, m_isVolumetricText);
01381                     }
01382                     else
01383                         m_textInfo.meshInfo[i].ResizeMeshInfo(referenceCount > 1024 ? referenceCount + 256 : Mathf.NextPowerOfTwo(referenceCount), m_isVolumetricText);
01384                 }
01385                 else if (m_textInfo.meshInfo[i].vertices.Length - referenceCount * (!m_isVolumetricText ? 4 : 8) > 1024)
01386                 {
01387                     // Resize vertex buffers if allocations are excessive.
01388                     //Debug.Log("Reducing the size of the vertex buffers.");
01389                     m_textInfo.meshInfo[i].ResizeMeshInfo(referenceCount > 1024 ? referenceCount + 256 : Mathf.Max(Mathf.NextPowerOfTwo(referenceCount), 256), m_isVolumetricText);
01390                 }
01391             }
01392 
01393             //TMP_MaterialManager.CleanupFallbackMaterials();
01394 
01395             // Clean up unused SubMeshes
01396             for (int i = materialCount; i < m_subTextObjects.Length && m_subTextObjects[i] != null; i++)
01397             {
01398                 if (i < m_textInfo.meshInfo.Length)
01399                     m_textInfo.meshInfo[i].ClearUnusedVertices(0, true);
01400 
01401                 //m_subTextObjects[i].gameObject.SetActive(false);
01402             }
01403 
01404             return m_totalCharacterCount;
01405         }
01406 
