01419         public override void ComputeMarginSize()
01420         {
01421             if (this.rectTransform != null)
01422             {
01423                 //Debug.Log("*** ComputeMarginSize() *** Current RectTransform's Width is " + m_rectTransform.rect.width + " and Height is " + m_rectTransform.rect.height); // + " and size delta is "  + m_rectTransform.sizeDelta);
01424 
01425                 m_marginWidth = m_rectTransform.rect.width - m_margin.x - m_margin.z;
01426                 m_marginHeight = m_rectTransform.rect.height - m_margin.y - m_margin.w;
01427 
01428                 // Update the corners of the RectTransform
01429                 m_RectTransformCorners = GetTextContainerLocalCorners();
01430             }
01431         }
