01466         void LateUpdate()
01467         {
01468             // TODO : Review this
01469             if (m_rectTransform.hasChanged)
01470             {
01471                 // We need to update the SDF scale or possibly regenerate the text object if lossy scale has changed.
01472                 float lossyScaleY = m_rectTransform.lossyScale.y;
01473                 if (!m_havePropertiesChanged && lossyScaleY != m_previousLossyScaleY && m_text != string.Empty && m_text != null)
01474                 {
01475                     UpdateSDFScale(lossyScaleY);
01476 
01477                     m_previousLossyScaleY = lossyScaleY;
01478                 }
01479             }
01480 
01481             // Added to handle legacy animation mode.
01482             if (m_isUsingLegacyAnimationComponent)
01483             {
01484                 //if (m_havePropertiesChanged)
01485                 m_havePropertiesChanged = true;
01486                 OnPreRenderObject();
01487             }
01488         }
