01490 
01494         void OnPreRenderObject()
01495         {
01496             //Debug.Log("*** OnPreRenderObject() ***");
01497 
01498             if (!m_isAwake || (this.IsActive() == false && m_ignoreActiveState == false)) return;
01499 
01500             // Debug Variables
01501             loopCountA = 0;
01502             //loopCountB = 0;
01503             //loopCountC = 0;
01504             //loopCountD = 0;
01505             //loopCountE = 0;
01506 
01507             if (m_havePropertiesChanged || m_isLayoutDirty)
01508             {
01509                 //Debug.Log("Properties have changed!"); // Assigned Material is:" + m_sharedMaterial); // New Text is: " + m_text + ".");
01510 
01511                 if (isMaskUpdateRequired)
01512                 {
01513                     UpdateMask();
01514                     isMaskUpdateRequired = false;
01515                 }
01516 
01517                 // Update mesh padding if necessary.
01518                 if (checkPaddingRequired)
01519                     UpdateMeshPadding();
01520 
01521                 // Reparse the text if the input has changed or text was truncated.
01522                 if (m_isInputParsingRequired || m_isTextTruncated)
01523                     ParseInputText();
01524 
01525                 // Reset Font min / max used with Auto-sizing
01526                 if (m_enableAutoSizing)
01527                     m_fontSize = Mathf.Clamp(m_fontSizeBase, m_fontSizeMin, m_fontSizeMax);
01528 
01529                 m_maxFontSize = m_fontSizeMax;
01530                 m_minFontSize = m_fontSizeMin;
01531                 m_lineSpacingDelta = 0;
01532                 m_charWidthAdjDelta = 0;
01533                 //m_recursiveCount = 0;
01534 
01535                 m_isCharacterWrappingEnabled = false;
01536                 m_isTextTruncated = false;
01537 
01538                 m_havePropertiesChanged = false;
01539                 m_isLayoutDirty = false;
01540                 m_ignoreActiveState = false;
01541 
01542                 GenerateTextMesh();
01543             }
01544         }
