01547 
01551         protected override void GenerateTextMesh()
01552         {
01553             //Debug.Log("***** GenerateTextMesh() *****"); // ***** Frame: " + Time.frameCount); // + ". Point Size: " + m_fontSize + ". Margins are (W) " + m_marginWidth + "  (H) " + m_marginHeight); // ". Iteration Count: " + loopCountA + ".  Min: " + m_minFontSize + "  Max: " + m_maxFontSize + "  Delta: " + (m_maxFontSize - m_minFontSize) + "  Font size is " + m_fontSize); //called for Object with ID " + GetInstanceID()); // Assigned Material is " + m_uiRenderer.GetMaterial().name); // IncludeForMasking " + this.m_IncludeForMasking); // and text is " + m_text);
01554 
01555             // Early exit if no font asset was assigned. This should not be needed since LiberationSans SDF will be assigned by default.
01556             if (m_fontAsset == null || m_fontAsset.characterDictionary == null)
01557             {
01558                 Debug.LogWarning("Can't Generate Mesh! No Font Asset has been assigned to Object ID: " + this.GetInstanceID());
01559                 return;
01560             }
01561 
01562             // Clear TextInfo
01563             if (m_textInfo != null)
01564                 m_textInfo.Clear();
01565 
01566             // Early exit if we don't have any Text to generate.
01567             if (m_char_buffer == null || m_char_buffer.Length == 0 || m_char_buffer[0] == (char)0)
01568             {
01569                 // Clear mesh and upload changes to the mesh.
01570                 ClearMesh(true);
01571 
01572                 m_preferredWidth = 0;
01573                 m_preferredHeight = 0;
01574 
01575                 // Event indicating the text has been regenerated.
01576                 TMPro_EventManager.ON_TEXT_CHANGED(this);
01577 
01578                 return;
01579             }
01580 
01581             m_currentFontAsset = m_fontAsset;
01582             m_currentMaterial = m_sharedMaterial;
01583             m_currentMaterialIndex = 0;
01584             m_materialReferenceStack.SetDefault(new MaterialReference(m_currentMaterialIndex, m_currentFontAsset, null, m_currentMaterial, m_padding));
01585 
01586             m_currentSpriteAsset = m_spriteAsset;
01587 
01588             // Stop  all Sprite Animations
01589             if (m_spriteAnimator != null)
01590                 m_spriteAnimator.StopAllAnimations();
01591 
01592             // Total character count is computed when the text is parsed.
01593             int totalCharacterCount = m_totalCharacterCount;
01594 
01595             // Calculate the scale of the font based on selected font size and sampling point size.
01596             // baseScale is calculated using the font asset assigned to the text object.
01597             float baseScale = m_fontScale = (m_fontSize / m_fontAsset.fontInfo.PointSize * m_fontAsset.fontInfo.Scale * (m_isOrthographic ? 1 : 0.1f));
01598             float currentElementScale = baseScale;
01599             m_fontScaleMultiplier = 1;
01600 
01601             m_currentFontSize = m_fontSize;
01602             m_sizeStack.SetDefault(m_currentFontSize);
01603             float fontSizeDelta = 0;
01604 
01605             int charCode = 0; // Holds the character code of the currently being processed character.
01606 
01607             m_style = m_fontStyle; // Set the default style.
01608             m_fontWeightInternal = (m_style & FontStyles.Bold) == FontStyles.Bold ? 700 : m_fontWeight;
01609             m_fontWeightStack.SetDefault(m_fontWeightInternal);
01610             m_fontStyleStack.Clear();
01611 
01612             m_lineJustification = m_textAlignment; // Sets the line justification mode to match editor alignment.
01613             m_lineJustificationStack.SetDefault(m_lineJustification);
01614 
01615             float padding = 0;
01616             float style_padding = 0; // Extra padding required to accommodate Bold style.
01617             float bold_xAdvance_multiplier = 1; // Used to increase spacing between character when style is bold.
01618 
01619             m_baselineOffset = 0; // Used by subscript characters.
01620             m_baselineOffsetStack.Clear();
01621 
01622             // Underline
01623             bool beginUnderline = false;
01624             Vector3 underline_start = Vector3.zero; // Used to track where underline starts & ends.
01625             Vector3 underline_end = Vector3.zero;
01626 
01627             // Strike-through
01628             bool beginStrikethrough = false;
01629             Vector3 strikethrough_start = Vector3.zero;
01630             Vector3 strikethrough_end = Vector3.zero;
01631 
01632             // Text Highlight
01633             bool beginHighlight = false;
01634             Vector3 highlight_start = Vector3.zero;
01635             Vector3 highlight_end = Vector3.zero;
01636 
01637             m_fontColor32 = m_fontColor;
01638             Color32 vertexColor;
01639             m_htmlColor = m_fontColor32;
01640             m_underlineColor = m_htmlColor;
01641             m_strikethroughColor = m_htmlColor;
01642 
01643             m_colorStack.SetDefault(m_htmlColor);
01644             m_underlineColorStack.SetDefault(m_htmlColor);
01645             m_strikethroughColorStack.SetDefault(m_htmlColor);
01646             m_highlightColorStack.SetDefault(m_htmlColor);
01647 
01648             m_colorGradientPreset = null;
01649             m_colorGradientStack.SetDefault(null);
01650 
01651             // Clear the Style stack.
01652             //m_styleStack.Clear();
01653 
01654             // Clear the Action stack.
01655             m_actionStack.Clear();
01656 
01657             m_isFXMatrixSet = false;
01658 
01659             m_lineOffset = 0; // Amount of space between lines (font line spacing + m_linespacing).
01660             m_lineHeight = TMP_Math.FLOAT_UNSET;
01661             float lineGap = m_currentFontAsset.fontInfo.LineHeight - (m_currentFontAsset.fontInfo.Ascender - m_currentFontAsset.fontInfo.Descender);
01662 
01663             m_cSpacing = 0; // Amount of space added between characters as a result of the use of the <cspace> tag.
01664             m_monoSpacing = 0;
01665             float lineOffsetDelta = 0;
01666             m_xAdvance = 0; // Used to track the position of each character.
01667 
01668             tag_LineIndent = 0; // Used for indentation of text.
01669             tag_Indent = 0;
01670             m_indentStack.SetDefault(0);
01671             tag_NoParsing = false;
01672             //m_isIgnoringAlignment = false;
01673 
01674             m_characterCount = 0; // Total characters in the char[]
01675 
01676             // Tracking of line information
01677             m_firstCharacterOfLine = 0;
01678             m_lastCharacterOfLine = 0;
01679             m_firstVisibleCharacterOfLine = 0;
01680             m_lastVisibleCharacterOfLine = 0;
01681             m_maxLineAscender = k_LargeNegativeFloat;
01682             m_maxLineDescender = k_LargePositiveFloat;
01683             m_lineNumber = 0;
01684             m_lineVisibleCharacterCount = 0;
01685             bool isStartOfNewLine = true;
01686             m_firstOverflowCharacterIndex = -1;
01687 
01688             m_pageNumber = 0;
01689             int pageToDisplay = Mathf.Clamp(m_pageToDisplay - 1, 0, m_textInfo.pageInfo.Length - 1);
01690             int previousPageOverflowChar = 0;
01691 
01692             int ellipsisIndex = 0;
01693 
01694             Vector4 margins = m_margin;
01695             float marginWidth = m_marginWidth;
01696             float marginHeight = m_marginHeight;
01697             m_marginLeft = 0;
01698             m_marginRight = 0;
01699             m_width = -1;
01700             float width = marginWidth + 0.0001f - m_marginLeft - m_marginRight;
01701 
01702             // Need to initialize these Extents structures
01703             m_meshExtents.min = k_LargePositiveVector2;
01704             m_meshExtents.max = k_LargeNegativeVector2;
01705 
01706             // Initialize lineInfo
01707             m_textInfo.ClearLineInfo();
01708 
01709             // Tracking of the highest Ascender
01710             m_maxCapHeight = 0;
01711             m_maxAscender = 0;
01712             m_maxDescender = 0;
01713             float pageAscender = 0;
01714             float maxVisibleDescender = 0;
01715             bool isMaxVisibleDescenderSet = false;
01716             m_isNewPage = false;
01717 
01718             // Initialize struct to track states of word wrapping
01719             bool isFirstWord = true;
01720             m_isNonBreakingSpace = false;
01721             bool ignoreNonBreakingSpace = false;
01722             bool isLastBreakingChar = false;
01723             float linebreakingWidth = 0;
01724             int wrappingIndex = 0;
01725 
01726             // Save character and line state before we begin layout.
01727             SaveWordWrappingState(ref m_SavedWordWrapState, -1, -1);
01728             SaveWordWrappingState(ref m_SavedLineState, -1, -1);
01729 
01730             loopCountA += 1;
01731 
01732             int endTagIndex = 0;
01733             // Parse through Character buffer to read HTML tags and begin creating mesh.
01734             for (int i = 0; i < m_char_buffer.Length && m_char_buffer[i] != 0; i++)
01735             {
01736                 charCode = m_char_buffer[i];
01737 
01738                 // Parse Rich Text Tag
01739                 #region Parse Rich Text Tag
01740                 if (m_isRichText && charCode == 60)  // '<'
01741                 {
01742                     m_isParsingText = true;
01743                     m_textElementType = TMP_TextElementType.Character;
01744 
01745                     // Check if Tag is valid. If valid, skip to the end of the validated tag.
01746                     if (ValidateHtmlTag(m_char_buffer, i + 1, out endTagIndex))
01747                     {
01748                         i = endTagIndex;
01749 
01750                         // Continue to next character or handle the sprite element
01751                         if (m_textElementType == TMP_TextElementType.Character)
01752                             continue;
01753                     }
01754                 }
01755                 else
01756                 {
01757                     m_textElementType = m_textInfo.characterInfo[m_characterCount].elementType;
01758                     m_currentMaterialIndex = m_textInfo.characterInfo[m_characterCount].materialReferenceIndex;
01759                     m_currentFontAsset = m_textInfo.characterInfo[m_characterCount].fontAsset;
01760                 }
01761                 #endregion End Parse Rich Text Tag
01762 
01763                 int prev_MaterialIndex = m_currentMaterialIndex;
01764                 bool isUsingAltTypeface = m_textInfo.characterInfo[m_characterCount].isUsingAlternateTypeface;
01765 
01766                 m_isParsingText = false;
01767 
01768                 // When using Linked text, mark character as ignored and skip to next character.
01769                 if (m_characterCount < m_firstVisibleCharacter)
01770                 {
01771                     m_textInfo.characterInfo[m_characterCount].isVisible = false;
01772                     m_textInfo.characterInfo[m_characterCount].character = (char)0x200B;
01773                     m_characterCount += 1;
01774                     continue;
01775                 }
01776 
01777                 // Handle Font Styles like LowerCase, UpperCase and SmallCaps.
01778                 #region Handling of LowerCase, UpperCase and SmallCaps Font Styles
01779 
01780                 float smallCapsMultiplier = 1.0f;
01781 
01782                 if (m_textElementType == TMP_TextElementType.Character)
01783                 {
01784                     if ((m_style & FontStyles.UpperCase) == FontStyles.UpperCase)
01785                     {
01786                         // If this character is lowercase, switch to uppercase.
01787                         if (char.IsLower((char)charCode))
01788                             charCode = char.ToUpper((char)charCode);
01789 
01790                     }
01791                     else if ((m_style & FontStyles.LowerCase) == FontStyles.LowerCase)
01792                     {
01793                         // If this character is uppercase, switch to lowercase.
01794                         if (char.IsUpper((char)charCode))
01795                             charCode = char.ToLower((char)charCode);
01796                     }
01797                     else if ((m_fontStyle & FontStyles.SmallCaps) == FontStyles.SmallCaps || (m_style & FontStyles.SmallCaps) == FontStyles.SmallCaps)
01798                     {
01799                         if (char.IsLower((char)charCode))
01800                         {
01801                             smallCapsMultiplier = 0.8f;
01802                             charCode = char.ToUpper((char)charCode);
01803                         }
01804                     }
01805                 }
01806                 #endregion
01807 
01808 
01809                 // Look up Character Data from Dictionary and cache it.
01810                 #region Look up Character Data
01811                 if (m_textElementType == TMP_TextElementType.Sprite)
01812                 {
01813                     // If a sprite is used as a fallback then get a reference to it and set the color to white.
01814                     // TODO : Finish adding support for the ability to use Sprites as Fallbacks.
01815                     m_currentSpriteAsset = m_textInfo.characterInfo[m_characterCount].spriteAsset;
01816                     m_spriteIndex = m_textInfo.characterInfo[m_characterCount].spriteIndex;
01817 
01818                     TMP_Sprite sprite = m_currentSpriteAsset.spriteInfoList[m_spriteIndex];
01819                     if (sprite == null) continue;
01820 
01821                     // Sprites are assigned in the E000 Private Area + sprite Index
01822                     if (charCode == 60)
01823                         charCode = 57344 + m_spriteIndex;
01824                     else
01825                         m_spriteColor = s_colorWhite;
01826 
01827                     // The sprite scale calculations are based on the font asset assigned to the text object.
01828                     float spriteScale = (m_currentFontSize / m_currentFontAsset.fontInfo.PointSize * m_currentFontAsset.fontInfo.Scale * (m_isOrthographic ? 1 : 0.1f));
01829                     currentElementScale = m_currentFontAsset.fontInfo.Ascender / sprite.height * sprite.scale * spriteScale;
01830 
01831                     m_cached_TextElement = sprite;
01832 
01833                     m_textInfo.characterInfo[m_characterCount].elementType = TMP_TextElementType.Sprite;
01834                     m_textInfo.characterInfo[m_characterCount].scale = spriteScale;
01835                     m_textInfo.characterInfo[m_characterCount].spriteAsset = m_currentSpriteAsset;
01836                     m_textInfo.characterInfo[m_characterCount].fontAsset = m_currentFontAsset;
01837                     m_textInfo.characterInfo[m_characterCount].materialReferenceIndex = m_currentMaterialIndex;
01838 
01839                     m_currentMaterialIndex = prev_MaterialIndex;
01840 
01841                     padding = 0;
01842                 }
01843                 else if (m_textElementType == TMP_TextElementType.Character)
01844                 {
01845                     m_cached_TextElement = m_textInfo.characterInfo[m_characterCount].textElement;
01846                     if (m_cached_TextElement == null) continue;
01847 
01848                     m_currentFontAsset = m_textInfo.characterInfo[m_characterCount].fontAsset;
01849                     m_currentMaterial = m_textInfo.characterInfo[m_characterCount].material;
01850                     m_currentMaterialIndex = m_textInfo.characterInfo[m_characterCount].materialReferenceIndex;
01851 
01852                     // Re-calculate font scale as the font asset may have changed.
01853                     m_fontScale = m_currentFontSize * smallCapsMultiplier / m_currentFontAsset.fontInfo.PointSize * m_currentFontAsset.fontInfo.Scale * (m_isOrthographic ? 1 : 0.1f);
01854 
01855                     currentElementScale = m_fontScale * m_fontScaleMultiplier * m_cached_TextElement.scale;
01856 
01857                     m_textInfo.characterInfo[m_characterCount].elementType = TMP_TextElementType.Character;
01858                     m_textInfo.characterInfo[m_characterCount].scale = currentElementScale;
01859 
01860                     padding = m_currentMaterialIndex == 0 ? m_padding : m_subTextObjects[m_currentMaterialIndex].padding;
01861                 }
01862                 #endregion
01863 
01864 
01865                 // Handle Soft Hyphen
01866                 #region Handle Soft Hyphen
01867                 float old_scale = currentElementScale;
01868                 if (charCode == 0xAD)
01869                 {
01870                     currentElementScale = 0;
01871                 }
01872                 #endregion
01873 
01874 
01875                 // Store some of the text object's information
01876                 m_textInfo.characterInfo[m_characterCount].character = (char)charCode;
01877                 m_textInfo.characterInfo[m_characterCount].pointSize = m_currentFontSize;
01878                 m_textInfo.characterInfo[m_characterCount].color = m_htmlColor;
01879                 m_textInfo.characterInfo[m_characterCount].underlineColor = m_underlineColor;
01880                 m_textInfo.characterInfo[m_characterCount].strikethroughColor = m_strikethroughColor;
01881                 m_textInfo.characterInfo[m_characterCount].highlightColor = m_highlightColor;
01882                 m_textInfo.characterInfo[m_characterCount].style = m_style;
01883                 m_textInfo.characterInfo[m_characterCount].index = i;
01884                 //m_textInfo.characterInfo[m_characterCount].isIgnoringAlignment = m_isIgnoringAlignment;
01885 
01886 
01887                 // Handle Kerning if Enabled.
01888                 #region Handle Kerning
01889                 GlyphValueRecord glyphAdjustments = new GlyphValueRecord();
01890                 if (m_enableKerning)
01891                 {
01892                     KerningPair adjustmentPair = null;
01893 
01894                     if (m_characterCount < totalCharacterCount - 1)
01895                     {
01896                         uint nextGlyph = m_textInfo.characterInfo[m_characterCount + 1].character;
01897                         KerningPairKey keyValue = new KerningPairKey((uint)charCode, nextGlyph);
01898 
01899                         m_currentFontAsset.kerningDictionary.TryGetValue((int)keyValue.key, out adjustmentPair);
01900                         if (adjustmentPair != null)
01901                             glyphAdjustments = adjustmentPair.firstGlyphAdjustments;
01902                     }
01903 
01904                     if (m_characterCount >= 1)
01905                     {
01906                         uint previousGlyph = m_textInfo.characterInfo[m_characterCount - 1].character;
01907                         KerningPairKey keyValue = new KerningPairKey(previousGlyph, (uint)charCode);
01908 
01909                         m_currentFontAsset.kerningDictionary.TryGetValue((int)keyValue.key, out adjustmentPair);
01910                         if (adjustmentPair != null)
01911                             glyphAdjustments += adjustmentPair.secondGlyphAdjustments;
01912                     }
01913                 }
01914                 #endregion
01915 
01916 
01917                 // Initial Implementation for RTL support.
01918                 #region Handle Right-to-Left
01919                 if (m_isRightToLeft)
01920                 {
01921                     m_xAdvance -= ((m_cached_TextElement.xAdvance * bold_xAdvance_multiplier + m_characterSpacing + m_wordSpacing + m_currentFontAsset.normalSpacingOffset) * currentElementScale + m_cSpacing) * (1 - m_charWidthAdjDelta);
01922 
01923                     if (char.IsWhiteSpace((char)charCode) || charCode == 0x200B)
01924                         m_xAdvance -= m_wordSpacing * currentElementScale;
01925                 }
01926                 #endregion
01927 
01928 
01929                 // Handle Mono Spacing
01930                 #region Handle Mono Spacing
01931                 float monoAdvance = 0;
01932                 if (m_monoSpacing != 0)
01933                 {
01934                     monoAdvance = (m_monoSpacing / 2 - (m_cached_TextElement.width / 2 + m_cached_TextElement.xOffset) * currentElementScale) * (1 - m_charWidthAdjDelta);
01935                     m_xAdvance += monoAdvance;
01936                 }
01937                 #endregion
01938 
01939 
01940                 // Set Padding based on selected font style
01941                 #region Handle Style Padding
01942                 if (m_textElementType == TMP_TextElementType.Character && !isUsingAltTypeface && ((m_style & FontStyles.Bold) == FontStyles.Bold || (m_fontStyle & FontStyles.Bold) == FontStyles.Bold)) // Checks for any combination of Bold Style.
01943                 {
01944                     if (m_currentMaterial.HasProperty(ShaderUtilities.ID_GradientScale))
01945                     {
01946                         float gradientScale = m_currentMaterial.GetFloat(ShaderUtilities.ID_GradientScale);
01947                         style_padding = m_currentFontAsset.boldStyle / 4.0f * gradientScale * m_currentMaterial.GetFloat(ShaderUtilities.ID_ScaleRatio_A);
01948 
01949                         // Clamp overall padding to Gradient Scale size.
01950                         if (style_padding + padding > gradientScale)
01951                             padding = gradientScale - style_padding;
01952                     }
01953                     else
01954                         style_padding = 0;
01955 
01956                     bold_xAdvance_multiplier = 1 + m_currentFontAsset.boldSpacing * 0.01f;
01957                 }
01958                 else
01959                 {
01960                     if (m_currentMaterial.HasProperty(ShaderUtilities.ID_GradientScale))
01961                     {
01962                         float gradientScale = m_currentMaterial.GetFloat(ShaderUtilities.ID_GradientScale);
01963                         style_padding = m_currentFontAsset.normalStyle / 4.0f * gradientScale * m_currentMaterial.GetFloat(ShaderUtilities.ID_ScaleRatio_A);
01964 
01965                         // Clamp overall padding to Gradient Scale size.
01966                         if (style_padding + padding > gradientScale)
01967                             padding = gradientScale - style_padding;
01968                     }
01969                     else
01970                         style_padding = 0;
01971 
01972                     bold_xAdvance_multiplier = 1.0f;
01973                 }
01974                 #endregion Handle Style Padding
01975 
01976 
01977                 // Determine the position of the vertices of the Character or Sprite.
01978                 #region Calculate Vertices Position
01979                 float fontBaseLineOffset = m_currentFontAsset.fontInfo.Baseline * m_fontScale * m_fontScaleMultiplier * m_currentFontAsset.fontInfo.Scale;
01980                 Vector3 top_left;
01981                 top_left.x = m_xAdvance + ((m_cached_TextElement.xOffset - padding - style_padding + glyphAdjustments.xPlacement) * currentElementScale * (1 - m_charWidthAdjDelta));
01982                 top_left.y = fontBaseLineOffset + (m_cached_TextElement.yOffset + padding + glyphAdjustments.yPlacement) * currentElementScale - m_lineOffset + m_baselineOffset;
01983                 top_left.z = 0;
01984 
01985                 Vector3 bottom_left;
01986                 bottom_left.x = top_left.x;
01987                 bottom_left.y = top_left.y - ((m_cached_TextElement.height + padding * 2) * currentElementScale);
01988                 bottom_left.z = 0;
01989 
01990                 Vector3 top_right;
01991                 top_right.x = bottom_left.x + ((m_cached_TextElement.width + padding * 2 + style_padding * 2) * currentElementScale * (1 - m_charWidthAdjDelta));
01992                 top_right.y = top_left.y;
01993                 top_right.z = 0;
01994 
01995                 Vector3 bottom_right;
01996                 bottom_right.x = top_right.x;
01997                 bottom_right.y = bottom_left.y;
01998                 bottom_right.z = 0;
01999                 #endregion
02000 
02001 
02002                 // Check if we need to Shear the rectangles for Italic styles
02003                 #region Handle Italic & Shearing
02004                 if (m_textElementType == TMP_TextElementType.Character && !isUsingAltTypeface && ((m_style & FontStyles.Italic) == FontStyles.Italic || (m_fontStyle & FontStyles.Italic) == FontStyles.Italic))
02005                 {
02006                     // Shift Top vertices forward by half (Shear Value * height of character) and Bottom vertices back by same amount. 
02007                     float shear_value = m_currentFontAsset.italicStyle * 0.01f;
02008                     Vector3 topShear = new Vector3(shear_value * ((m_cached_TextElement.yOffset + padding + style_padding) * currentElementScale), 0, 0);
02009                     Vector3 bottomShear = new Vector3(shear_value * (((m_cached_TextElement.yOffset - m_cached_TextElement.height - padding - style_padding)) * currentElementScale), 0, 0);
02010 
02011                     top_left = top_left + topShear;
02012                     bottom_left = bottom_left + bottomShear;
02013                     top_right = top_right + topShear;
02014                     bottom_right = bottom_right + bottomShear;
02015                 }
02016                 #endregion Handle Italics & Shearing
02017 
02018 
02019                 // Handle Character Rotation
02020                 #region Handle Character Rotation
02021                 if (m_isFXMatrixSet)
02022                 {
02023                     // Apply scale matrix when simulating Condensed text.
02024                     if (m_FXMatrix.m00 != 1)
02025                     {
02026                         //top_left = m_FXMatrix.MultiplyPoint3x4(top_left);
02027                         //bottom_left = m_FXMatrix.MultiplyPoint3x4(bottom_left);
02028                         //top_right = m_FXMatrix.MultiplyPoint3x4(top_right);
02029                         //bottom_right = m_FXMatrix.MultiplyPoint3x4(bottom_right);
02030                     }
02031 
02032                     Vector3 positionOffset = (top_right + bottom_left) / 2;
02033 
02034                     top_left = m_FXMatrix.MultiplyPoint3x4(top_left - positionOffset) + positionOffset;
02035                     bottom_left = m_FXMatrix.MultiplyPoint3x4(bottom_left - positionOffset) + positionOffset;
02036                     top_right = m_FXMatrix.MultiplyPoint3x4(top_right - positionOffset) + positionOffset;
02037                     bottom_right = m_FXMatrix.MultiplyPoint3x4(bottom_right - positionOffset) + positionOffset;
02038                 }
02039                 #endregion
02040 
02041 
02042                 // Store vertex information for the character or sprite.
02043                 m_textInfo.characterInfo[m_characterCount].bottomLeft = bottom_left;
02044                 m_textInfo.characterInfo[m_characterCount].topLeft = top_left;
02045                 m_textInfo.characterInfo[m_characterCount].topRight = top_right;
02046                 m_textInfo.characterInfo[m_characterCount].bottomRight = bottom_right;
02047 
02048                 m_textInfo.characterInfo[m_characterCount].origin = m_xAdvance;
02049                 m_textInfo.characterInfo[m_characterCount].baseLine = fontBaseLineOffset - m_lineOffset + m_baselineOffset;
02050                 m_textInfo.characterInfo[m_characterCount].aspectRatio = (top_right.x - bottom_left.x) / (top_left.y - bottom_left.y);
02051 
02052 
02053                 // Compute and save text element Ascender and maximum line Ascender.
02054                 float elementAscender = m_currentFontAsset.fontInfo.Ascender * (m_textElementType == TMP_TextElementType.Character ? currentElementScale / smallCapsMultiplier : m_textInfo.characterInfo[m_characterCount].scale) + m_baselineOffset;
02055                 m_textInfo.characterInfo[m_characterCount].ascender = elementAscender - m_lineOffset;
02056                 m_maxLineAscender = elementAscender > m_maxLineAscender ? elementAscender : m_maxLineAscender;
02057 
02058                 // Compute and save text element Descender and maximum line Descender.
02059                 float elementDescender = m_currentFontAsset.fontInfo.Descender * (m_textElementType == TMP_TextElementType.Character ? currentElementScale / smallCapsMultiplier : m_textInfo.characterInfo[m_characterCount].scale) + m_baselineOffset;
02060                 float elementDescenderII = m_textInfo.characterInfo[m_characterCount].descender = elementDescender - m_lineOffset;
02061                 m_maxLineDescender = elementDescender < m_maxLineDescender ? elementDescender : m_maxLineDescender;
02062 
02063                 // Adjust maxLineAscender and maxLineDescender if style is superscript or subscript
02064                 if ((m_style & FontStyles.Subscript) == FontStyles.Subscript || (m_style & FontStyles.Superscript) == FontStyles.Superscript)
02065                 {
02066                     float baseAscender = (elementAscender - m_baselineOffset) / m_currentFontAsset.fontInfo.SubSize;
02067                     elementAscender = m_maxLineAscender;
02068                     m_maxLineAscender = baseAscender > m_maxLineAscender ? baseAscender : m_maxLineAscender;
02069 
02070                     float baseDescender = (elementDescender - m_baselineOffset) / m_currentFontAsset.fontInfo.SubSize;
02071                     elementDescender = m_maxLineDescender;
02072                     m_maxLineDescender = baseDescender < m_maxLineDescender ? baseDescender : m_maxLineDescender;
02073                 }
02074 
02075                 if (m_lineNumber == 0 || m_isNewPage)
02076                 {
02077                     m_maxAscender = m_maxAscender > elementAscender ? m_maxAscender : elementAscender;
02078                     m_maxCapHeight = Mathf.Max(m_maxCapHeight, m_currentFontAsset.fontInfo.CapHeight * currentElementScale / smallCapsMultiplier);
02079                 }
02080                 if (m_lineOffset == 0) pageAscender = pageAscender > elementAscender ? pageAscender : elementAscender;
02081 
02082 
02083                 // Set Characters to not visible by default.
02084                 m_textInfo.characterInfo[m_characterCount].isVisible = false;
02085 
02086                 // Setup Mesh for visible text elements. ie. not a SPACE / LINEFEED / CARRIAGE RETURN.
02087                 #region Handle Visible Characters
02088                 if (charCode == 9 || charCode == 0xA0 || charCode == 0x2007 || (!char.IsWhiteSpace((char)charCode) && charCode != 0x200B) || m_textElementType == TMP_TextElementType.Sprite)
02089                 {
02090                     m_textInfo.characterInfo[m_characterCount].isVisible = true;
02091 
02092                     #region Experimental Margin Shaper
02093                     //Vector2 shapedMargins;
02094                     //if (marginShaper)
02095                     //{
02096                     //    shapedMargins = m_marginShaper.GetShapedMargins(m_textInfo.characterInfo[m_characterCount].baseLine);
02097                     //    if (shapedMargins.x < margins.x)
02098                     //    {
02099                     //        shapedMargins.x = m_marginLeft;
02100                     //    }
02101                     //    else
02102                     //    {
02103                     //        shapedMargins.x += m_marginLeft - margins.x;
02104                     //    }
02105                     //    if (shapedMargins.y < margins.z)
02106                     //    {
02107                     //        shapedMargins.y = m_marginRight;
02108                     //    }
02109                     //    else
02110                     //    {
02111                     //        shapedMargins.y += m_marginRight - margins.z;
02112                     //    }
02113                     //}
02114                     //else
02115                     //{
02116                     //    shapedMargins.x = m_marginLeft;
02117                     //    shapedMargins.y = m_marginRight;
02118                     //}
02119                     //width = marginWidth + 0.0001f - shapedMargins.x - shapedMargins.y;
02120                     //if (m_width != -1 && m_width < width)
02121                     //{
02122                     //    width = m_width;
02123                     //}
02124                     //m_textInfo.lineInfo[m_lineNumber].marginLeft = shapedMargins.x;
02125                     #endregion
02126 
02127                     width = m_width != -1 ? Mathf.Min(marginWidth + 0.0001f - m_marginLeft - m_marginRight, m_width) : marginWidth + 0.0001f - m_marginLeft - m_marginRight;
02128                     m_textInfo.lineInfo[m_lineNumber].marginLeft = m_marginLeft;
02129 
02130                     bool isJustifiedOrFlush = ((_HorizontalAlignmentOptions)m_lineJustification & _HorizontalAlignmentOptions.Flush) == _HorizontalAlignmentOptions.Flush || ((_HorizontalAlignmentOptions)m_lineJustification & _HorizontalAlignmentOptions.Justified) == _HorizontalAlignmentOptions.Justified;
02131 
02132                     // Calculate the line breaking width of the text.
02133                     linebreakingWidth = Mathf.Abs(m_xAdvance) + (!m_isRightToLeft ? m_cached_TextElement.xAdvance : 0) * (1 - m_charWidthAdjDelta) * (charCode != 0xAD ? currentElementScale : old_scale);
02134 
02135                     // Check if Character exceeds the width of the Text Container
02136                     #region Handle Line Breaking, Text Auto-Sizing and Horizontal Overflow
02137                     if (linebreakingWidth > width * (isJustifiedOrFlush ? 1.05f : 1.0f))
02138                     {
02139                         ellipsisIndex = m_characterCount - 1; // Last safely rendered character
02140 
02141                         // Word Wrapping
02142                         #region Handle Word Wrapping
02143                         if (enableWordWrapping && m_characterCount != m_firstCharacterOfLine)
02144                         {
02145                             // Check if word wrapping is still possible
02146                             #region Line Breaking Check
02147                             if (wrappingIndex == m_SavedWordWrapState.previous_WordBreak || isFirstWord)
02148                             {
02149                                 // Word wrapping is no longer possible. Shrink size of text if auto-sizing is enabled.
02150                                 if (m_enableAutoSizing && m_fontSize > m_fontSizeMin)
02151                                 {
02152                                     // Handle Character Width Adjustments
02153                                     #region Character Width Adjustments
02154                                     if (m_charWidthAdjDelta < m_charWidthMaxAdj / 100)
02155                                     {
02156                                         loopCountA = 0;
02157                                         m_charWidthAdjDelta += 0.01f;
02158                                         GenerateTextMesh();
02159                                         return;
02160                                     }
02161                                     #endregion
02162 
02163                                     // Adjust Point Size
02164                                     m_maxFontSize = m_fontSize;
02165 
02166                                     m_fontSize -= Mathf.Max((m_fontSize - m_minFontSize) / 2, 0.05f);
02167                                     m_fontSize = (int)(Mathf.Max(m_fontSize, m_fontSizeMin) * 20 + 0.5f) / 20f;
02168 
02169                                     if (loopCountA > 20) return; // Added to debug
02170                                     GenerateTextMesh();
02171                                     return;
02172                                 }
02173 
02174                                 // Word wrapping is no longer possible, now breaking up individual words.
02175                                 if (m_isCharacterWrappingEnabled == false)
02176                                 {
02177                                     if (ignoreNonBreakingSpace == false)
02178                                         ignoreNonBreakingSpace = true;
02179                                     else
02180                                         m_isCharacterWrappingEnabled = true;
02181                                 }
02182                                 else
02183                                     isLastBreakingChar = true;
02184 
02185                                 //m_recursiveCount += 1;
02186                                 //if (m_recursiveCount > 20)
02187                                 //{
02188                                 //    Debug.Log("Recursive count exceeded!");
02189                                 //    continue;
02190                                 //}
02191                             }
02192                             #endregion
02193 
02194                             // Restore to previously stored state of last valid (space character or linefeed)
02195                             i = RestoreWordWrappingState(ref m_SavedWordWrapState);
02196                             wrappingIndex = i;  // Used to detect when line length can no longer be reduced.
02197 
02198                             // Handling for Soft Hyphen
02199                             if (m_char_buffer[i] == 0xAD) // && !m_isCharacterWrappingEnabled) // && ellipsisIndex != i && !m_isCharacterWrappingEnabled)
02200                             {
02201                                 m_isTextTruncated = true;
02202                                 m_char_buffer[i] = 0x2D;
02203                                 GenerateTextMesh();
02204                                 return;
02205                             }
02206 
02207                             //Debug.Log("Last Visible Character of line # " + m_lineNumber + " is [" + m_textInfo.characterInfo[m_lastVisibleCharacterOfLine].character + " Character Count: " + m_characterCount + " Last visible: " + m_lastVisibleCharacterOfLine);
02208 
02209                             // Check if Line Spacing of previous line needs to be adjusted.
02210                             if (m_lineNumber > 0 && !TMP_Math.Approximately(m_maxLineAscender, m_startOfLineAscender) && m_lineHeight == TMP_Math.FLOAT_UNSET && !m_isNewPage)
02211                             {
02212                                 //Debug.Log("(Line Break - Adjusting Line Spacing on line #" + m_lineNumber);
02213                                 float offsetDelta = m_maxLineAscender - m_startOfLineAscender;
02214                                 AdjustLineOffset(m_firstCharacterOfLine, m_characterCount, offsetDelta);
02215                                 m_lineOffset += offsetDelta;
02216                                 m_SavedWordWrapState.lineOffset = m_lineOffset;
02217                                 m_SavedWordWrapState.previousLineAscender = m_maxLineAscender;
02218 
02219                                 // TODO - Add check for character exceeding vertical bounds
02220                             }
02221                             m_isNewPage = false;
02222 
02223                             // Calculate lineAscender & make sure if last character is superscript or subscript that we check that as well.
02224                             float lineAscender = m_maxLineAscender - m_lineOffset;
02225                             float lineDescender = m_maxLineDescender - m_lineOffset;
02226 
02227 
02228                             // Update maxDescender and maxVisibleDescender
02229                             m_maxDescender = m_maxDescender < lineDescender ? m_maxDescender : lineDescender;
02230                             if (!isMaxVisibleDescenderSet)
02231                                 maxVisibleDescender = m_maxDescender;
02232 
02233                             if (m_useMaxVisibleDescender && (m_characterCount >= m_maxVisibleCharacters || m_lineNumber >= m_maxVisibleLines))
02234                                 isMaxVisibleDescenderSet = true;
02235 
02236                             // Track & Store lineInfo for the new line
02237                             m_textInfo.lineInfo[m_lineNumber].firstCharacterIndex = m_firstCharacterOfLine;
02238                             m_textInfo.lineInfo[m_lineNumber].firstVisibleCharacterIndex = m_firstVisibleCharacterOfLine = m_firstCharacterOfLine > m_firstVisibleCharacterOfLine ? m_firstCharacterOfLine : m_firstVisibleCharacterOfLine;
02239                             m_textInfo.lineInfo[m_lineNumber].lastCharacterIndex = m_lastCharacterOfLine = m_characterCount - 1 > 0 ? m_characterCount - 1 : 0;
02240                             m_textInfo.lineInfo[m_lineNumber].lastVisibleCharacterIndex = m_lastVisibleCharacterOfLine = m_lastVisibleCharacterOfLine < m_firstVisibleCharacterOfLine ? m_firstVisibleCharacterOfLine : m_lastVisibleCharacterOfLine;
02241 
02242                             m_textInfo.lineInfo[m_lineNumber].characterCount = m_textInfo.lineInfo[m_lineNumber].lastCharacterIndex - m_textInfo.lineInfo[m_lineNumber].firstCharacterIndex + 1;
02243                             m_textInfo.lineInfo[m_lineNumber].visibleCharacterCount = m_lineVisibleCharacterCount;
02244                             m_textInfo.lineInfo[m_lineNumber].lineExtents.min = new Vector2(m_textInfo.characterInfo[m_firstVisibleCharacterOfLine].bottomLeft.x, lineDescender);
02245                             m_textInfo.lineInfo[m_lineNumber].lineExtents.max = new Vector2(m_textInfo.characterInfo[m_lastVisibleCharacterOfLine].topRight.x, lineAscender);
02246                             m_textInfo.lineInfo[m_lineNumber].length = m_textInfo.lineInfo[m_lineNumber].lineExtents.max.x;
02247                             m_textInfo.lineInfo[m_lineNumber].width = width;
02248 
02249                             //m_textInfo.lineInfo[m_lineNumber].alignment = m_lineJustification;
02250 
02251                             m_textInfo.lineInfo[m_lineNumber].maxAdvance = m_textInfo.characterInfo[m_lastVisibleCharacterOfLine].xAdvance - (m_characterSpacing + m_currentFontAsset.normalSpacingOffset) * currentElementScale - m_cSpacing;
02252 
02253                             m_textInfo.lineInfo[m_lineNumber].baseline = 0 - m_lineOffset;
02254                             m_textInfo.lineInfo[m_lineNumber].ascender = lineAscender;
02255                             m_textInfo.lineInfo[m_lineNumber].descender = lineDescender;
02256                             m_textInfo.lineInfo[m_lineNumber].lineHeight = lineAscender - lineDescender + lineGap * baseScale;
02257 
02258                             m_firstCharacterOfLine = m_characterCount; // Store first character of the next line.
02259                             m_lineVisibleCharacterCount = 0;
02260 
02261                             // Store the state of the line before starting on the new line.
02262                             SaveWordWrappingState(ref m_SavedLineState, i, m_characterCount - 1);
02263 
02264                             m_lineNumber += 1;
02265                             isStartOfNewLine = true;
02266                             isFirstWord = true;
02267 
02268                             // Check to make sure Array is large enough to hold a new line.
02269                             if (m_lineNumber >= m_textInfo.lineInfo.Length)
02270                                 ResizeLineExtents(m_lineNumber);
02271 
02272                             // Apply Line Spacing based on scale of the last character of the line.
02273                             if (m_lineHeight == TMP_Math.FLOAT_UNSET)
02274                             {
02275                                 float ascender = m_textInfo.characterInfo[m_characterCount].ascender - m_textInfo.characterInfo[m_characterCount].baseLine;
02276                                 lineOffsetDelta = 0 - m_maxLineDescender + ascender + (lineGap + m_lineSpacing + m_lineSpacingDelta) * baseScale;
02277                                 m_lineOffset += lineOffsetDelta;
02278 
02279                                 m_startOfLineAscender = ascender;
02280                             }
02281                             else
02282                                 m_lineOffset += m_lineHeight + m_lineSpacing * baseScale;
02283 
02284                             m_maxLineAscender = k_LargeNegativeFloat;
02285                             m_maxLineDescender = k_LargePositiveFloat;
02286 
02287                             m_xAdvance = 0 + tag_Indent;
02288 
02289                             continue;
02290                         }
02291                         #endregion End Word Wrapping
02292 
02293 
02294                         // Text Auto-Sizing (text exceeding Width of container. 
02295                         #region Handle Text Auto-Sizing
02296                         if (m_enableAutoSizing && m_fontSize > m_fontSizeMin)
02297                         {
02298                             // Handle Character Width Adjustments
02299                             #region Character Width Adjustments
02300                             if (m_charWidthAdjDelta < m_charWidthMaxAdj / 100)
02301                             {
02302                                 loopCountA = 0;
02303                                 m_charWidthAdjDelta += 0.01f;
02304                                 GenerateTextMesh();
02305                                 return;
02306                             }
02307                             #endregion
02308 
02309                             // Adjust Point Size
02310                             m_maxFontSize = m_fontSize;
02311 
02312                             m_fontSize -= Mathf.Max((m_fontSize - m_minFontSize) / 2, 0.05f);
02313                             m_fontSize = (int)(Mathf.Max(m_fontSize, m_fontSizeMin) * 20 + 0.5f) / 20f;
02314 
02315                             //m_recursiveCount = 0;
02316                             if (loopCountA > 20) return; // Added to debug 
02317                             GenerateTextMesh();
02318                             return;
02319                         }
02320                         #endregion End Text Auto-Sizing
02321 
02322 
02323                         // Handle Text Overflow
02324                         #region Handle Text Overflow
02325                         switch (m_overflowMode)
02326                         {
02327                             case TextOverflowModes.Overflow:
02328                                 if (m_isMaskingEnabled)
02329                                     DisableMasking();
02330 
02331                                 break;
02332                             case TextOverflowModes.Ellipsis:
02333                                 if (m_isMaskingEnabled)
02334                                     DisableMasking();
02335 
02336                                 m_isTextTruncated = true;
02337 
02338                                 if (m_characterCount < 1)
02339                                 {
02340                                     m_textInfo.characterInfo[m_characterCount].isVisible = false;
02341                                     //m_visibleCharacterCount = 0;
02342                                     break;
02343                                 }
02344 
02345                                 m_char_buffer[i - 1] = 8230;
02346                                 m_char_buffer[i] = (char)0;
02347 
02348                                 if (m_cached_Ellipsis_GlyphInfo != null)
02349                                 {
02350                                     m_textInfo.characterInfo[ellipsisIndex].character = (char)8230;
02351                                     m_textInfo.characterInfo[ellipsisIndex].textElement = m_cached_Ellipsis_GlyphInfo;
02352                                     m_textInfo.characterInfo[ellipsisIndex].fontAsset = m_materialReferences[0].fontAsset;
02353                                     m_textInfo.characterInfo[ellipsisIndex].material = m_materialReferences[0].material;
02354                                     m_textInfo.characterInfo[ellipsisIndex].materialReferenceIndex = 0;
02355                                 }
02356                                 else
02357                                 {
02358                                     Debug.LogWarning("Unable to use Ellipsis character since it wasn't found in the current Font Asset [" + m_fontAsset.name + "]. Consider regenerating this font asset to include the Ellipsis character (u+2026).\nNote: Warnings can be disabled in the TMP Settings file.", this);
02359                                 }
02360 
02361                                 m_totalCharacterCount = ellipsisIndex + 1;
02362 
02363                                 GenerateTextMesh();
02364                                 return;
02365                             case TextOverflowModes.Masking:
02366                                 if (!m_isMaskingEnabled)
02367                                     EnableMasking();
02368                                 break;
02369                             case TextOverflowModes.ScrollRect:
02370                                 if (!m_isMaskingEnabled)
02371                                     EnableMasking();
02372                                 break;
02373                             case TextOverflowModes.Truncate:
02374                                 if (m_isMaskingEnabled)
02375                                     DisableMasking();
02376 
02377                                 m_textInfo.characterInfo[m_characterCount].isVisible = false;
02378                                 break;
02379                             case TextOverflowModes.Linked:
02380                                 //m_textInfo.characterInfo[m_characterCount].isVisible = false;
02381 
02382                                 //if (m_linkedTextComponent != null)
02383                                 //{
02384                                 //    m_linkedTextComponent.text = text;
02385                                 //    m_linkedTextComponent.firstVisibleCharacter = m_characterCount;
02386                                 //    m_linkedTextComponent.ForceMeshUpdate();
02387                                 //}
02388                                 break;
02389                         }
02390                         #endregion End Text Overflow
02391 
02392                     }
02393                     #endregion End Check for Characters Exceeding Width of Text Container
02394 
02395 
02396                     // Special handling of characters that are not ignored at the end of a line.
02397                     if (charCode == 9 || charCode == 0xA0 || charCode == 0x2007)
02398                     {
02399                         m_textInfo.characterInfo[m_characterCount].isVisible = false;
02400                         m_lastVisibleCharacterOfLine = m_characterCount;
02401                         m_textInfo.lineInfo[m_lineNumber].spaceCount += 1;
02402                         m_textInfo.spaceCount += 1;
02403 
02404                         if (charCode == 0xA0)
02405                             m_textInfo.lineInfo[m_lineNumber].controlCharacterCount += 1;
02406                     }
02407                     else
02408                     {
02409                         // Determine Vertex Color
02410                         if (m_overrideHtmlColors)
02411                             vertexColor = m_fontColor32;
02412                         else
02413                             vertexColor = m_htmlColor;
02414 
02415                         // Store Character & Sprite Vertex Information
02416                         if (m_textElementType == TMP_TextElementType.Character)
02417                         {
02418                             // Save Character Vertex Data
02419                             SaveGlyphVertexInfo(padding, style_padding, vertexColor);
02420                         }
02421                         else if (m_textElementType == TMP_TextElementType.Sprite)
02422                         {
02423                             SaveSpriteVertexInfo(vertexColor);
02424                         }
02425                     }
02426 
02427 
02428                     // Increase visible count for Characters.
02429                     if (m_textInfo.characterInfo[m_characterCount].isVisible && charCode != 0xAD)
02430                     {
02431                         if (isStartOfNewLine) { isStartOfNewLine = false; m_firstVisibleCharacterOfLine = m_characterCount; }
02432 
02433                         m_lineVisibleCharacterCount += 1;
02434                         m_lastVisibleCharacterOfLine = m_characterCount;
02435                     }
02436                 }
02437                 else
02438                 {   // This is a Space, Tab, LineFeed or Carriage Return
02439 
02440                     // Track # of spaces per line which is used for line justification.
02441                     if ((charCode == 10 || char.IsSeparator((char)charCode)) && charCode != 0xAD && charCode != 0x200B && charCode != 0x2060)
02442                     {
02443                         m_textInfo.lineInfo[m_lineNumber].spaceCount += 1;
02444                         m_textInfo.spaceCount += 1;
02445                     }
02446                 }
02447                 #endregion Handle Visible Characters
02448 
02449 
02450                 // Check if Line Spacing of previous line needs to be adjusted.
02451                 #region Adjust Line Spacing
02452                 if (m_lineNumber > 0 && !TMP_Math.Approximately(m_maxLineAscender, m_startOfLineAscender) && m_lineHeight == TMP_Math.FLOAT_UNSET && !m_isNewPage)
02453                 {
02454                     //Debug.Log("Inline - Adjusting Line Spacing on line #" + m_lineNumber);
02455                     //float gap = 0; // Compute gap.
02456 
02457                     float offsetDelta = m_maxLineAscender - m_startOfLineAscender;
02458                     AdjustLineOffset(m_firstCharacterOfLine, m_characterCount, offsetDelta);
02459                     elementDescenderII -= offsetDelta;
02460                     m_lineOffset += offsetDelta;
02461 
02462                     m_startOfLineAscender += offsetDelta;
02463                     m_SavedWordWrapState.lineOffset = m_lineOffset;
02464                     m_SavedWordWrapState.previousLineAscender = m_startOfLineAscender;
02465                 }
02466                 #endregion
02467 
02468 
02469                 // Store Rectangle positions for each Character.
02470                 #region Store Character Data
02471                 m_textInfo.characterInfo[m_characterCount].lineNumber = m_lineNumber;
02472                 m_textInfo.characterInfo[m_characterCount].pageNumber = m_pageNumber;
02473 
02474                 if (charCode != 10 && charCode != 13 && charCode != 8230 || m_textInfo.lineInfo[m_lineNumber].characterCount == 1)
02475                     m_textInfo.lineInfo[m_lineNumber].alignment = m_lineJustification;
02476                 #endregion Store Character Data
02477 
02478 
02479                 // Check if text Exceeds the vertical bounds of the margin area.
02480                 #region Check Vertical Bounds & Auto-Sizing
02481                 if (m_maxAscender - elementDescenderII > marginHeight + 0.0001f)
02482                 {
02483                     // Handle Line spacing adjustments
02484                     #region Line Spacing Adjustments
02485                     if (m_enableAutoSizing && m_lineSpacingDelta > m_lineSpacingMax && m_lineNumber > 0)
02486                     {
02487                         loopCountA = 0;
02488 
02489                         m_lineSpacingDelta -= 1;
02490                         GenerateTextMesh();
02491                         return;
02492                     }
02493                     #endregion
02494 
02495 
02496                     // Handle Text Auto-sizing resulting from text exceeding vertical bounds.
02497                     #region Text Auto-Sizing (Text greater than vertical bounds)
02498                     if (m_enableAutoSizing && m_fontSize > m_fontSizeMin)
02499                     {
02500                         m_maxFontSize = m_fontSize;
02501 
02502                         m_fontSize -= Mathf.Max((m_fontSize - m_minFontSize) / 2, 0.05f);
02503                         m_fontSize = (int)(Mathf.Max(m_fontSize, m_fontSizeMin) * 20 + 0.5f) / 20f;
02504 
02505                         //m_recursiveCount = 0;
02506                         if (loopCountA > 20) return; // Added to debug 
02507                         GenerateTextMesh();
02508                         return;
02509                     }
02510                     #endregion Text Auto-Sizing
02511 
02512                     // Set isTextOverflowing and firstOverflowCharacterIndex
02513                     if (m_firstOverflowCharacterIndex == -1)
02514                         m_firstOverflowCharacterIndex = m_characterCount;
02515 
02516                     // Handle Text Overflow
02517                     #region Text Overflow
02518                     switch (m_overflowMode)
02519                     {
02520                         case TextOverflowModes.Overflow:
02521                             if (m_isMaskingEnabled)
02522                                 DisableMasking();
02523 
02524                             break;
02525                         case TextOverflowModes.Ellipsis:
02526                             if (m_isMaskingEnabled)
02527                                 DisableMasking();
02528 
02529                             if (m_lineNumber > 0)
02530                             {
02531                                 m_char_buffer[m_textInfo.characterInfo[ellipsisIndex].index] = 8230;
02532                                 m_char_buffer[m_textInfo.characterInfo[ellipsisIndex].index + 1] = (char)0;
02533 
02534                                 if (m_cached_Ellipsis_GlyphInfo != null)
02535                                 {
02536                                     m_textInfo.characterInfo[ellipsisIndex].character = (char)8230;
02537                                     m_textInfo.characterInfo[ellipsisIndex].textElement = m_cached_Ellipsis_GlyphInfo;
02538                                     m_textInfo.characterInfo[ellipsisIndex].fontAsset = m_materialReferences[0].fontAsset;
02539                                     m_textInfo.characterInfo[ellipsisIndex].material = m_materialReferences[0].material;
02540                                     m_textInfo.characterInfo[ellipsisIndex].materialReferenceIndex = 0;
02541                                 }
02542                                 else
02543                                 {
02544                                     Debug.LogWarning("Unable to use Ellipsis character since it wasn't found in the current Font Asset [" + m_fontAsset.name + "]. Consider regenerating this font asset to include the Ellipsis character (u+2026).\nNote: Warnings can be disabled in the TMP Settings file.", this);
02545                                 }
02546 
02547                                 m_totalCharacterCount = ellipsisIndex + 1;
02548 
02549                                 GenerateTextMesh();
02550                                 m_isTextTruncated = true;
02551                                 return;
02552                             }
02553                             else
02554                             {
02555                                 ClearMesh(false);
02556                                 return;
02557                             }
02558                         case TextOverflowModes.Masking:
02559                             if (!m_isMaskingEnabled)
02560                                 EnableMasking();
02561                             break;
02562                         case TextOverflowModes.ScrollRect:
02563                             if (!m_isMaskingEnabled)
02564                                 EnableMasking();
02565                             break;
02566                         case TextOverflowModes.Truncate:
02567                             if (m_isMaskingEnabled)
02568                                 DisableMasking();
02569 
02570                             // TODO : Optimize 
02571                             if (m_lineNumber > 0)
02572                             {
02573                                 m_char_buffer[m_textInfo.characterInfo[ellipsisIndex].index + 1] = (char)0;
02574 
02575                                 m_totalCharacterCount = ellipsisIndex + 1;
02576 
02577                                 GenerateTextMesh();
02578                                 m_isTextTruncated = true;
02579                                 return;
02580                             }
02581                             else
02582                             {
02583                                 ClearMesh(false);
02584                                 return;
02585                             }
02586                         case TextOverflowModes.Page:
02587                             if (m_isMaskingEnabled)
02588                                 DisableMasking();
02589 
02590                             // Ignore Page Break, Linefeed or carriage return
02591                             if (charCode == 13 || charCode == 10)
02592                                 break;
02593 
02594                             // Return if the first character doesn't fit.
02595                             if (i == 0)
02596                             {
02597                                 ClearMesh();
02598                                 return;
02599                             }
02600                             else if (previousPageOverflowChar == i)
02601                             {
02602                                 m_char_buffer[i] = 0;
02603                                 m_isTextTruncated = true;
02604                             }
02605 
02606                             previousPageOverflowChar = i;
02607 
02608                             // Go back to previous line and re-layout 
02609                             i = RestoreWordWrappingState(ref m_SavedLineState);
02610 
02611                             m_isNewPage = true;
02612                             m_xAdvance = 0 + tag_Indent;
02613                             m_lineOffset = 0;
02614                             m_maxAscender = 0;
02615                             pageAscender = 0;
02616                             m_lineNumber += 1;
02617                             m_pageNumber += 1;
02618                             continue;
02619                         case TextOverflowModes.Linked:
02620                             if (m_linkedTextComponent != null)
02621                             {
02622                                 m_linkedTextComponent.text = text;
02623                                 m_linkedTextComponent.firstVisibleCharacter = m_characterCount;
02624                                 m_linkedTextComponent.ForceMeshUpdate();
02625                             }
02626 
02627                             // Truncate remaining text
02628                             if (m_lineNumber > 0)
02629                             {
02630                                 m_char_buffer[i] = (char)0;
02631 
02632                                 m_totalCharacterCount = m_characterCount;
02633 
02634                                 // TODO : Optimize as we should be able to end the layout phase here without having to do another pass.
02635                                 GenerateTextMesh();
02636                                 m_isTextTruncated = true;
02637                                 return;
02638                             }
02639                             else
02640                             {
02641                                 ClearMesh(true);
02642                                 return;
02643                             }
02644                     }
02645                     #endregion End Text Overflow
02646 
02647                 }
02648                 #endregion Check Vertical Bounds
02649 
02650 
02651                 // Handle xAdvance & Tabulation Stops. Tab stops at every 25% of Font Size.
02652                 #region XAdvance, Tabulation & Stops
02653                 if (charCode == 9)
02654                 {
02655                     float tabSize = m_currentFontAsset.fontInfo.TabWidth * currentElementScale;
02656                     float tabs = Mathf.Ceil(m_xAdvance / tabSize) * tabSize;
02657                     m_xAdvance = tabs > m_xAdvance ? tabs : m_xAdvance + tabSize;
02658                 }
02659                 else if (m_monoSpacing != 0)
02660                 {
02661                     m_xAdvance += (m_monoSpacing - monoAdvance + ((m_characterSpacing + m_currentFontAsset.normalSpacingOffset) * currentElementScale) + m_cSpacing) * (1 - m_charWidthAdjDelta);
02662 
02663                     if (char.IsWhiteSpace((char)charCode) || charCode == 0x200B)
02664                         m_xAdvance += m_wordSpacing * currentElementScale;
02665                 }
02666                 else if (!m_isRightToLeft)
02667                 {
02668                     float scaleFXMultiplier = 1;
02669                     if (m_isFXMatrixSet) scaleFXMultiplier = m_FXMatrix.m00;
02670 
02671                     m_xAdvance += ((m_cached_TextElement.xAdvance * scaleFXMultiplier * bold_xAdvance_multiplier + m_characterSpacing + m_currentFontAsset.normalSpacingOffset + glyphAdjustments.xAdvance) * currentElementScale + m_cSpacing) * (1 - m_charWidthAdjDelta);
02672 
02673                     if (char.IsWhiteSpace((char)charCode) || charCode == 0x200B)
02674                         m_xAdvance += m_wordSpacing * currentElementScale;
02675                 }
02676                 else
02677                 {
02678                     m_xAdvance -= glyphAdjustments.xAdvance * currentElementScale;
02679                 }
02680 
02681 
02682                 // Store xAdvance information
02683                 m_textInfo.characterInfo[m_characterCount].xAdvance = m_xAdvance;
02684 
02685                 #endregion Tabulation & Stops
02686 
02687 
02688                 // Handle Carriage Return
02689                 #region Carriage Return
02690                 if (charCode == 13)
02691                 {
02692                     m_xAdvance = 0 + tag_Indent;
02693                 }
02694                 #endregion Carriage Return
02695 
02696 
02697                 // Handle Line Spacing Adjustments + Word Wrapping & special case for last line.
02698                 #region Check for Line Feed and Last Character
02699                 if (charCode == 10 || m_characterCount == totalCharacterCount - 1)
02700                 {
02701                     // Check if Line Spacing of previous line needs to be adjusted.
02702                     if (m_lineNumber > 0 && !TMP_Math.Approximately(m_maxLineAscender, m_startOfLineAscender) && m_lineHeight == TMP_Math.FLOAT_UNSET && !m_isNewPage)
02703                     {
02704                         //Debug.Log("Line Feed - Adjusting Line Spacing on line #" + m_lineNumber);
02705                         float offsetDelta = m_maxLineAscender - m_startOfLineAscender;
02706                         AdjustLineOffset(m_firstCharacterOfLine, m_characterCount, offsetDelta);
02707                         elementDescenderII -= offsetDelta;
02708                         m_lineOffset += offsetDelta;
02709                     }
02710                     m_isNewPage = false;
02711 
02712                     // Calculate lineAscender & make sure if last character is superscript or subscript that we check that as well.
02713                     float lineAscender = m_maxLineAscender - m_lineOffset;
02714                     float lineDescender = m_maxLineDescender - m_lineOffset;
02715 
02716                     // Update maxDescender and maxVisibleDescender
02717                     m_maxDescender = m_maxDescender < lineDescender ? m_maxDescender : lineDescender;
02718                     if (!isMaxVisibleDescenderSet)
02719                         maxVisibleDescender = m_maxDescender;
02720 
02721                     if (m_useMaxVisibleDescender && (m_characterCount >= m_maxVisibleCharacters || m_lineNumber >= m_maxVisibleLines))
02722                         isMaxVisibleDescenderSet = true;
02723 
02724                     // Save Line Information
02725                     m_textInfo.lineInfo[m_lineNumber].firstCharacterIndex = m_firstCharacterOfLine;
02726                     m_textInfo.lineInfo[m_lineNumber].firstVisibleCharacterIndex = m_firstVisibleCharacterOfLine = m_firstCharacterOfLine > m_firstVisibleCharacterOfLine ? m_firstCharacterOfLine : m_firstVisibleCharacterOfLine;
02727                     m_textInfo.lineInfo[m_lineNumber].lastCharacterIndex = m_lastCharacterOfLine = m_characterCount;
02728                     m_textInfo.lineInfo[m_lineNumber].lastVisibleCharacterIndex = m_lastVisibleCharacterOfLine = m_lastVisibleCharacterOfLine < m_firstVisibleCharacterOfLine ? m_firstVisibleCharacterOfLine : m_lastVisibleCharacterOfLine;
02729 
02730                     m_textInfo.lineInfo[m_lineNumber].characterCount = m_textInfo.lineInfo[m_lineNumber].lastCharacterIndex - m_textInfo.lineInfo[m_lineNumber].firstCharacterIndex + 1;
02731                     m_textInfo.lineInfo[m_lineNumber].visibleCharacterCount = m_lineVisibleCharacterCount;
02732                     m_textInfo.lineInfo[m_lineNumber].lineExtents.min = new Vector2(m_textInfo.characterInfo[m_firstVisibleCharacterOfLine].bottomLeft.x, lineDescender);
02733                     m_textInfo.lineInfo[m_lineNumber].lineExtents.max = new Vector2(m_textInfo.characterInfo[m_lastVisibleCharacterOfLine].topRight.x, lineAscender);
02734                     m_textInfo.lineInfo[m_lineNumber].length = m_textInfo.lineInfo[m_lineNumber].lineExtents.max.x - (padding * currentElementScale);
02735                     m_textInfo.lineInfo[m_lineNumber].width = width;
02736 
02737                     if (m_textInfo.lineInfo[m_lineNumber].characterCount == 1)
02738                         m_textInfo.lineInfo[m_lineNumber].alignment = m_lineJustification;
02739 
02740                     if (m_textInfo.characterInfo[m_lastVisibleCharacterOfLine].isVisible)
02741                         m_textInfo.lineInfo[m_lineNumber].maxAdvance = m_textInfo.characterInfo[m_lastVisibleCharacterOfLine].xAdvance - (m_characterSpacing + m_currentFontAsset.normalSpacingOffset) * currentElementScale - m_cSpacing;
02742                     else
02743                         m_textInfo.lineInfo[m_lineNumber].maxAdvance = m_textInfo.characterInfo[m_lastCharacterOfLine].xAdvance - (m_characterSpacing + m_currentFontAsset.normalSpacingOffset) * currentElementScale - m_cSpacing;
02744 
02745                     m_textInfo.lineInfo[m_lineNumber].baseline = 0 - m_lineOffset;
02746                     m_textInfo.lineInfo[m_lineNumber].ascender = lineAscender;
02747                     m_textInfo.lineInfo[m_lineNumber].descender = lineDescender;
02748                     m_textInfo.lineInfo[m_lineNumber].lineHeight = lineAscender - lineDescender + lineGap * baseScale;
02749 
02750                     m_firstCharacterOfLine = m_characterCount + 1;
02751                     m_lineVisibleCharacterCount = 0;
02752 
02753                     // Add new line if not last line or character.
02754                     if (charCode == 10)
02755                     {
02756                         // Store the state of the line before starting on the new line.
02757                         SaveWordWrappingState(ref m_SavedLineState, i, m_characterCount);
02758                         // Store the state of the last Character before the new line.
02759                         SaveWordWrappingState(ref m_SavedWordWrapState, i, m_characterCount);
02760 
02761                         m_lineNumber += 1;
02762                         isStartOfNewLine = true;
02763                         ignoreNonBreakingSpace = false;
02764                         isFirstWord = true;
02765 
02766                         // Check to make sure Array is large enough to hold a new line.
02767                         if (m_lineNumber >= m_textInfo.lineInfo.Length)
02768                             ResizeLineExtents(m_lineNumber);
02769 
02770                         // Apply Line Spacing
02771                         if (m_lineHeight == TMP_Math.FLOAT_UNSET)
02772                         {
02773                             lineOffsetDelta = 0 - m_maxLineDescender + elementAscender + (lineGap + m_lineSpacing + m_paragraphSpacing + m_lineSpacingDelta) * baseScale;
02774                             m_lineOffset += lineOffsetDelta;
02775                         }
02776                         else
02777                             m_lineOffset += m_lineHeight + (m_lineSpacing + m_paragraphSpacing) * baseScale;
02778 
02779                         m_maxLineAscender = k_LargeNegativeFloat;
02780                         m_maxLineDescender = k_LargePositiveFloat;
02781                         m_startOfLineAscender = elementAscender;
02782 
02783                         m_xAdvance = 0 + tag_LineIndent + tag_Indent;
02784 
02785                         ellipsisIndex = m_characterCount - 1;
02786 
02787                         m_characterCount += 1;
02788                         continue;
02789                     }
02790                 }
02791                 #endregion Check for Linefeed or Last Character
02792 
02793 
02794                 // Store Rectangle positions for each Character.
02795                 #region Save CharacterInfo for the current character.
02796                 // Determine the bounds of the Mesh.
02797                 if (m_textInfo.characterInfo[m_characterCount].isVisible)
02798                 {
02799                     m_meshExtents.min.x = Mathf.Min(m_meshExtents.min.x, m_textInfo.characterInfo[m_characterCount].bottomLeft.x);
02800                     m_meshExtents.min.y = Mathf.Min(m_meshExtents.min.y, m_textInfo.characterInfo[m_characterCount].bottomLeft.y);
02801 
02802                     m_meshExtents.max.x = Mathf.Max(m_meshExtents.max.x, m_textInfo.characterInfo[m_characterCount].topRight.x);
02803                     m_meshExtents.max.y = Mathf.Max(m_meshExtents.max.y, m_textInfo.characterInfo[m_characterCount].topRight.y);
02804 
02805                     //m_meshExtents.min = new Vector2(Mathf.Min(m_meshExtents.min.x, m_textInfo.characterInfo[m_characterCount].bottomLeft.x), Mathf.Min(m_meshExtents.min.y, m_textInfo.characterInfo[m_characterCount].bottomLeft.y));
02806                     //m_meshExtents.max = new Vector2(Mathf.Max(m_meshExtents.max.x, m_textInfo.characterInfo[m_characterCount].topRight.x), Mathf.Max(m_meshExtents.max.y, m_textInfo.characterInfo[m_characterCount].topRight.y));
02807                 }
02808 
02809 
02810                 // Save pageInfo Data
02811                 if (m_overflowMode == TextOverflowModes.Page && charCode != 13 && charCode != 10) // && m_pageNumber < 16)
02812                 {
02813                     // Check if we need to increase allocations for the pageInfo array.
02814                     if (m_pageNumber + 1 > m_textInfo.pageInfo.Length)
02815                         TMP_TextInfo.Resize(ref m_textInfo.pageInfo, m_pageNumber + 1, true);
02816 
02817                     m_textInfo.pageInfo[m_pageNumber].ascender = pageAscender;
02818                     m_textInfo.pageInfo[m_pageNumber].descender = elementDescender < m_textInfo.pageInfo[m_pageNumber].descender ? elementDescender : m_textInfo.pageInfo[m_pageNumber].descender;
02819 
02820                     if (m_pageNumber == 0 && m_characterCount == 0)
02821                         m_textInfo.pageInfo[m_pageNumber].firstCharacterIndex = m_characterCount;
02822                     else if (m_characterCount > 0 && m_pageNumber != m_textInfo.characterInfo[m_characterCount - 1].pageNumber)
02823                     {
02824                         m_textInfo.pageInfo[m_pageNumber - 1].lastCharacterIndex = m_characterCount - 1;
02825                         m_textInfo.pageInfo[m_pageNumber].firstCharacterIndex = m_characterCount;
02826                     }
02827                     else if (m_characterCount == totalCharacterCount - 1)
02828                         m_textInfo.pageInfo[m_pageNumber].lastCharacterIndex = m_characterCount;
02829                 }
02830                 #endregion Saving CharacterInfo
02831 
02832 
02833                 // Save State of Mesh Creation for handling of Word Wrapping
02834                 #region Save Word Wrapping State
02835                 if (m_enableWordWrapping || m_overflowMode == TextOverflowModes.Truncate || m_overflowMode == TextOverflowModes.Ellipsis)
02836                 {
02837                     if ((char.IsWhiteSpace((char)charCode) || charCode == 0x200B || charCode == 0x2D || charCode == 0xAD) && (!m_isNonBreakingSpace || ignoreNonBreakingSpace) && charCode != 0xA0 && charCode != 0x2007 && charCode != 0x2011 && charCode != 0x202F && charCode != 0x2060)
02838                     {
02839                         // We store the state of numerous variables for the most recent Space, LineFeed or Carriage Return to enable them to be restored 
02840                         // for Word Wrapping.
02841                         SaveWordWrappingState(ref m_SavedWordWrapState, i, m_characterCount);
02842                         m_isCharacterWrappingEnabled = false;
02843                         isFirstWord = false;
02844                     }
02845                     // Handling for East Asian languages
02846                     else if ((  charCode > 0x1100 && charCode < 0x11ff || /* Hangul Jamo */
02847                                 charCode > 0x2E80 && charCode < 0x9FFF || /* CJK */
02848                                 charCode > 0xA960 && charCode < 0xA97F || /* Hangul Jame Extended-A */
02849                                 charCode > 0xAC00 && charCode < 0xD7FF || /* Hangul Syllables */
02850                                 charCode > 0xF900 && charCode < 0xFAFF || /* CJK Compatibility Ideographs */
02851                                 charCode > 0xFE30 && charCode < 0xFE4F || /* CJK Compatibility Forms */
02852                                 charCode > 0xFF00 && charCode < 0xFFEF)   /* CJK Halfwidth */
02853                                 && !m_isNonBreakingSpace)
02854                     {
02855                         if (isFirstWord || isLastBreakingChar || TMP_Settings.linebreakingRules.leadingCharacters.ContainsKey(charCode) == false &&
02856                             (m_characterCount < totalCharacterCount - 1 &&
02857                             TMP_Settings.linebreakingRules.followingCharacters.ContainsKey(m_textInfo.characterInfo[m_characterCount + 1].character) == false))
02858                         {
02859                             SaveWordWrappingState(ref m_SavedWordWrapState, i, m_characterCount);
02860                             m_isCharacterWrappingEnabled = false;
02861                             isFirstWord = false;
02862                         }
02863                     }
02864                     else if ((isFirstWord || m_isCharacterWrappingEnabled == true || isLastBreakingChar))
02865                         SaveWordWrappingState(ref m_SavedWordWrapState, i, m_characterCount);
02866                 }
02867                 #endregion Save Word Wrapping State
02868 
02869                 m_characterCount += 1;
02870             }
02871 
02872             // Check Auto Sizing and increase font size to fill text container.
02873             #region Check Auto-Sizing (Upper Font Size Bounds)
02874             fontSizeDelta = m_maxFontSize - m_minFontSize;
02875             if (!m_isCharacterWrappingEnabled && m_enableAutoSizing && fontSizeDelta > 0.051f && m_fontSize < m_fontSizeMax)
02876             {
02877                 m_minFontSize = m_fontSize;
02878                 m_fontSize += Mathf.Max((m_maxFontSize - m_fontSize) / 2, 0.05f);
02879                 m_fontSize = (int)(Mathf.Min(m_fontSize, m_fontSizeMax) * 20 + 0.5f) / 20f;
02880 
02881                 //Debug.Log(m_fontSize);
02882 
02883                 if (loopCountA > 20) return; // Added to debug
02884                 GenerateTextMesh();
02885                 return;
02886             }
02887             #endregion End Auto-sizing Check
02888 
02889 
02890             m_isCharacterWrappingEnabled = false;
02891 
02892 
02893             //Debug.Log("Iteration Count: " + loopCountA + ". Final Point Size: " + m_fontSize); // + "  B: " + loopCountB + "  C: " + loopCountC + "  D: " + loopCountD);
02894 
02895 
02896 
02897             // If there are no visible characters... no need to continue
02898             if (m_characterCount == 0) // && m_visibleSpriteCount == 0)
02899             {
02900                 ClearMesh(true);
02901 
02902                 // Event indicating the text has been regenerated.
02903                 TMPro_EventManager.ON_TEXT_CHANGED(this);
02904                 return;
02905             }
02906 
02907 
02908             // *** PHASE II of Text Generation ***
02909             int last_vert_index = m_materialReferences[0].referenceCount * (!m_isVolumetricText ? 4 : 8);
02910 
02911             // Partial clear of the vertices array to mark unused vertices as degenerate.
02912             m_textInfo.meshInfo[0].Clear(false);
02913 
02914             // Handle Text Alignment
02915             #region Text Vertical Alignment
02916             Vector3 anchorOffset = Vector3.zero;
02917             Vector3[] corners = m_RectTransformCorners; // GetTextContainerLocalCorners();
02918 
02919             // Handle Vertical Text Alignment
02920             switch (m_textAlignment)
02921             {
02922                 // Top Vertically
02923                 case TextAlignmentOptions.Top:
02924                 case TextAlignmentOptions.TopLeft:
02925                 case TextAlignmentOptions.TopRight:
02926                 case TextAlignmentOptions.TopJustified:
02927                 case TextAlignmentOptions.TopFlush:
02928                 case TextAlignmentOptions.TopGeoAligned:
02929                     if (m_overflowMode != TextOverflowModes.Page)
02930                         anchorOffset = corners[1] + new Vector3(0 + margins.x, 0 - m_maxAscender - margins.y, 0);
02931                     else
02932                         anchorOffset = corners[1] + new Vector3(0 + margins.x, 0 - m_textInfo.pageInfo[pageToDisplay].ascender - margins.y, 0);
02933                     break;
02934 
02935                 // Middle Vertically
02936                 case TextAlignmentOptions.Left:
02937                 case TextAlignmentOptions.Right:
02938                 case TextAlignmentOptions.Center:
02939                 case TextAlignmentOptions.Justified:
02940                 case TextAlignmentOptions.Flush:
02941                 case TextAlignmentOptions.CenterGeoAligned:
02942                     if (m_overflowMode != TextOverflowModes.Page)
02943                         anchorOffset = (corners[0] + corners[1]) / 2 + new Vector3(0 + margins.x, 0 - (m_maxAscender + margins.y + maxVisibleDescender - margins.w) / 2, 0);
02944                     else
02945                         anchorOffset = (corners[0] + corners[1]) / 2 + new Vector3(0 + margins.x, 0 - (m_textInfo.pageInfo[pageToDisplay].ascender + margins.y + m_textInfo.pageInfo[pageToDisplay].descender - margins.w) / 2, 0);
02946                     break;
02947 
02948                 // Bottom Vertically
02949                 case TextAlignmentOptions.Bottom:
02950                 case TextAlignmentOptions.BottomLeft:
02951                 case TextAlignmentOptions.BottomRight:
02952                 case TextAlignmentOptions.BottomJustified:
02953                 case TextAlignmentOptions.BottomFlush:
02954                 case TextAlignmentOptions.BottomGeoAligned:
02955                     if (m_overflowMode != TextOverflowModes.Page)
02956                         anchorOffset = corners[0] + new Vector3(0 + margins.x, 0 - maxVisibleDescender + margins.w, 0);
02957                     else
02958                         anchorOffset = corners[0] + new Vector3(0 + margins.x, 0 - m_textInfo.pageInfo[pageToDisplay].descender + margins.w, 0);
02959                     break;
02960 
02961                 // Baseline Vertically
02962                 case TextAlignmentOptions.Baseline:
02963                 case TextAlignmentOptions.BaselineLeft:
02964                 case TextAlignmentOptions.BaselineRight:
02965                 case TextAlignmentOptions.BaselineJustified:
02966                 case TextAlignmentOptions.BaselineFlush:
02967                 case TextAlignmentOptions.BaselineGeoAligned:
02968                     anchorOffset = (corners[0] + corners[1]) / 2 + new Vector3(0 + margins.x, 0, 0);
02969                     break;
02970 
02971                 // Midline Vertically 
02972                 case TextAlignmentOptions.MidlineLeft:
02973                 case TextAlignmentOptions.Midline:
02974                 case TextAlignmentOptions.MidlineRight:
02975                 case TextAlignmentOptions.MidlineJustified:
02976                 case TextAlignmentOptions.MidlineFlush:
02977                 case TextAlignmentOptions.MidlineGeoAligned:
02978                     anchorOffset = (corners[0] + corners[1]) / 2 + new Vector3(0 + margins.x, 0 - (m_meshExtents.max.y + margins.y + m_meshExtents.min.y - margins.w) / 2, 0);
02979                     break;
02980 
02981                 // Capline Vertically 
02982                 case TextAlignmentOptions.CaplineLeft:
02983                 case TextAlignmentOptions.Capline:
02984                 case TextAlignmentOptions.CaplineRight:
02985                 case TextAlignmentOptions.CaplineJustified:
02986                 case TextAlignmentOptions.CaplineFlush:
02987                 case TextAlignmentOptions.CaplineGeoAligned:
02988                     anchorOffset = (corners[0] + corners[1]) / 2 + new Vector3(0 + margins.x, 0 - (m_maxCapHeight - margins.y - margins.w) / 2, 0);
02989                     break;
02990             }
02991             #endregion
02992 
02993 
02994             // Initialization for Second Pass
02995             Vector3 justificationOffset = Vector3.zero;
02996             Vector3 offset = Vector3.zero;
02997             int vert_index_X4 = 0;
02998             int sprite_index_X4 = 0;
02999 
03000             int wordCount = 0;
03001             int lineCount = 0;
03002             int lastLine = 0;
03003             bool isFirstSeperator = false;
03004 
03005             bool isStartOfWord = false;
03006             int wordFirstChar = 0;
03007             int wordLastChar = 0;
03008 
03009             // Second Pass : Line Justification, UV Mapping, Character & Line Visibility & more.
03010             float lossyScale = m_previousLossyScaleY = this.transform.lossyScale.y;
03011 
03012             Color32 underlineColor = Color.white;
03013             Color32 strikethroughColor = Color.white;
03014             Color32 highlightColor = new Color32(255, 255, 0, 64);
03015             float xScale = 0;
03016             float underlineStartScale = 0;
03017             float underlineEndScale = 0;
03018             float underlineMaxScale = 0;
03019             float underlineBaseLine = k_LargePositiveFloat;
03020             int lastPage = 0;
03021 
03022             float strikethroughPointSize = 0;
03023             float strikethroughScale = 0;
03024             float strikethroughBaseline = 0;
03025 
03026             TMP_CharacterInfo[] characterInfos = m_textInfo.characterInfo;
03027             #region Handle Line Justification & UV Mapping & Character Visibility & More
03028             for (int i = 0; i < m_characterCount; i++)
03029             {
03030                 TMP_FontAsset currentFontAsset = characterInfos[i].fontAsset;
03031 
03032                 char currentCharacter = characterInfos[i].character;
03033 
03034                 int currentLine = characterInfos[i].lineNumber;
03035                 TMP_LineInfo lineInfo = m_textInfo.lineInfo[currentLine];
03036                 lineCount = currentLine + 1;
03037 
03038                 TextAlignmentOptions lineAlignment = lineInfo.alignment;
03039 
03040                 // Process Line Justification
03041                 #region Handle Line Justification
03042                 switch (lineAlignment)
03043                 {
03044                     case TextAlignmentOptions.TopLeft:
03045                     case TextAlignmentOptions.Left:
03046                     case TextAlignmentOptions.BottomLeft:
03047                     case TextAlignmentOptions.BaselineLeft:
03048                     case TextAlignmentOptions.MidlineLeft:
03049                     case TextAlignmentOptions.CaplineLeft:
03050                             if (!m_isRightToLeft)
03051                                 justificationOffset = new Vector3(0 + lineInfo.marginLeft, 0, 0);
03052                             else
03053                                 justificationOffset = new Vector3(0 - lineInfo.maxAdvance, 0, 0);
03054                             break;
03055 
03056                     case TextAlignmentOptions.Top:
03057                     case TextAlignmentOptions.Center:
03058                     case TextAlignmentOptions.Bottom:
03059                     case TextAlignmentOptions.Baseline:
03060                     case TextAlignmentOptions.Midline:
03061                     case TextAlignmentOptions.Capline:
03062                         justificationOffset = new Vector3(lineInfo.marginLeft + lineInfo.width / 2 - lineInfo.maxAdvance / 2, 0, 0);
03063                         break;
03064 
03065                     case TextAlignmentOptions.TopGeoAligned:
03066                     case TextAlignmentOptions.CenterGeoAligned:
03067                     case TextAlignmentOptions.BottomGeoAligned:
03068                     case TextAlignmentOptions.BaselineGeoAligned:
03069                     case TextAlignmentOptions.MidlineGeoAligned:
03070                     case TextAlignmentOptions.CaplineGeoAligned:
03071                         justificationOffset = new Vector3(lineInfo.marginLeft + lineInfo.width / 2 - (lineInfo.lineExtents.min.x + lineInfo.lineExtents.max.x) / 2, 0, 0);
03072                         break;
03073 
03074                     case TextAlignmentOptions.TopRight:
03075                     case TextAlignmentOptions.Right:
03076                     case TextAlignmentOptions.BottomRight:
03077                     case TextAlignmentOptions.BaselineRight:
03078                     case TextAlignmentOptions.MidlineRight:
03079                     case TextAlignmentOptions.CaplineRight:
03080                         if (!m_isRightToLeft)
03081                             justificationOffset = new Vector3(lineInfo.marginLeft + lineInfo.width - lineInfo.maxAdvance, 0, 0);
03082                         else
03083                             justificationOffset = new Vector3(lineInfo.marginLeft + lineInfo.width, 0, 0);
03084                         break;
03085 
03086                     case TextAlignmentOptions.TopJustified:
03087                     case TextAlignmentOptions.Justified:
03088                     case TextAlignmentOptions.BottomJustified:
03089                     case TextAlignmentOptions.BaselineJustified:
03090                     case TextAlignmentOptions.MidlineJustified:
03091                     case TextAlignmentOptions.CaplineJustified:
03092                     case TextAlignmentOptions.TopFlush:
03093                     case TextAlignmentOptions.Flush:
03094                     case TextAlignmentOptions.BottomFlush:
03095                     case TextAlignmentOptions.BaselineFlush:
03096                     case TextAlignmentOptions.MidlineFlush:
03097                     case TextAlignmentOptions.CaplineFlush:
03098                         // Skip Zero Width Characters
03099                         if (currentCharacter == 0xAD || currentCharacter == 0x200B || currentCharacter == 0x2060) break;
03100 
03101                         char lastCharOfCurrentLine = characterInfos[lineInfo.lastCharacterIndex].character;
03102                         bool isFlush = ((_HorizontalAlignmentOptions)lineAlignment & _HorizontalAlignmentOptions.Flush) == _HorizontalAlignmentOptions.Flush;
03103 
03104                         // In Justified mode, all lines are justified except the last one.
03105                         // In Flush mode, all lines are justified.
03106                         if (char.IsControl(lastCharOfCurrentLine) == false && currentLine < m_lineNumber || isFlush || lineInfo.maxAdvance > lineInfo.width)
03107                         {
03108                             // First character of each line.
03109                             if (currentLine != lastLine || i == 0 || i == m_firstVisibleCharacter)
03110                             {
03111                                 if (!m_isRightToLeft)
03112                                     justificationOffset = new Vector3(lineInfo.marginLeft, 0, 0);
03113                                 else
03114                                     justificationOffset = new Vector3(lineInfo.marginLeft + lineInfo.width, 0, 0);
03115 
03116                                 if (char.IsSeparator(currentCharacter))
03117                                     isFirstSeperator = true;
03118                                 else
03119                                     isFirstSeperator = false;
03120                             }
03121                             else
03122                             {
03123                                 float gap = !m_isRightToLeft ? lineInfo.width - lineInfo.maxAdvance : lineInfo.width + lineInfo.maxAdvance;
03124 
03125                                 int visibleCount = lineInfo.visibleCharacterCount - 1 + lineInfo.controlCharacterCount;
03126 
03127                                 // Get the number of spaces for each line ignoring the last character if it is not visible (ie. a space or linefeed).
03128                                 int spaces = (characterInfos[lineInfo.lastCharacterIndex].isVisible ? lineInfo.spaceCount : lineInfo.spaceCount - 1) - lineInfo.controlCharacterCount;
03129 
03130                                 if (isFirstSeperator) { spaces -= 1; visibleCount += 1; }
03131 
03132                                 float ratio = spaces > 0 ? m_wordWrappingRatios : 1;
03133 
03134                                 if (spaces < 1) spaces = 1;
03135 
03136                                 if (currentCharacter != 0xA0 && (currentCharacter == 9 || char.IsSeparator((char)currentCharacter)))
03137                                 {
03138                                     if (!m_isRightToLeft)
03139                                         justificationOffset += new Vector3(gap * (1 - ratio) / spaces, 0, 0);
03140                                     else
03141                                         justificationOffset -= new Vector3(gap * (1 - ratio) / spaces, 0, 0);
03142                                 }
03143                                 else
03144                                 {
03145                                     if (!m_isRightToLeft)
03146                                         justificationOffset += new Vector3(gap * ratio / visibleCount, 0, 0);
03147                                     else
03148                                         justificationOffset -= new Vector3(gap * ratio / visibleCount, 0, 0);
03149                                 }
03150                             }
03151                         }
03152                         else
03153                         {
03154                             if (!m_isRightToLeft)
03155                                 justificationOffset = new Vector3(lineInfo.marginLeft, 0, 0); // Keep last line left justified.
03156                             else
03157                                 justificationOffset = new Vector3(lineInfo.marginLeft + lineInfo.width, 0, 0); // Keep last line right justified.
03158                         }
03159                         //Debug.Log("Char [" + (char)charCode + "] Code:" + charCode + "  Line # " + currentLine + "  Offset:" + justificationOffset + "  # Spaces:" + lineInfo.spaceCount + "  # Characters:" + lineInfo.characterCount);
03160                         break;
03161                 }
03162                 #endregion End Text Justification
03163 
03164                 offset = anchorOffset + justificationOffset;
03165 
03166                 // Handle UV2 mapping options and packing of scale information into UV2.
03167                 #region Handling of UV2 mapping & Scale packing
03168                 bool isCharacterVisible = characterInfos[i].isVisible;
03169                 if (isCharacterVisible)
03170                 {
03171                     TMP_TextElementType elementType = characterInfos[i].elementType;
03172                     switch (elementType)
03173                     {
03174                         // CHARACTERS
03175                         case TMP_TextElementType.Character:
03176                             Extents lineExtents = lineInfo.lineExtents;
03177                             float uvOffset = (m_uvLineOffset * currentLine) % 1; // + m_uvOffset.x;
03178 
03179                             // Setup UV2 based on Character Mapping Options Selected
03180                             #region Handle UV Mapping Options
03181                             switch (m_horizontalMapping)
03182                             {
03183                                 case TextureMappingOptions.Character:
03184                                     characterInfos[i].vertex_BL.uv2.x = 0; //+ m_uvOffset.x;
03185                                     characterInfos[i].vertex_TL.uv2.x = 0; //+ m_uvOffset.x;
03186                                     characterInfos[i].vertex_TR.uv2.x = 1; //+ m_uvOffset.x;
03187                                     characterInfos[i].vertex_BR.uv2.x = 1; //+ m_uvOffset.x;
03188                                     break;
03189 
03190                                 case TextureMappingOptions.Line:
03191                                     if (m_textAlignment != TextAlignmentOptions.Justified)
03192                                     {
03193                                         characterInfos[i].vertex_BL.uv2.x = (characterInfos[i].vertex_BL.position.x - lineExtents.min.x) / (lineExtents.max.x - lineExtents.min.x) + uvOffset;
03194                                         characterInfos[i].vertex_TL.uv2.x = (characterInfos[i].vertex_TL.position.x - lineExtents.min.x) / (lineExtents.max.x - lineExtents.min.x) + uvOffset;
03195                                         characterInfos[i].vertex_TR.uv2.x = (characterInfos[i].vertex_TR.position.x - lineExtents.min.x) / (lineExtents.max.x - lineExtents.min.x) + uvOffset;
03196                                         characterInfos[i].vertex_BR.uv2.x = (characterInfos[i].vertex_BR.position.x - lineExtents.min.x) / (lineExtents.max.x - lineExtents.min.x) + uvOffset;
03197                                         break;
03198                                     }
03199                                     else // Special Case if Justified is used in Line Mode.
03200                                     {
03201                                         characterInfos[i].vertex_BL.uv2.x = (characterInfos[i].vertex_BL.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03202                                         characterInfos[i].vertex_TL.uv2.x = (characterInfos[i].vertex_TL.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03203                                         characterInfos[i].vertex_TR.uv2.x = (characterInfos[i].vertex_TR.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03204                                         characterInfos[i].vertex_BR.uv2.x = (characterInfos[i].vertex_BR.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03205                                         break;
03206                                     }
03207 
03208                                 case TextureMappingOptions.Paragraph:
03209                                     characterInfos[i].vertex_BL.uv2.x = (characterInfos[i].vertex_BL.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03210                                     characterInfos[i].vertex_TL.uv2.x = (characterInfos[i].vertex_TL.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03211                                     characterInfos[i].vertex_TR.uv2.x = (characterInfos[i].vertex_TR.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03212                                     characterInfos[i].vertex_BR.uv2.x = (characterInfos[i].vertex_BR.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03213                                     break;
03214 
03215                                 case TextureMappingOptions.MatchAspect:
03216 
03217                                     switch (m_verticalMapping)
03218                                     {
03219                                         case TextureMappingOptions.Character:
03220                                             characterInfos[i].vertex_BL.uv2.y = 0; //+ m_uvOffset.y;
03221                                             characterInfos[i].vertex_TL.uv2.y = 1; //+ m_uvOffset.y;
03222                                             characterInfos[i].vertex_TR.uv2.y = 0; //+ m_uvOffset.y;
03223                                             characterInfos[i].vertex_BR.uv2.y = 1; //+ m_uvOffset.y;
03224                                             break;
03225 
03226                                         case TextureMappingOptions.Line:
03227                                             characterInfos[i].vertex_BL.uv2.y = (characterInfos[i].vertex_BL.position.y - lineExtents.min.y) / (lineExtents.max.y - lineExtents.min.y) + uvOffset;
03228                                             characterInfos[i].vertex_TL.uv2.y = (characterInfos[i].vertex_TL.position.y - lineExtents.min.y) / (lineExtents.max.y - lineExtents.min.y) + uvOffset;
03229                                             characterInfos[i].vertex_TR.uv2.y = characterInfos[i].vertex_BL.uv2.y;
03230                                             characterInfos[i].vertex_BR.uv2.y = characterInfos[i].vertex_TL.uv2.y;
03231                                             break;
03232 
03233                                         case TextureMappingOptions.Paragraph:
03234                                             characterInfos[i].vertex_BL.uv2.y = (characterInfos[i].vertex_BL.position.y - m_meshExtents.min.y) / (m_meshExtents.max.y - m_meshExtents.min.y) + uvOffset;
03235                                             characterInfos[i].vertex_TL.uv2.y = (characterInfos[i].vertex_TL.position.y - m_meshExtents.min.y) / (m_meshExtents.max.y - m_meshExtents.min.y) + uvOffset;
03236                                             characterInfos[i].vertex_TR.uv2.y = characterInfos[i].vertex_BL.uv2.y;
03237                                             characterInfos[i].vertex_BR.uv2.y = characterInfos[i].vertex_TL.uv2.y;
03238                                             break;
03239 
03240                                         case TextureMappingOptions.MatchAspect:
03241                                             Debug.Log("ERROR: Cannot Match both Vertical & Horizontal.");
03242                                             break;
03243                                     }
03244 
03245                                     //float xDelta = 1 - (_uv2s[vert_index + 0].y * textMeshCharacterInfo[i].AspectRatio); // Left aligned
03246                                     float xDelta = (1 - ((characterInfos[i].vertex_BL.uv2.y + characterInfos[i].vertex_TL.uv2.y) * characterInfos[i].aspectRatio)) / 2; // Center of Rectangle
03247 
03248                                     characterInfos[i].vertex_BL.uv2.x = (characterInfos[i].vertex_BL.uv2.y * characterInfos[i].aspectRatio) + xDelta + uvOffset;
03249                                     characterInfos[i].vertex_TL.uv2.x = characterInfos[i].vertex_BL.uv2.x;
03250                                     characterInfos[i].vertex_TR.uv2.x = (characterInfos[i].vertex_TL.uv2.y * characterInfos[i].aspectRatio) + xDelta + uvOffset;
03251                                     characterInfos[i].vertex_BR.uv2.x = characterInfos[i].vertex_TR.uv2.x;
03252                                     break;
03253                             }
03254 
03255                             switch (m_verticalMapping)
03256                             {
03257                                 case TextureMappingOptions.Character:
03258                                     characterInfos[i].vertex_BL.uv2.y = 0; //+ m_uvOffset.y;
03259                                     characterInfos[i].vertex_TL.uv2.y = 1; //+ m_uvOffset.y;
03260                                     characterInfos[i].vertex_TR.uv2.y = 1; //+ m_uvOffset.y;
03261                                     characterInfos[i].vertex_BR.uv2.y = 0; //+ m_uvOffset.y;
03262                                     break;
03263 
03264                                 case TextureMappingOptions.Line:
03265                                     characterInfos[i].vertex_BL.uv2.y = (characterInfos[i].vertex_BL.position.y - lineInfo.descender) / (lineInfo.ascender - lineInfo.descender); // + m_uvOffset.y;
03266                                     characterInfos[i].vertex_TL.uv2.y = (characterInfos[i].vertex_TL.position.y - lineInfo.descender) / (lineInfo.ascender - lineInfo.descender); // + m_uvOffset.y;
03267                                     characterInfos[i].vertex_TR.uv2.y = characterInfos[i].vertex_TL.uv2.y;
03268                                     characterInfos[i].vertex_BR.uv2.y = characterInfos[i].vertex_BL.uv2.y;
03269                                     break;
03270 
03271                                 case TextureMappingOptions.Paragraph:
03272                                     characterInfos[i].vertex_BL.uv2.y = (characterInfos[i].vertex_BL.position.y - m_meshExtents.min.y) / (m_meshExtents.max.y - m_meshExtents.min.y); // + m_uvOffset.y;
03273                                     characterInfos[i].vertex_TL.uv2.y = (characterInfos[i].vertex_TL.position.y - m_meshExtents.min.y) / (m_meshExtents.max.y - m_meshExtents.min.y); // + m_uvOffset.y;
03274                                     characterInfos[i].vertex_TR.uv2.y = characterInfos[i].vertex_TL.uv2.y;
03275                                     characterInfos[i].vertex_BR.uv2.y = characterInfos[i].vertex_BL.uv2.y;
03276                                     break;
03277 
03278                                 case TextureMappingOptions.MatchAspect:
03279                                     float yDelta = (1 - ((characterInfos[i].vertex_BL.uv2.x + characterInfos[i].vertex_TR.uv2.x) / characterInfos[i].aspectRatio)) / 2; // Center of Rectangle
03280 
03281                                     characterInfos[i].vertex_BL.uv2.y = yDelta + (characterInfos[i].vertex_BL.uv2.x / characterInfos[i].aspectRatio); // + m_uvOffset.y;
03282                                     characterInfos[i].vertex_TL.uv2.y = yDelta + (characterInfos[i].vertex_TR.uv2.x / characterInfos[i].aspectRatio); // + m_uvOffset.y;
03283                                     characterInfos[i].vertex_BR.uv2.y = characterInfos[i].vertex_BL.uv2.y;
03284                                     characterInfos[i].vertex_TR.uv2.y = characterInfos[i].vertex_TL.uv2.y;
03285                                     break;
03286                             }
03287                             #endregion
03288 
03289                             // Pack UV's so that we can pass Xscale needed for Shader to maintain 1:1 ratio.
03290                             #region Pack Scale into UV2
03291                             xScale = characterInfos[i].scale * lossyScale * (1 - m_charWidthAdjDelta);
03292                             if (!characterInfos[i].isUsingAlternateTypeface && (characterInfos[i].style & FontStyles.Bold) == FontStyles.Bold) xScale *= -1;
03293 
03294                             //int isBold = (m_textInfo.characterInfo[i].style & FontStyles.Bold) == FontStyles.Bold ? 1 : 0;
03295                             //Vector2 vertexData = new Vector2(isBold, xScale);
03296                             //characterInfos[i].vertex_BL.uv4 = vertexData;
03297                             //characterInfos[i].vertex_TL.uv4 = vertexData;
03298                             //characterInfos[i].vertex_TR.uv4 = vertexData;
03299                             //characterInfos[i].vertex_BR.uv4 = vertexData;
03300 
03301                             float x0 = characterInfos[i].vertex_BL.uv2.x;
03302                             float y0 = characterInfos[i].vertex_BL.uv2.y;
03303                             float x1 = characterInfos[i].vertex_TR.uv2.x;
03304                             float y1 = characterInfos[i].vertex_TR.uv2.y;
03305 
03306                             float dx = (int)x0;
03307                             float dy = (int)y0;
03308 
03309                             x0 = x0 - dx;
03310                             x1 = x1 - dx;
03311                             y0 = y0 - dy;
03312                             y1 = y1 - dy;
03313 
03314                             // Optimization to avoid having a vector2 returned from the Pack UV function.
03315                             characterInfos[i].vertex_BL.uv2.x = PackUV(x0, y0); characterInfos[i].vertex_BL.uv2.y = xScale;
03316                             characterInfos[i].vertex_TL.uv2.x = PackUV(x0, y1); characterInfos[i].vertex_TL.uv2.y = xScale;
03317                             characterInfos[i].vertex_TR.uv2.x = PackUV(x1, y1); characterInfos[i].vertex_TR.uv2.y = xScale;
03318                             characterInfos[i].vertex_BR.uv2.x = PackUV(x1, y0); characterInfos[i].vertex_BR.uv2.y = xScale;
03319                             #endregion
03320                             break;
03321                         
03322                         // SPRITES
03323                         case TMP_TextElementType.Sprite:
03324                             // Nothing right now
03325                             break;
03326                     }
03327 
03328                     // Handle maxVisibleCharacters, maxVisibleLines and Overflow Page Mode.
03329                     #region Handle maxVisibleCharacters / maxVisibleLines / Page Mode
03330                     if (i < m_maxVisibleCharacters && wordCount < m_maxVisibleWords && currentLine < m_maxVisibleLines && m_overflowMode != TextOverflowModes.Page)
03331                     {
03332                         characterInfos[i].vertex_BL.position += offset;
03333                         characterInfos[i].vertex_TL.position += offset;
03334                         characterInfos[i].vertex_TR.position += offset;
03335                         characterInfos[i].vertex_BR.position += offset;
03336                     }
03337                     else if (i < m_maxVisibleCharacters && wordCount < m_maxVisibleWords && currentLine < m_maxVisibleLines && m_overflowMode == TextOverflowModes.Page && characterInfos[i].pageNumber == pageToDisplay)
03338                     {
03339                         characterInfos[i].vertex_BL.position += offset;
03340                         characterInfos[i].vertex_TL.position += offset;
03341                         characterInfos[i].vertex_TR.position += offset;
03342                         characterInfos[i].vertex_BR.position += offset;
03343                     }
03344                     else
03345                     {
03346                         characterInfos[i].vertex_BL.position = Vector3.zero;
03347                         characterInfos[i].vertex_TL.position = Vector3.zero;
03348                         characterInfos[i].vertex_TR.position = Vector3.zero;
03349                         characterInfos[i].vertex_BR.position = Vector3.zero;
03350                         characterInfos[i].isVisible = false;
03351                     }
03352                     #endregion
03353 
03354 
03355                     // Fill Vertex Buffers for the various types of element
03356                     if (elementType == TMP_TextElementType.Character)
03357                     {
03358                         FillCharacterVertexBuffers(i, vert_index_X4, m_isVolumetricText);
03359                     }
03360                     else if (elementType == TMP_TextElementType.Sprite)
03361                     {
03362                         FillSpriteVertexBuffers(i, sprite_index_X4);
03363                     }
03364                 }
03365                 #endregion
03366 
03367                 // Apply Alignment and Justification Offset
03368                 m_textInfo.characterInfo[i].bottomLeft += offset;
03369                 m_textInfo.characterInfo[i].topLeft += offset;
03370                 m_textInfo.characterInfo[i].topRight += offset;
03371                 m_textInfo.characterInfo[i].bottomRight += offset;
03372 
03373                 m_textInfo.characterInfo[i].origin += offset.x;
03374                 m_textInfo.characterInfo[i].xAdvance += offset.x;
03375 
03376                 m_textInfo.characterInfo[i].ascender += offset.y;
03377                 m_textInfo.characterInfo[i].descender += offset.y;
03378                 m_textInfo.characterInfo[i].baseLine += offset.y;
03379 
03380                 // Update MeshExtents
03381                 if (isCharacterVisible)
03382                 {
03383                     //m_meshExtents.min = new Vector2(Mathf.Min(m_meshExtents.min.x, m_textInfo.characterInfo[i].bottomLeft.x), Mathf.Min(m_meshExtents.min.y, m_textInfo.characterInfo[i].bottomLeft.y));
03384                     //m_meshExtents.max = new Vector2(Mathf.Max(m_meshExtents.max.x, m_textInfo.characterInfo[i].topRight.x), Mathf.Max(m_meshExtents.max.y, m_textInfo.characterInfo[i].topLeft.y));
03385                 }
03386 
03387                 // Need to recompute lineExtent to account for the offset from justification.
03388                 #region Adjust lineExtents resulting from alignment offset
03389                 if (currentLine != lastLine || i == m_characterCount - 1)
03390                 {
03391                     // Update the previous line's extents
03392                     if (currentLine != lastLine)
03393                     {
03394                         m_textInfo.lineInfo[lastLine].baseline += offset.y;
03395                         m_textInfo.lineInfo[lastLine].ascender += offset.y;
03396                         m_textInfo.lineInfo[lastLine].descender += offset.y;
03397 
03398                         m_textInfo.lineInfo[lastLine].lineExtents.min = new Vector2(m_textInfo.characterInfo[m_textInfo.lineInfo[lastLine].firstCharacterIndex].bottomLeft.x, m_textInfo.lineInfo[lastLine].descender);
03399                         m_textInfo.lineInfo[lastLine].lineExtents.max = new Vector2(m_textInfo.characterInfo[m_textInfo.lineInfo[lastLine].lastVisibleCharacterIndex].topRight.x, m_textInfo.lineInfo[lastLine].ascender);
03400                     }
03401 
03402                     // Update the current line's extents
03403                     if (i == m_characterCount - 1)
03404                     {
03405                         m_textInfo.lineInfo[currentLine].baseline += offset.y;
03406                         m_textInfo.lineInfo[currentLine].ascender += offset.y;
03407                         m_textInfo.lineInfo[currentLine].descender += offset.y;
03408 
03409                         m_textInfo.lineInfo[currentLine].lineExtents.min = new Vector2(m_textInfo.characterInfo[m_textInfo.lineInfo[currentLine].firstCharacterIndex].bottomLeft.x, m_textInfo.lineInfo[currentLine].descender);
03410                         m_textInfo.lineInfo[currentLine].lineExtents.max = new Vector2(m_textInfo.characterInfo[m_textInfo.lineInfo[currentLine].lastVisibleCharacterIndex].topRight.x, m_textInfo.lineInfo[currentLine].ascender);
03411                     }
03412                 }
03413                 #endregion
03414 
03415 
03416                 // Track Word Count per line and for the object
03417                 #region Track Word Count
03418                 if (char.IsLetterOrDigit(currentCharacter) || currentCharacter == 0x2D || currentCharacter == 0xAD || currentCharacter == 0x2010 || currentCharacter == 0x2011)
03419                 {
03420                     if (isStartOfWord == false)
03421                     {
03422                         isStartOfWord = true;
03423                         wordFirstChar = i;
03424                     }
03425 
03426                     // If last character is a word
03427                     if (isStartOfWord && i == m_characterCount - 1)
03428                     {
03429                         int size = m_textInfo.wordInfo.Length;
03430                         int index = m_textInfo.wordCount;
03431 
03432                         if (m_textInfo.wordCount + 1 > size)
03433                             TMP_TextInfo.Resize(ref m_textInfo.wordInfo, size + 1);
03434 
03435                         wordLastChar = i;
03436 
03437                         m_textInfo.wordInfo[index].firstCharacterIndex = wordFirstChar;
03438                         m_textInfo.wordInfo[index].lastCharacterIndex = wordLastChar;
03439                         m_textInfo.wordInfo[index].characterCount = wordLastChar - wordFirstChar + 1;
03440                         m_textInfo.wordInfo[index].textComponent = this;
03441 
03442                         wordCount += 1;
03443                         m_textInfo.wordCount += 1;
03444                         m_textInfo.lineInfo[currentLine].wordCount += 1;
03445                     }
03446                 }
03447                 else if (isStartOfWord || i == 0 && (!char.IsPunctuation(currentCharacter) || char.IsWhiteSpace(currentCharacter) || currentCharacter == 0x200B || i == m_characterCount - 1))
03448                 {
03449                     if (i > 0 && i < characterInfos.Length - 1 && i < m_characterCount && (currentCharacter == 39 || currentCharacter == 8217) && char.IsLetterOrDigit(characterInfos[i - 1].character) && char.IsLetterOrDigit(characterInfos[i + 1].character))
03450                     {
03451 
03452                     }
03453                     else
03454                     {
03455                         wordLastChar = i == m_characterCount - 1 && char.IsLetterOrDigit(currentCharacter) ? i : i - 1;
03456                         isStartOfWord = false;
03457 
03458                         int size = m_textInfo.wordInfo.Length;
03459                         int index = m_textInfo.wordCount;
03460 
03461                         if (m_textInfo.wordCount + 1 > size)
03462                             TMP_TextInfo.Resize(ref m_textInfo.wordInfo, size + 1);
03463 
03464                         m_textInfo.wordInfo[index].firstCharacterIndex = wordFirstChar;
03465                         m_textInfo.wordInfo[index].lastCharacterIndex = wordLastChar;
03466                         m_textInfo.wordInfo[index].characterCount = wordLastChar - wordFirstChar + 1;
03467                         m_textInfo.wordInfo[index].textComponent = this;
03468 
03469                         wordCount += 1;
03470                         m_textInfo.wordCount += 1;
03471                         m_textInfo.lineInfo[currentLine].wordCount += 1;
03472                     }
03473                 }
03474                 #endregion
03475 
03476 
03477                 // Setup & Handle Underline
03478                 #region Underline
03479                 // NOTE: Need to figure out how underline will be handled with multiple fonts and which font will be used for the underline.
03480                 bool isUnderline = (m_textInfo.characterInfo[i].style & FontStyles.Underline) == FontStyles.Underline;
03481                 if (isUnderline)
03482                 {
03483                     bool isUnderlineVisible = true;
03484                     int currentPage = m_textInfo.characterInfo[i].pageNumber;
03485 
03486                     if (i > m_maxVisibleCharacters || currentLine > m_maxVisibleLines || (m_overflowMode == TextOverflowModes.Page && currentPage + 1 != m_pageToDisplay))
03487                         isUnderlineVisible = false;
03488 
03489                     // We only use the scale of visible characters.
03490                     if (!char.IsWhiteSpace(currentCharacter) && currentCharacter != 0x200B)
03491                     {
03492                         underlineMaxScale = Mathf.Max(underlineMaxScale, m_textInfo.characterInfo[i].scale);
03493                         underlineBaseLine = Mathf.Min(currentPage == lastPage ? underlineBaseLine : k_LargePositiveFloat, m_textInfo.characterInfo[i].baseLine + font.fontInfo.Underline * underlineMaxScale);
03494                         lastPage = currentPage; // Need to track pages to ensure we reset baseline for the new pages.
03495                     }
03496 
03497                     if (beginUnderline == false && isUnderlineVisible == true && i <= lineInfo.lastVisibleCharacterIndex && currentCharacter != 10 && currentCharacter != 13)
03498                     {
03499                         if (i == lineInfo.lastVisibleCharacterIndex && char.IsSeparator(currentCharacter))
03500                         { }
03501                         else
03502                         {
03503                             beginUnderline = true;
03504                             underlineStartScale = m_textInfo.characterInfo[i].scale;
03505                             if (underlineMaxScale == 0) underlineMaxScale = underlineStartScale;
03506                             underline_start = new Vector3(m_textInfo.characterInfo[i].bottomLeft.x, underlineBaseLine, 0);
03507                             underlineColor = m_textInfo.characterInfo[i].underlineColor;
03508                         }
03509                     }
03510 
03511                     // End Underline if text only contains one character.
03512                     if (beginUnderline && m_characterCount == 1)
03513                     {
03514                         beginUnderline = false;
03515                         underline_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, underlineBaseLine, 0);
03516                         underlineEndScale = m_textInfo.characterInfo[i].scale;
03517 
03518                         DrawUnderlineMesh(underline_start, underline_end, ref last_vert_index, underlineStartScale, underlineEndScale, underlineMaxScale, xScale, underlineColor);
03519                         underlineMaxScale = 0;
03520                         underlineBaseLine = k_LargePositiveFloat;
03521                     }
03522                     else if (beginUnderline && (i == lineInfo.lastCharacterIndex || i >= lineInfo.lastVisibleCharacterIndex))
03523                     {
03524                         // Terminate underline at previous visible character if space or carriage return.
03525                         if (char.IsWhiteSpace(currentCharacter) || currentCharacter == 0x200B)
03526                         {
03527                             int lastVisibleCharacterIndex = lineInfo.lastVisibleCharacterIndex;
03528                             underline_end = new Vector3(m_textInfo.characterInfo[lastVisibleCharacterIndex].topRight.x, underlineBaseLine, 0);
03529                             underlineEndScale = m_textInfo.characterInfo[lastVisibleCharacterIndex].scale;
03530                         }
03531                         else
03532                         {   // End underline if last character of the line.
03533                             underline_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, underlineBaseLine, 0);
03534                             underlineEndScale = m_textInfo.characterInfo[i].scale;
03535                         }
03536 
03537                         beginUnderline = false;
03538                         DrawUnderlineMesh(underline_start, underline_end, ref last_vert_index, underlineStartScale, underlineEndScale, underlineMaxScale, xScale, underlineColor);
03539                         underlineMaxScale = 0;
03540                         underlineBaseLine = k_LargePositiveFloat;
03541                     }
03542                     else if (beginUnderline && !isUnderlineVisible)
03543                     {
03544                         beginUnderline = false;
03545                         underline_end = new Vector3(m_textInfo.characterInfo[i - 1].topRight.x, underlineBaseLine, 0);
03546                         underlineEndScale = m_textInfo.characterInfo[i - 1].scale;
03547 
03548                         DrawUnderlineMesh(underline_start, underline_end, ref last_vert_index, underlineStartScale, underlineEndScale, underlineMaxScale, xScale, underlineColor);
03549                         underlineMaxScale = 0;
03550                         underlineBaseLine = k_LargePositiveFloat;
03551                     }
03552                     else if (beginUnderline && i < m_characterCount - 1 && !underlineColor.Compare(m_textInfo.characterInfo[i + 1].underlineColor))
03553                     {
03554                         // End underline if underline color has changed.
03555                         beginUnderline = false;
03556                         underline_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, underlineBaseLine, 0);
03557                         underlineEndScale = m_textInfo.characterInfo[i].scale;
03558 
03559                         DrawUnderlineMesh(underline_start, underline_end, ref last_vert_index, underlineStartScale, underlineEndScale, underlineMaxScale, xScale, underlineColor);
03560                         underlineMaxScale = 0;
03561                         underlineBaseLine = k_LargePositiveFloat;
03562                     }
03563                 }
03564                 else
03565                 {
03566                     // End Underline
03567                     if (beginUnderline == true)
03568                     {
03569                         beginUnderline = false;
03570                         underline_end = new Vector3(m_textInfo.characterInfo[i - 1].topRight.x, underlineBaseLine, 0);
03571                         underlineEndScale = m_textInfo.characterInfo[i - 1].scale;
03572 
03573                         DrawUnderlineMesh(underline_start, underline_end, ref last_vert_index, underlineStartScale, underlineEndScale, underlineMaxScale, xScale, underlineColor);
03574                         underlineMaxScale = 0;
03575                         underlineBaseLine = k_LargePositiveFloat;
03576                     }
03577                 }
03578                 #endregion
03579 
03580 
03581                 // Setup & Handle Strikethrough
03582                 #region Strikethrough
03583                 // NOTE: Need to figure out how underline will be handled with multiple fonts and which font will be used for the underline.
03584                 bool isStrikethrough = (m_textInfo.characterInfo[i].style & FontStyles.Strikethrough) == FontStyles.Strikethrough;
03585                 float strikethroughOffset = currentFontAsset.fontInfo.strikethrough;
03586 
03587                 if (isStrikethrough)
03588                 {
03589                     bool isStrikeThroughVisible = true;
03590 
03591                     if (i > m_maxVisibleCharacters || currentLine > m_maxVisibleLines || (m_overflowMode == TextOverflowModes.Page && m_textInfo.characterInfo[i].pageNumber + 1 != m_pageToDisplay))
03592                         isStrikeThroughVisible = false;
03593 
03594                     if (beginStrikethrough == false && isStrikeThroughVisible && i <= lineInfo.lastVisibleCharacterIndex && currentCharacter != 10 && currentCharacter != 13)
03595                     {
03596                         if (i == lineInfo.lastVisibleCharacterIndex && char.IsSeparator(currentCharacter))
03597                         { }
03598                         else
03599                         {
03600                             beginStrikethrough = true;
03601                             strikethroughPointSize = m_textInfo.characterInfo[i].pointSize;
03602                             strikethroughScale = m_textInfo.characterInfo[i].scale;
03603                             strikethrough_start = new Vector3(m_textInfo.characterInfo[i].bottomLeft.x, m_textInfo.characterInfo[i].baseLine + strikethroughOffset * strikethroughScale, 0);
03604                             strikethroughColor = m_textInfo.characterInfo[i].strikethroughColor;
03605                             strikethroughBaseline = m_textInfo.characterInfo[i].baseLine;
03606                             //Debug.Log("Char [" + currentCharacter + "] Start Strikethrough POS: " + strikethrough_start);
03607                         }
03608                     }
03609 
03610                     // End Strikethrough if text only contains one character.
03611                     if (beginStrikethrough && m_characterCount == 1)
03612                     {
03613                         beginStrikethrough = false;
03614                         strikethrough_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, m_textInfo.characterInfo[i].baseLine + strikethroughOffset * strikethroughScale, 0);
03615 
03616                         DrawUnderlineMesh(strikethrough_start, strikethrough_end, ref last_vert_index, strikethroughScale, strikethroughScale, strikethroughScale, xScale, strikethroughColor);
03617                     }
03618                     else if (beginStrikethrough && i == lineInfo.lastCharacterIndex)
03619                     {
03620                         // Terminate Strikethrough at previous visible character if space or carriage return.
03621                         if (char.IsWhiteSpace(currentCharacter) || currentCharacter == 0x200B)
03622                         {
03623                             int lastVisibleCharacterIndex = lineInfo.lastVisibleCharacterIndex;
03624                             strikethrough_end = new Vector3(m_textInfo.characterInfo[lastVisibleCharacterIndex].topRight.x, m_textInfo.characterInfo[lastVisibleCharacterIndex].baseLine + strikethroughOffset * strikethroughScale, 0);
03625                         }
03626                         else
03627                         {
03628                             // Terminate Strikethrough at last character of line.
03629                             strikethrough_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, m_textInfo.characterInfo[i].baseLine + strikethroughOffset * strikethroughScale, 0);
03630                         }
03631 
03632                         beginStrikethrough = false;
03633                         DrawUnderlineMesh(strikethrough_start, strikethrough_end, ref last_vert_index, strikethroughScale, strikethroughScale, strikethroughScale, xScale, strikethroughColor);
03634                     }
03635                     else if (beginStrikethrough && i < m_characterCount && (m_textInfo.characterInfo[i + 1].pointSize != strikethroughPointSize || !TMP_Math.Approximately(m_textInfo.characterInfo[i + 1].baseLine + offset.y, strikethroughBaseline)))
03636                     {
03637                         // Terminate Strikethrough if scale changes.
03638                         beginStrikethrough = false;
03639 
03640                         int lastVisibleCharacterIndex = lineInfo.lastVisibleCharacterIndex;
03641                         if (i > lastVisibleCharacterIndex)
03642                             strikethrough_end = new Vector3(m_textInfo.characterInfo[lastVisibleCharacterIndex].topRight.x, m_textInfo.characterInfo[lastVisibleCharacterIndex].baseLine + strikethroughOffset * strikethroughScale, 0);
03643                         else
03644                             strikethrough_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, m_textInfo.characterInfo[i].baseLine + strikethroughOffset * strikethroughScale, 0);
03645 
03646                         DrawUnderlineMesh(strikethrough_start, strikethrough_end, ref last_vert_index, strikethroughScale, strikethroughScale, strikethroughScale, xScale, strikethroughColor);
03647                         //Debug.Log("Char [" + currentCharacter + "] at Index: " + i + "  End Strikethrough POS: " + strikethrough_end + "  Baseline: " + m_textInfo.characterInfo[i].baseLine.ToString("f3"));
03648                     }
03649                     else if (beginStrikethrough && i < m_characterCount && currentFontAsset.GetInstanceID() != characterInfos[i + 1].fontAsset.GetInstanceID())
03650                     {
03651                         // Terminate Strikethrough if font asset changes.
03652                         beginStrikethrough = false;
03653                         strikethrough_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, m_textInfo.characterInfo[i].baseLine + strikethroughOffset * strikethroughScale, 0);
03654 
03655                         DrawUnderlineMesh(strikethrough_start, strikethrough_end, ref last_vert_index, strikethroughScale, strikethroughScale, strikethroughScale, xScale, strikethroughColor);
03656                     }
03657                     else if (beginStrikethrough && !isStrikeThroughVisible)
03658                     {
03659                         // Terminate Strikethrough if character is not visible.
03660                         beginStrikethrough = false;
03661                         strikethrough_end = new Vector3(m_textInfo.characterInfo[i - 1].topRight.x, m_textInfo.characterInfo[i - 1].baseLine + strikethroughOffset * strikethroughScale, 0);
03662 
03663                         DrawUnderlineMesh(strikethrough_start, strikethrough_end, ref last_vert_index, strikethroughScale, strikethroughScale, strikethroughScale, xScale, strikethroughColor);
03664                     }
03665                 }
03666                 else
03667                 {
03668                     // End Strikethrough
03669                     if (beginStrikethrough == true)
03670                     {
03671                         beginStrikethrough = false;
03672                         strikethrough_end = new Vector3(m_textInfo.characterInfo[i - 1].topRight.x, m_textInfo.characterInfo[i - 1].baseLine + strikethroughOffset * strikethroughScale, 0);
03673 
03674                         DrawUnderlineMesh(strikethrough_start, strikethrough_end, ref last_vert_index, strikethroughScale, strikethroughScale, strikethroughScale, xScale, strikethroughColor);
03675                     }
03676                 }
03677                 #endregion
03678 
03679 
03680                 // HANDLE TEXT HIGHLIGHTING
03681                 #region Text Highlighting
03682                 bool isHighlight = (m_textInfo.characterInfo[i].style & FontStyles.Highlight) == FontStyles.Highlight;
03683                 if (isHighlight)
03684                 {
03685                     bool isHighlightVisible = true;
03686                     int currentPage = m_textInfo.characterInfo[i].pageNumber;
03687 
03688                     if (i > m_maxVisibleCharacters || currentLine > m_maxVisibleLines || (m_overflowMode == TextOverflowModes.Page && currentPage + 1 != m_pageToDisplay))
03689                         isHighlightVisible = false;
03690 
03691                     if (beginHighlight == false && isHighlightVisible == true && i <= lineInfo.lastVisibleCharacterIndex && currentCharacter != 10 && currentCharacter != 13)
03692                     {
03693                         if (i == lineInfo.lastVisibleCharacterIndex && char.IsSeparator(currentCharacter))
03694                         { }
03695                         else
03696                         {
03697                             beginHighlight = true;
03698                             highlight_start = k_LargePositiveVector2;
03699                             highlight_end = k_LargeNegativeVector2;
03700                             highlightColor = m_textInfo.characterInfo[i].highlightColor;
03701                         }
03702                     }
03703 
03704                     if (beginHighlight)
03705                     {
03706                         Color32 currentHighlightColor = m_textInfo.characterInfo[i].highlightColor;
03707                         bool isColorTransition = false;
03708 
03709                         // Handle Highlight color changes
03710                         if (!highlightColor.Compare(currentHighlightColor))
03711                         {
03712                             // End drawing at the start of new highlight color to prevent a gap between highlight sections.
03713                             highlight_end.x = (highlight_end.x + m_textInfo.characterInfo[i].bottomLeft.x) / 2;
03714 
03715                             highlight_start.y = Mathf.Min(highlight_start.y, m_textInfo.characterInfo[i].descender);
03716                             highlight_end.y = Mathf.Max(highlight_end.y, m_textInfo.characterInfo[i].ascender);
03717 
03718                             DrawTextHighlight(highlight_start, highlight_end, ref last_vert_index, highlightColor);
03719 
03720                             beginHighlight = true;
03721                             highlight_start = highlight_end;
03722 
03723                             highlight_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, m_textInfo.characterInfo[i].descender, 0);
03724                             highlightColor = m_textInfo.characterInfo[i].highlightColor;
03725 
03726                             isColorTransition = true;
03727                         }
03728 
03729                         if (!isColorTransition)
03730                         {
03731                             // Use the Min / Max Extents of the Highlight area to handle different character sizes and fonts.
03732                             highlight_start.x = Mathf.Min(highlight_start.x, m_textInfo.characterInfo[i].bottomLeft.x); // - (1 * m_textInfo.characterInfo[i].scale));
03733                             highlight_start.y = Mathf.Min(highlight_start.y, m_textInfo.characterInfo[i].descender); // - (1 * m_textInfo.characterInfo[i].scale));
03734 
03735                             highlight_end.x = Mathf.Max(highlight_end.x, m_textInfo.characterInfo[i].topRight.x); // + (1 * m_textInfo.characterInfo[i].scale));
03736                             highlight_end.y = Mathf.Max(highlight_end.y, m_textInfo.characterInfo[i].ascender); // + (1 * m_textInfo.characterInfo[i].scale)); 
03737                         }
03738                     }
03739 
03740                     // End Highlight if text only contains one character.
03741                     if (beginHighlight && m_characterCount == 1)
03742                     {
03743                         beginHighlight = false;
03744 
03745                         DrawTextHighlight(highlight_start, highlight_end, ref last_vert_index, highlightColor);
03746                     }
03747                     else if (beginHighlight && (i == lineInfo.lastCharacterIndex || i >= lineInfo.lastVisibleCharacterIndex))
03748                     {
03749                         beginHighlight = false;
03750                         DrawTextHighlight(highlight_start, highlight_end, ref last_vert_index, highlightColor);
03751                     }
03752                     else if (beginHighlight && !isHighlightVisible)
03753                     {
03754                         beginHighlight = false;
03755                         DrawTextHighlight(highlight_start, highlight_end, ref last_vert_index, highlightColor);
03756                     }
03757                 }
03758                 else
03759                 {
03760                     // End Highlight
03761                     if (beginHighlight == true)
03762                     {
03763                         beginHighlight = false;
03764                         DrawTextHighlight(highlight_start, highlight_end, ref last_vert_index, highlightColor);
03765                     }
03766                 }
03767                 #endregion
03768 
03769 
03770                 lastLine = currentLine;
03771             }
03772             #endregion
03773 
03774 
03775             // METRICS ABOUT THE TEXT OBJECT
03776             m_textInfo.characterCount = m_characterCount;
03777             m_textInfo.spriteCount = m_spriteCount;
03778             m_textInfo.lineCount = lineCount;
03779             m_textInfo.wordCount = wordCount != 0 && m_characterCount > 0 ? wordCount : 1;
03780             m_textInfo.pageCount = m_pageNumber + 1;
03781 
03782 
03784             // Update Mesh Vertex Data
03785             if (m_renderMode == TextRenderFlags.Render && IsActive())
03786             {
03787                 // Clear unused vertices
03788                 //m_textInfo.meshInfo[0].ClearUnusedVertices();
03789 
03790                 // Sort the geometry of the text object if needed.
03791                 if (m_geometrySortingOrder != VertexSortingOrder.Normal)
03792                     m_textInfo.meshInfo[0].SortGeometry(VertexSortingOrder.Reverse);
03793 
03794                 // Upload Mesh Data
03795                 m_mesh.MarkDynamic();
03796                 m_mesh.vertices = m_textInfo.meshInfo[0].vertices;
03797                 m_mesh.uv = m_textInfo.meshInfo[0].uvs0;
03798                 m_mesh.uv2 = m_textInfo.meshInfo[0].uvs2;
03799                 //m_mesh.uv4 = m_textInfo.meshInfo[0].uvs4;
03800                 m_mesh.colors32 = m_textInfo.meshInfo[0].colors32;
03801 
03802                 // Compute Bounds for the mesh. Manual computation is more efficient then using Mesh.recalcualteBounds.
03803                 m_mesh.RecalculateBounds();
03804                 //m_mesh.bounds = new Bounds(new Vector3((m_meshExtents.max.x + m_meshExtents.min.x) / 2, (m_meshExtents.max.y + m_meshExtents.min.y) / 2, 0) + offset, new Vector3(m_meshExtents.max.x - m_meshExtents.min.x, m_meshExtents.max.y - m_meshExtents.min.y, 0));
03805 
03806                 for (int i = 1; i < m_textInfo.materialCount; i++)
03807                 {
03808                     // Clear unused vertices
03809                     m_textInfo.meshInfo[i].ClearUnusedVertices();
03810 
03811                     if (m_subTextObjects[i] == null) continue;
03812 
03813                     // Sort the geometry of the sub-text objects if needed.
03814                     if (m_geometrySortingOrder != VertexSortingOrder.Normal)
03815                         m_textInfo.meshInfo[i].SortGeometry(VertexSortingOrder.Reverse);
03816 
03817                     m_subTextObjects[i].mesh.vertices = m_textInfo.meshInfo[i].vertices;
03818                     m_subTextObjects[i].mesh.uv = m_textInfo.meshInfo[i].uvs0;
03819                     m_subTextObjects[i].mesh.uv2 = m_textInfo.meshInfo[i].uvs2;
03820                     //m_subTextObjects[i].mesh.uv4 = m_textInfo.meshInfo[i].uvs4;
03821                     m_subTextObjects[i].mesh.colors32 = m_textInfo.meshInfo[i].colors32;
03822 
03823                     m_subTextObjects[i].mesh.RecalculateBounds();
03824 
03825                     // Update the collider on the sub text object
03826                     //m_subTextObjects[i].UpdateColliders(m_textInfo.meshInfo[i].vertexCount);
03827                 }
03828             }
03829 
03830             // Event indicating the text has been regenerated.
03831             TMPro_EventManager.ON_TEXT_CHANGED(this);
03832 
03834             //Debug.Log("Done Rendering Text.");
03835         }
03836 
