03842         protected override Vector3[] GetTextContainerLocalCorners()
03843         {
03844             if (m_rectTransform == null) m_rectTransform = this.rectTransform;
03845 
03846             m_rectTransform.GetLocalCorners(m_RectTransformCorners);
03847 
03848             return m_RectTransformCorners;
03849         }
