03855         void SetMeshFilters(bool state)
03856         {
03857             // Parent text object
03858             if (m_meshFilter != null)
03859             {
03860                 if (state)
03861                     m_meshFilter.sharedMesh = m_mesh;
03862                 else
03863                     m_meshFilter.sharedMesh = null;
03864             }
03865 
03866             for (int i = 1; i < m_subTextObjects.Length && m_subTextObjects[i] != null; i++)
03867             {
03868                 if (m_subTextObjects[i].meshFilter != null)
03869                 {
03870                     if (state)
03871                         m_subTextObjects[i].meshFilter.sharedMesh = m_subTextObjects[i].mesh;
03872                     else
03873                         m_subTextObjects[i].meshFilter.sharedMesh = null;
03874                 }
03875             }
03876         }
03877 
03878 
03883         protected override void SetActiveSubMeshes(bool state)
03884         {
03885             for (int i = 1; i < m_subTextObjects.Length && m_subTextObjects[i] != null; i++)
03886             {
03887                 if (m_subTextObjects[i].enabled != state)
03888                     m_subTextObjects[i].enabled = state;
03889             }
03890         }
03891 
03892 
03896         protected override void ClearSubMeshObjects()
03897         {
03898             for (int i = 1; i < m_subTextObjects.Length && m_subTextObjects[i] != null; i++)
03899             {
03900                 Debug.Log("Destroying Sub Text object[" + i + "].");
03901                 DestroyImmediate(m_subTextObjects[i]);
03902             }
03903         }
03904 
03905 
03910         protected override Bounds GetCompoundBounds()
03911         {
03912             Bounds mainBounds = m_mesh.bounds;
03913             Vector3 min = mainBounds.min;
03914             Vector3 max = mainBounds.max;
03915 
03916             for (int i = 1; i < m_subTextObjects.Length && m_subTextObjects[i] != null; i++)
03917             {
03918                 Bounds subBounds = m_subTextObjects[i].mesh.bounds;
03919                 min.x = min.x < subBounds.min.x ? min.x : subBounds.min.x;
03920                 min.y = min.y < subBounds.min.y ? min.y : subBounds.min.y;
03921 
03922                 max.x = max.x > subBounds.max.x ? max.x : subBounds.max.x;
03923                 max.y = max.y > subBounds.max.y ? max.y : subBounds.max.y;
03924             }
03925 
03926             Vector3 center = (min + max) / 2;
03927             Vector2 size = max - min;
03928             return new Bounds(center, size);
03929         }
03930 
03931 
03935         void UpdateSDFScale(float lossyScale)
03936         {
03937             //Debug.Log("*** UpdateSDFScale() ***");
03938 
03939             // Iterate through each of the characters.
03940             for (int i = 0; i < m_textInfo.characterCount; i++)
03941             {
03942                 // Only update scale for visible characters.
03943                 if (m_textInfo.characterInfo[i].isVisible && m_textInfo.characterInfo[i].elementType == TMP_TextElementType.Character)
03944                 {
03945                     float scale = lossyScale * m_textInfo.characterInfo[i].scale * (1 - m_charWidthAdjDelta);
03946                     if (!m_textInfo.characterInfo[i].isUsingAlternateTypeface && (m_textInfo.characterInfo[i].style & FontStyles.Bold) == FontStyles.Bold) scale *= -1;
03947 
03948                     int index = m_textInfo.characterInfo[i].materialReferenceIndex;
03949                     int vertexIndex = m_textInfo.characterInfo[i].vertexIndex;
03950 
03951                     m_textInfo.meshInfo[index].uvs2[vertexIndex + 0].y = scale;
03952                     m_textInfo.meshInfo[index].uvs2[vertexIndex + 1].y = scale;
03953                     m_textInfo.meshInfo[index].uvs2[vertexIndex + 2].y = scale;
03954                     m_textInfo.meshInfo[index].uvs2[vertexIndex + 3].y = scale;
03955                 }
03956             }
03957 
03958             // Push the updated uv2 scale information to the meshes.
03959             for (int i = 0; i < m_textInfo.meshInfo.Length; i++)
03960             {
03961                 if (i == 0)
03962                     m_mesh.uv2 = m_textInfo.meshInfo[0].uvs2;
03963                 else
03964                     m_subTextObjects[i].mesh.uv2 = m_textInfo.meshInfo[i].uvs2;
03965             }
03966         }
03967 
03968 
03969         // Function to offset vertices position to account for line spacing changes.
03970         protected override void AdjustLineOffset(int startIndex, int endIndex, float offset)
03971         {
03972             Vector3 vertexOffset = new Vector3(0, offset, 0);
03973 
03974             for (int i = startIndex; i <= endIndex; i++)
03975             {
03976                 m_textInfo.characterInfo[i].bottomLeft -= vertexOffset;
03977                 m_textInfo.characterInfo[i].topLeft -= vertexOffset;
03978                 m_textInfo.characterInfo[i].topRight -= vertexOffset;
03979                 m_textInfo.characterInfo[i].bottomRight -= vertexOffset;
03980 
03981                 m_textInfo.characterInfo[i].ascender -= vertexOffset.y;
03982                 m_textInfo.characterInfo[i].baseLine -= vertexOffset.y;
03983                 m_textInfo.characterInfo[i].descender -= vertexOffset.y;
03984 
03985                 if (m_textInfo.characterInfo[i].isVisible)
03986                 {
03987                     m_textInfo.characterInfo[i].vertex_BL.position -= vertexOffset;
03988                     m_textInfo.characterInfo[i].vertex_TL.position -= vertexOffset;
03989                     m_textInfo.characterInfo[i].vertex_TR.position -= vertexOffset;
03990                     m_textInfo.characterInfo[i].vertex_BR.position -= vertexOffset;
03991                 }
03992             }
03993         }
