00066         public override void OnInspectorGUI()
00067         {
00068             serializedObject.Update();
00069 
00070             EditorGUI.BeginChangeCheck();
00071             EditorGUILayout.PropertyField(anchorPosition_prop);
00072             if (anchorPosition_prop.enumValueIndex == 9)
