00195         Vector2 GetAnchorPosition(int index)
00196         {
00197             Vector2 anchorPosition = Vector2.zero;
00198                    
00199             switch (index)
00200             {
00201                 case 0: // TOP LEFT
00202                     anchorPosition = new Vector2(0, 1);
00203                     break;
00204                 case 1: // TOP
00205                     anchorPosition = new Vector2(0.5f, 1);
00206                     break;
00207                 case 2: // TOP RIGHT
00208                     anchorPosition = new Vector2(1, 1);
00209                     break;
00210                 case 3: // LEFT
00211                     anchorPosition = new Vector2(0, 0.5f);
00212                     break;
00213                 case 4: // MIDDLE
00214                     anchorPosition = new Vector2(0.5f, 0.5f);
00215                     break;
00216                 case 5: // RIGHT
00217                     anchorPosition = new Vector2(1, 0.5f);
00218                     break;
00219                 case 6: // BOTTOM LEFT
00220                     anchorPosition = new Vector2(0, 0);
00221                     break;
00222                 case 7: // BOTTOM
00223                     anchorPosition = new Vector2(0.5f, 0);
00224                     break;
00225                 case 8: // BOTTOM RIGHT
00226                     anchorPosition = new Vector2(1, 0);
00227                     break;
00228             }
00229           
00230             return anchorPosition;
00231         }
