00071         protected override void Awake()
00072         {
00073             //Debug.Log("***** Awake() *****");
00074 
00075             #if UNITY_EDITOR
00076             // Special handling for TMP Settings and importing Essential Resources
00077             if (TMP_Settings.instance == null)
00078             {
00079                 if (m_isWaitingOnResourceLoad == false)
00080                     TMPro_EventManager.RESOURCE_LOAD_EVENT.Add(ON_RESOURCES_LOADED);
00081 
00082                 m_isWaitingOnResourceLoad = true;
00083                 return;
00084             }
00085             #endif
00086 
00087             // Cache Reference to the Canvas
00088             m_canvas = this.canvas;
00089 
00090             m_isOrthographic = true;
00091 
00092             // Cache Reference to RectTransform.
00093             m_rectTransform = gameObject.GetComponent<RectTransform>();
00094             if (m_rectTransform == null)  
00095                 m_rectTransform = gameObject.AddComponent<RectTransform>();
00096 
00097             // Cache a reference to the CanvasRenderer.
00098             m_canvasRenderer = GetComponent<CanvasRenderer>();
00099             if (m_canvasRenderer == null) 
00100                 m_canvasRenderer = gameObject.AddComponent<CanvasRenderer> ();
00101 
00102             if (m_mesh == null)
00103             {
00104                 //Debug.Log("Creating new mesh.");
00105                 m_mesh = new Mesh();
00106                 m_mesh.hideFlags = HideFlags.HideAndDontSave;
00107 
00108                 //m_mesh.bounds = new Bounds(transform.position, new Vector3(1000, 1000, 0));
00109             }
00110 
00111             // Load TMP Settings for new text object instances.
00112             LoadDefaultSettings();
00113 
00114             // Load the font asset and assign material to renderer.
00115             LoadFontAsset();
00116 
00117             // Load Default TMP StyleSheet
00118             TMP_StyleSheet.LoadDefaultStyleSheet();
00119 
00120             // Allocate our initial buffers.
00121             if (m_char_buffer == null)
00122                 m_char_buffer = new int[m_max_characters];
00123 
00124             m_cached_TextElement = new TMP_Glyph();
00125             m_isFirstAllocation = true;
00126 
00127             if (m_textInfo == null)
00128                 m_textInfo = new TMP_TextInfo(this);
00129 
00130             // Check if we have a font asset assigned. Return if we don't because no one likes to see purple squares on screen.
00131             if (m_fontAsset == null)
00132             {
00133                 Debug.LogWarning("Please assign a Font Asset to this " + transform.name + " gameobject.", this);
00134                 return;
00135             }
00136 
00137             // Check to make sure Sub Text Objects are tracked correctly in the event a Prefab is used.
00138             TMP_SubMeshUI[] subTextObjects = GetComponentsInChildren<TMP_SubMeshUI>();
00139             if (subTextObjects.Length > 0)
00140             {
00141                 for (int i = 0; i < subTextObjects.Length; i++)
00142                     m_subTextObjects[i + 1] = subTextObjects[i];
00143             }
00144 
00145             // Set flags to ensure our text is parsed and redrawn.
00146             m_isInputParsingRequired = true;
00147             m_havePropertiesChanged = true;
00148             m_isCalculateSizeRequired = true;
00149 
00150             m_isAwake = true;
00151         }
