00156             //Debug.Log("*** OnEnable() ***", this);
00157 
00158             // Return if Awake() has not been called on the text object.
00159             if (m_isAwake == false)
00160                 return;
00161 
00162             if (!m_isRegisteredForEvents)
00163             {
00164                 //Debug.Log("Registering for Events.");
00165                 
00166                 #if UNITY_EDITOR
00167                 // Register Callbacks for various events.
00168                 TMPro_EventManager.MATERIAL_PROPERTY_EVENT.Add(ON_MATERIAL_PROPERTY_CHANGED);
00169                 TMPro_EventManager.FONT_PROPERTY_EVENT.Add(ON_FONT_PROPERTY_CHANGED);
