00178 
00179             // Cache Reference to the Canvas
00180             m_canvas = GetCanvas();
00181 
00182             SetActiveSubMeshes(true);
00183 
00184             // Register Graphic Component to receive event triggers
00185             GraphicRegistry.RegisterGraphicForCanvas(m_canvas, this);
