00243                 TMP_MaterialManager.ReleaseStencilMaterial(m_MaskMaterial);
00244                 m_MaskMaterial = null;
00245             }
00246 
00247             #if UNITY_EDITOR
00248             // Unregister the event this object was listening to
00249             TMPro_EventManager.MATERIAL_PROPERTY_EVENT.Remove(ON_MATERIAL_PROPERTY_CHANGED);
