00279         protected override void OnValidate()
00280         {
00281             //Debug.Log("***** OnValidate() ***** Frame:" + Time.frameCount); // ID " + GetInstanceID()); // New Material [" + m_sharedMaterial.name + "] with ID " + m_sharedMaterial.GetInstanceID() + ". Base Material is [" + m_baseMaterial.name + "] with ID " + m_baseMaterial.GetInstanceID() + ". Previous Base Material is [" + (m_lastBaseMaterial == null ? "Null" : m_lastBaseMaterial.name) + "].");
00282 
00283             // Return if Awake() has not been called on the text object.
00284             if (m_isAwake == false)
00285                 return;
00286 
00287             // Handle Font Asset changes in the inspector.
00288             if (m_fontAsset == null || m_hasFontAssetChanged)
00289             {
00290                 LoadFontAsset();
00291                 m_isCalculateSizeRequired = true;
00292                 m_hasFontAssetChanged = false;
00293             }
00294 
00295 
00296             if (m_canvasRenderer == null || m_canvasRenderer.GetMaterial() == null || m_canvasRenderer.GetMaterial().GetTexture(ShaderUtilities.ID_MainTex) == null || m_fontAsset == null || m_fontAsset.atlas.GetInstanceID() != m_canvasRenderer.GetMaterial().GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID())
00297             {
00298                 LoadFontAsset();
00299                 m_isCalculateSizeRequired = true;
00300                 m_hasFontAssetChanged = false;
00301             }
00302 
00303             m_padding = GetPaddingForMaterial();
00304             //ComputeMarginSize();
00305 
00306             m_isInputParsingRequired = true;
00307             m_inputSource = TextInputSources.Text;
00308             m_havePropertiesChanged = true;
00309             m_isCalculateSizeRequired = true;
00310             m_isPreferredWidthDirty = true;
00311             m_isPreferredHeightDirty = true;
00312 
00313             SetAllDirty();
00314         }
