00327         // Event received when custom material editor properties are changed.
00328         void ON_MATERIAL_PROPERTY_CHANGED(bool isChanged, Material mat)
00329         {
00330             //Debug.Log("ON_MATERIAL_PROPERTY_CHANGED event received."); // Targeted Material is: " + mat.name + "  m_sharedMaterial: " + m_sharedMaterial.name + " with ID:" + m_sharedMaterial.GetInstanceID() + "  m_renderer.sharedMaterial: " + m_canvasRenderer.GetMaterial() + "  Masking Material:" + m_MaskMaterial.GetInstanceID());
00331 
00332             ShaderUtilities.GetShaderPropertyIDs(); // Initialize ShaderUtilities and get shader property IDs.
00333 
00334             int materialID = mat.GetInstanceID();
00335             int sharedMaterialID = m_sharedMaterial.GetInstanceID();
00336             int maskingMaterialID = m_MaskMaterial == null ? 0 : m_MaskMaterial.GetInstanceID();
00337 
00338             if (m_canvasRenderer == null || m_canvasRenderer.GetMaterial() == null)
00339             {
00340                 if (m_canvasRenderer == null) return;
00341 
00342                 if (m_fontAsset != null)
00343                 {
00344                     m_canvasRenderer.SetMaterial(m_fontAsset.material, m_sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex));
00345                     //Debug.LogWarning("No Material was assigned to " + name + ". " + m_fontAsset.material.name + " was assigned.");
00346                 }
00347                 else
00348                     Debug.LogWarning("No Font Asset assigned to " + name + ". Please assign a Font Asset.", this);
00349             }
00350 
00351 
00352             if (m_canvasRenderer.GetMaterial() != m_sharedMaterial && m_fontAsset == null) //    || m_renderer.sharedMaterials.Contains(mat))
00353             {
00354                 //Debug.Log("ON_MATERIAL_PROPERTY_CHANGED Called on Target ID: " + GetInstanceID() + ". Previous Material:" + m_sharedMaterial + "  New Material:" + m_uiRenderer.GetMaterial()); // on Object ID:" + GetInstanceID() + ". m_sharedMaterial: " + m_sharedMaterial.name + "  m_renderer.sharedMaterial: " + m_renderer.sharedMaterial.name);         
00355                 m_sharedMaterial = m_canvasRenderer.GetMaterial();
00356             }
00357 
00358 
00359             // Make sure material properties are synchronized between the assigned material and masking material.
00360             if (m_MaskMaterial != null)
00361             {
00362                 UnityEditor.Undo.RecordObject(m_MaskMaterial, "Material Property Changes");
00363                 UnityEditor.Undo.RecordObject(m_sharedMaterial, "Material Property Changes");
00364 
00365                 if (materialID == sharedMaterialID)
00366                 {
00367                     //Debug.Log("Copy base material properties to masking material if not null.");
00368                     float stencilID = m_MaskMaterial.GetFloat(ShaderUtilities.ID_StencilID);
00369                     float stencilComp = m_MaskMaterial.GetFloat(ShaderUtilities.ID_StencilComp);
00370                     //float stencilOp = m_MaskMaterial.GetFloat(ShaderUtilities.ID_StencilOp);
00371                     //float stencilRead = m_MaskMaterial.GetFloat(ShaderUtilities.ID_StencilReadMask);
00372                     //float stencilWrite = m_MaskMaterial.GetFloat(ShaderUtilities.ID_StencilWriteMask);
00373 
00374                     m_MaskMaterial.CopyPropertiesFromMaterial(mat);
00375                     m_MaskMaterial.shaderKeywords = mat.shaderKeywords;
00376 
00377                     m_MaskMaterial.SetFloat(ShaderUtilities.ID_StencilID, stencilID);
00378                     m_MaskMaterial.SetFloat(ShaderUtilities.ID_StencilComp, stencilComp);
00379                     //m_MaskMaterial.SetFloat(ShaderUtilities.ID_StencilOp, stencilOp);
00380                     //m_MaskMaterial.SetFloat(ShaderUtilities.ID_StencilReadMask, stencilID);
00381                     //m_MaskMaterial.SetFloat(ShaderUtilities.ID_StencilWriteMask, 0);
00382                 }
00383                 else if (materialID == maskingMaterialID)
00384                 {
00385                     // Update the padding 
00386                     GetPaddingForMaterial(mat);
00387 
00388                     m_sharedMaterial.CopyPropertiesFromMaterial(mat);
00389                     m_sharedMaterial.shaderKeywords = mat.shaderKeywords;
00390                     m_sharedMaterial.SetFloat(ShaderUtilities.ID_StencilID, 0);
00391                     m_sharedMaterial.SetFloat(ShaderUtilities.ID_StencilComp, 8);
00392                     //m_sharedMaterial.SetFloat(ShaderUtilities.ID_StencilOp, 0);
00393                     //m_sharedMaterial.SetFloat(ShaderUtilities.ID_StencilReadMask, 255);
00394                     //m_sharedMaterial.SetFloat(ShaderUtilities.ID_StencilWriteMask, 255);
00395                 }
00396 
00397             }
00398 
00399             m_padding = GetPaddingForMaterial();
00400             m_havePropertiesChanged = true;
00401             SetVerticesDirty();
00402             //SetMaterialDirty();
00403         }
