00438         // Event to Track Material Changed resulting from Drag-n-drop.
00439         void ON_DRAG_AND_DROP_MATERIAL(GameObject obj, Material currentMaterial, Material newMaterial)
00440         {
00441             //Debug.Log("Drag-n-Drop Event - Receiving Object ID " + GetInstanceID() + ". Sender ID " + obj.GetInstanceID()); // +  ". Prefab Parent is " + UnityEditor.PrefabUtility.GetPrefabParent(gameObject).GetInstanceID()); // + ". New Material is " + newMaterial.name + " with ID " + newMaterial.GetInstanceID() + ". Base Material is " + m_baseMaterial.name + " with ID " + m_baseMaterial.GetInstanceID());
00442 
00443             // Check if event applies to this current object
00444             #if UNITY_2018_2_OR_NEWER
00445             if (obj == gameObject || UnityEditor.PrefabUtility.GetCorrespondingObjectFromSource(gameObject) == obj)
00446             #else
00447             if (obj == gameObject || UnityEditor.PrefabUtility.GetPrefabParent(gameObject) == obj)
00448             #endif
00449             {
00450                 UnityEditor.Undo.RecordObject(this, "Material Assignment");
00451                 UnityEditor.Undo.RecordObject(m_canvasRenderer, "Material Assignment");
00452 
00453                 m_sharedMaterial = newMaterial;
00454 
00455                 m_padding = GetPaddingForMaterial();
00456 
00457                 m_havePropertiesChanged = true;
00458                 SetVerticesDirty();
00459                 SetMaterialDirty();
00460             }
00461         }
