00500         // Function which loads either the default font or a newly assigned font asset. This function also assigned the appropriate material to the renderer.
00501         protected override void LoadFontAsset()
00502         {
00503             //Debug.Log("***** LoadFontAsset() *****"); //TextMeshPro LoadFontAsset() has been called."); // Current Font Asset is " + (font != null ? font.name: "Null") );
00504             
00505             ShaderUtilities.GetShaderPropertyIDs(); // Initialize & Get shader property IDs.
00506 
00507             if (m_fontAsset == null)
00508             {
00509                 if (TMP_Settings.defaultFontAsset != null)
00510                     m_fontAsset = TMP_Settings.defaultFontAsset;
00511                 else
00512                     m_fontAsset = Resources.Load<TMP_FontAsset>("Fonts & Materials/LiberationSans SDF");
00513 
00514                 if (m_fontAsset == null)
00515                 {
00516                     Debug.LogWarning("The LiberationSans SDF Font Asset was not found. There is no Font Asset assigned to " + gameObject.name + ".", this);
00517                     return;
00518                 }
00519 
00520                 if (m_fontAsset.characterDictionary == null)
00521                 {
00522                     Debug.Log("Dictionary is Null!");
00523                 }
00524 
00525                 m_sharedMaterial = m_fontAsset.material;
00526             }
00527             else
00528             {
00529                 // Read font definition if needed.
00530                 if (m_fontAsset.characterDictionary == null)
00531                     m_fontAsset.ReadFontDefinition();
00532 
00533                 // Added for compatibility with previous releases.
00534                 if (m_sharedMaterial == null && m_baseMaterial != null)
00535                 {
00536                     m_sharedMaterial = m_baseMaterial;
00537                     m_baseMaterial = null;
00538                 }
00539 
00540                 // If font atlas texture doesn't match the assigned material font atlas, switch back to default material specified in the Font Asset.
00541                 if (m_sharedMaterial == null || m_sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex) == null || m_fontAsset.atlas.GetInstanceID() != m_sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID())
00542                 {
00543                     if (m_fontAsset.material == null)
00544                         Debug.LogWarning("The Font Atlas Texture of the Font Asset " + m_fontAsset.name + " assigned to " + gameObject.name + " is missing.", this);
00545                     else
00546                         m_sharedMaterial = m_fontAsset.material;
00547                 }
00548             }
00549 
00550 
00551             // Find and cache Underline & Ellipsis characters.
00552             GetSpecialCharacters(m_fontAsset);
00553 
00554             m_padding = GetPaddingForMaterial();
00555 
00556             SetMaterialDirty();
00557         }
00558 
00559 
00563         private Canvas GetCanvas()
00564         {
00565             Canvas canvas = null;
00566             var list = TMP_ListPool<Canvas>.Get();
00567 
00568             gameObject.GetComponentsInParent(false, list);
00569             if (list.Count > 0)
00570             {
00571                 // Find the first active and enabled canvas.
00572                 for (int i = 0; i < list.Count; ++i)
00573                 {
00574                     if (list[i].isActiveAndEnabled)
00575                     {
00576                         canvas = list[i];
00577                         break;
00578                     }
00579                 }
00580             }
00581 
00582             TMP_ListPool<Canvas>.Release(list);
00583 
00584             return canvas;
00585         }
