00604         // Enable Masking in the Shader
00605         void EnableMasking()
00606         {
00607             if (m_fontMaterial == null)
00608             {
00609                 m_fontMaterial = CreateMaterialInstance(m_sharedMaterial);
00610                 m_canvasRenderer.SetMaterial(m_fontMaterial, m_sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex));
00611             }
00612 
00613             m_sharedMaterial = m_fontMaterial;
00614             if (m_sharedMaterial.HasProperty(ShaderUtilities.ID_ClipRect))
00615             {
00616                 m_sharedMaterial.EnableKeyword(ShaderUtilities.Keyword_MASK_SOFT);
00617                 m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_HARD);
00618                 m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_TEX);
00619 
00620                 UpdateMask(); // Update Masking Coordinates
00621             }
00622 
00623             m_isMaskingEnabled = true;
00624 
00625             //m_uiRenderer.SetMaterial(m_sharedMaterial, null);
00626 
00627             //m_padding = ShaderUtilities.GetPadding(m_sharedMaterial, m_enableExtraPadding, m_isUsingBold);
00628             //m_alignmentPadding = ShaderUtilities.GetFontExtent(m_sharedMaterial);
00629 
00630             /*
00631             Material mat = m_uiRenderer.GetMaterial();
00632             if (mat.HasProperty(ShaderUtilities.ID_MaskCoord))
00633             {
00634                 mat.EnableKeyword("MASK_SOFT");
00635                 mat.DisableKeyword("MASK_HARD");
00636                 mat.DisableKeyword("MASK_OFF");
00637 
00638                 m_isMaskingEnabled = true;
00639                 UpdateMask();
