00691 
00692         // Update & recompute Mask offset
00693         void UpdateMask()
00694         {
00695             //Debug.Log("Updating Mask...");
00696 
00697             if (m_rectTransform != null)
00698             {
00699                 //Material mat = m_uiRenderer.GetMaterial();
00700                 //if (mat == null || (m_overflowMode == TextOverflowModes.ScrollRect && m_isScrollRegionSet))
00701                 //    return;
00702 
00703                 if (!ShaderUtilities.isInitialized)
00704                     ShaderUtilities.GetShaderPropertyIDs();
00705                 
00706                 //Debug.Log("Setting Mask for the first time.");
00707 
00708                 m_isScrollRegionSet = true;
00709 
00710                 float softnessX = Mathf.Min(Mathf.Min(m_margin.x, m_margin.z), m_sharedMaterial.GetFloat(ShaderUtilities.ID_MaskSoftnessX));
00711                 float softnessY = Mathf.Min(Mathf.Min(m_margin.y, m_margin.w), m_sharedMaterial.GetFloat(ShaderUtilities.ID_MaskSoftnessY));
00712 
00713                 softnessX = softnessX > 0 ? softnessX : 0;
00714                 softnessY = softnessY > 0 ? softnessY : 0;
00715 
00716                 float width = (m_rectTransform.rect.width - Mathf.Max(m_margin.x, 0) - Mathf.Max(m_margin.z, 0)) / 2 + softnessX;
00717                 float height = (m_rectTransform.rect.height - Mathf.Max(m_margin.y, 0) - Mathf.Max(m_margin.w, 0)) / 2 + softnessY;
00718 
00719                 
00720                 Vector2 center = m_rectTransform.localPosition + new Vector3((0.5f - m_rectTransform.pivot.x) * m_rectTransform.rect.width + (Mathf.Max(m_margin.x, 0) - Mathf.Max(m_margin.z, 0)) / 2, (0.5f - m_rectTransform.pivot.y) * m_rectTransform.rect.height + (-Mathf.Max(m_margin.y, 0) + Mathf.Max(m_margin.w, 0)) / 2);
00721         
00722                 //Vector2 center = m_rectTransform.localPosition + new Vector3((0.5f - m_rectTransform.pivot.x) * m_rectTransform.rect.width + (margin.x - margin.z) / 2, (0.5f - m_rectTransform.pivot.y) * m_rectTransform.rect.height + (-margin.y + margin.w) / 2);
00723                 Vector4 mask = new Vector4(center.x, center.y, width, height);
00724                 //Debug.Log(mask);
00725 
00726 
00727 
00728                 //Rect rect = new Rect(0, 0, m_rectTransform.rect.width + margin.x + margin.z, m_rectTransform.rect.height + margin.y + margin.w);
00729                 //int softness = (int)m_sharedMaterial.GetFloat(ShaderUtilities.ID_MaskSoftnessX) / 2;
00730                 m_sharedMaterial.SetVector(ShaderUtilities.ID_ClipRect, mask);
00731             }
00732         }
