00735         // Function called internally when a new material is assigned via the fontMaterial property.
00736         protected override Material GetMaterial(Material mat)
00737         {
00738             // Get Shader PropertyIDs if they haven't been cached already.
00739             ShaderUtilities.GetShaderPropertyIDs();
00740             
00741             // Check in case Object is disabled. If so, we don't have a valid reference to the Renderer.
00742             // This can occur when the Duplicate Material Context menu is used on an inactive object.
00743             //if (m_canvasRenderer == null)
00744             //    m_canvasRenderer = GetComponent<CanvasRenderer>();
00745 
00746             // Create Instance Material only if the new material is not the same instance previously used.
00747             if (m_fontMaterial == null || m_fontMaterial.GetInstanceID() != mat.GetInstanceID())
00748                 m_fontMaterial = CreateMaterialInstance(mat);
00749 
00750             m_sharedMaterial = m_fontMaterial;
00751 
00752             m_padding = GetPaddingForMaterial();
00753 
00754             m_ShouldRecalculateStencil = true;
00755             SetVerticesDirty();
00756             SetMaterialDirty();
00757 
00758             return m_sharedMaterial;
00759         }
