00766         protected override Material[] GetMaterials(Material[] mats)
00767         {
00768             int materialCount = m_textInfo.materialCount;
00769 
00770             if (m_fontMaterials == null)
00771                 m_fontMaterials = new Material[materialCount];
00772             else if (m_fontMaterials.Length != materialCount)
00773                 TMP_TextInfo.Resize(ref m_fontMaterials, materialCount, false);
00774 
00775             // Get instances of the materials
00776             for (int i = 0; i < materialCount; i++)
00777             {
00778                 if (i == 0)
00779                     m_fontMaterials[i] = fontMaterial;
00780                 else
00781                     m_fontMaterials[i] = m_subTextObjects[i].material;
00782             }
00783 
00784             m_fontSharedMaterials = m_fontMaterials;
00785 
00786             return m_fontMaterials;
00787         }
