00834         protected override void SetSharedMaterials(Material[] materials)
00835         {
00836             int materialCount = m_textInfo.materialCount;
00837 
00838             // Check allocation of the fontSharedMaterials array.
00839             if (m_fontSharedMaterials == null)
00840                 m_fontSharedMaterials = new Material[materialCount];
00841             else if (m_fontSharedMaterials.Length != materialCount)
00842                 TMP_TextInfo.Resize(ref m_fontSharedMaterials, materialCount, false);
00843 
00844             // Only assign as many materials as the text object contains.
00845             for (int i = 0; i < materialCount; i++)
00846             {
00847                 if (i == 0)
00848                 {
00849                     // Only assign new material if the font atlas textures match.
00850                     if (materials[i].GetTexture(ShaderUtilities.ID_MainTex) == null || materials[i].GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID() != m_sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID())
00851                         continue;
00852 
00853                     m_sharedMaterial = m_fontSharedMaterials[i] = materials[i];
00854                     m_padding = GetPaddingForMaterial(m_sharedMaterial);
00855                 }
00856                 else
00857                 {
00858                     // Only assign new material if the font atlas textures match.
00859                     if (materials[i].GetTexture(ShaderUtilities.ID_MainTex) == null || materials[i].GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID() != m_subTextObjects[i].sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID())
00860                         continue;
00861 
00862                     // Only assign a new material if none were specified in the text input.
00863                     if (m_subTextObjects[i].isDefaultMaterial)
00864                         m_subTextObjects[i].sharedMaterial = m_fontSharedMaterials[i] = materials[i];
00865                 }
00866             }
00867         }
