00870         // This function will create an instance of the Font Material.
00871         protected override void SetOutlineThickness(float thickness)
00872         {
00873             // Use material instance if one exists. Otherwise, create a new instance of the shared material.
00874             if (m_fontMaterial != null && m_sharedMaterial.GetInstanceID() != m_fontMaterial.GetInstanceID())
00875             {
00876                 m_sharedMaterial = m_fontMaterial;
00877                 m_canvasRenderer.SetMaterial(m_sharedMaterial, m_sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex));
00878             }
00879             else if(m_fontMaterial == null)
00880             {
00881                 m_fontMaterial = CreateMaterialInstance(m_sharedMaterial);
00882                 m_sharedMaterial = m_fontMaterial;
00883                 m_canvasRenderer.SetMaterial(m_sharedMaterial, m_sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex));
00884             }
00885 
00886             thickness = Mathf.Clamp01(thickness);
00887             m_sharedMaterial.SetFloat(ShaderUtilities.ID_OutlineWidth, thickness);
00888             m_padding = GetPaddingForMaterial();
00889         }
