00920         // Sets the Render Queue and Ztest mode 
00921         protected override void SetShaderDepth()
00922         {
00923             if (m_canvas == null || m_sharedMaterial == null)
00924                 return;
00925 
00926             if (m_canvas.renderMode == RenderMode.ScreenSpaceOverlay || m_isOverlay)
00927             {
00928                 // Should this use an instanced material?
00929                 m_sharedMaterial.SetFloat(ShaderUtilities.ShaderTag_ZTestMode, 0);
00930             }
00931             else
00932             {   // TODO: This section needs to be tested.
00933                 m_sharedMaterial.SetFloat(ShaderUtilities.ShaderTag_ZTestMode, 4);
00934             }
00935         }
