00938         // Sets the Culling mode of the material
00939         protected override void SetCulling()
00940         {
00941             if (m_isCullingEnabled)
00942             {
00943                 Material mat = materialForRendering;
00944 
00945                 if (mat != null)
00946                     mat.SetFloat("_CullMode", 2);
00947 
00948                 for (int i = 1; i < m_subTextObjects.Length && m_subTextObjects[i] != null; i++)
00949                 {
00950                     mat = m_subTextObjects[i].materialForRendering;
00951 
00952                     if (mat != null)
00953                     {
00954                         mat.SetFloat(ShaderUtilities.ShaderTag_CullMode, 2);
00955                     }
00956                 }
00957             }
00958             else
00959             {
00960                 Material mat = materialForRendering;
00961 
00962                 if (mat != null)
00963                     mat.SetFloat("_CullMode", 0);
00964 
00965                 for (int i = 1; i < m_subTextObjects.Length && m_subTextObjects[i] != null; i++)
00966                 {
00967                     mat = m_subTextObjects[i].materialForRendering;
00968 
00969                     if (mat != null)
00970                     {
00971                         mat.SetFloat(ShaderUtilities.ShaderTag_CullMode, 0);
00972                     }
00973                 }
00974             }
00975         }
