01025 
01026         // This function parses through the Char[] to determine how many characters will be visible. It then makes sure the arrays are large enough for all those characters.
01027         protected override int SetArraySizes(int[] chars)
01028         {
01029             //Debug.Log("*** SetArraySizes() on Instance ID (" + GetInstanceID() + ") ***");
01030             #if TMP_PROFILE_ON
01031             Profiler.BeginSample("SetArraySizes");
01032             #endif
01033 
01034             int tagEnd = 0;
01035             int spriteCount = 0;
01036 
01037             m_totalCharacterCount = 0;
01038             m_isUsingBold = false;
01039             m_isParsingText = false;
01040             tag_NoParsing = false;
01041             m_style = m_fontStyle;
01042 
01043             m_fontWeightInternal = (m_style & FontStyles.Bold) == FontStyles.Bold ? 700 : m_fontWeight;
01044             m_fontWeightStack.SetDefault(m_fontWeightInternal);
01045 
01046             m_currentFontAsset = m_fontAsset;
01047             m_currentMaterial = m_sharedMaterial;
01048             m_currentMaterialIndex = 0;
01049 
01050             m_materialReferenceStack.SetDefault(new MaterialReference(m_currentMaterialIndex, m_currentFontAsset, null, m_currentMaterial, m_padding));
01051 
01052             m_materialReferenceIndexLookup.Clear();
01053             MaterialReference.AddMaterialReference(m_currentMaterial, m_currentFontAsset, m_materialReferences, m_materialReferenceIndexLookup);
01054 
01055             if (m_textInfo == null) m_textInfo = new TMP_TextInfo();
01056             m_textElementType = TMP_TextElementType.Character;
01057 
01058             // Clear Linked Text object if we have one.
01059             if (m_linkedTextComponent != null)
01060             {
01061                 m_linkedTextComponent.text = string.Empty; // SetText(string.Empty, false);
01062                 m_linkedTextComponent.ForceMeshUpdate();
01063             }
01064 
01065             // Parsing XML tags in the text
01066             for (int i = 0; i < chars.Length && chars[i] != 0; i++)
01067             {
01068                 //Make sure the characterInfo array can hold the next text element.
01069                 if (m_textInfo.characterInfo == null || m_totalCharacterCount >= m_textInfo.characterInfo.Length)
01070                     TMP_TextInfo.Resize(ref m_textInfo.characterInfo, m_totalCharacterCount + 1, true);
01071 
01072                 int c = chars[i];
01073 
01074                 // PARSE XML TAGS
01075                 #region PARSE XML TAGS
01076                 if (m_isRichText && c == 60) // if Char '<'
01077                 {
01078                     int prev_MaterialIndex = m_currentMaterialIndex;
01079 
01080                     // Check if Tag is Valid
01081                     if (ValidateHtmlTag(chars, i + 1, out tagEnd))
01082                     {
01083                         i = tagEnd;
01084 
01085                         if ((m_style & FontStyles.Bold) == FontStyles.Bold) m_isUsingBold = true;
01086 
01087                         if (m_textElementType == TMP_TextElementType.Sprite)
01088                         {
01089                             m_materialReferences[m_currentMaterialIndex].referenceCount += 1;
01090 
01091                             m_textInfo.characterInfo[m_totalCharacterCount].character = (char)(57344 + m_spriteIndex);
01092                             m_textInfo.characterInfo[m_totalCharacterCount].spriteIndex = m_spriteIndex;
01093                             m_textInfo.characterInfo[m_totalCharacterCount].fontAsset = m_currentFontAsset;
01094                             m_textInfo.characterInfo[m_totalCharacterCount].spriteAsset = m_currentSpriteAsset;
01095                             m_textInfo.characterInfo[m_totalCharacterCount].materialReferenceIndex = m_currentMaterialIndex;
01096                             m_textInfo.characterInfo[m_totalCharacterCount].elementType = m_textElementType;
01097 
01098                             // Restore element type and material index to previous values.
01099                             m_textElementType = TMP_TextElementType.Character;
01100                             m_currentMaterialIndex = prev_MaterialIndex;
01101 
01102                             spriteCount += 1;
01103                             m_totalCharacterCount += 1;
01104                         }
01105 
01106                         continue;
01107                     }
01108                 }
01109                 #endregion
01110 
01111 
01112                 bool isUsingFallback = false;
01113                 bool isUsingAlternativeTypeface = false;
01114 
01115                 TMP_Glyph glyph;
01116                 TMP_FontAsset tempFontAsset;
01117                 TMP_FontAsset prev_fontAsset = m_currentFontAsset;
01118                 Material prev_material = m_currentMaterial;
01119                 int prev_materialIndex = m_currentMaterialIndex;
01120 
01121 
01122                 // Handle Font Styles like LowerCase, UpperCase and SmallCaps.
01123                 #region Handling of LowerCase, UpperCase and SmallCaps Font Styles
01124                 if (m_textElementType == TMP_TextElementType.Character)
01125                 {
01126                     if ((m_style & FontStyles.UpperCase) == FontStyles.UpperCase)
01127                     {
01128                         // If this character is lowercase, switch to uppercase.
01129                         if (char.IsLower((char)c))
01130                             c = char.ToUpper((char)c);
01131 
01132                     }
01133                     else if ((m_style & FontStyles.LowerCase) == FontStyles.LowerCase)
01134                     {
01135                         // If this character is uppercase, switch to lowercase.
01136                         if (char.IsUpper((char)c))
01137                             c = char.ToLower((char)c);
01138                     }
01139                     else if ((m_fontStyle & FontStyles.SmallCaps) == FontStyles.SmallCaps || (m_style & FontStyles.SmallCaps) == FontStyles.SmallCaps)
01140                     {
01141                         // Only convert lowercase characters to uppercase.
01142                         if (char.IsLower((char)c))
01143                             c = char.ToUpper((char)c);
01144                     }
01145                 }
01146                 #endregion
01147 
01148 
01149                 // Handling of font weights.
01150                 #region HANDLING OF FONT WEIGHT
01151                 tempFontAsset = GetFontAssetForWeight(m_fontWeightInternal);
01152                 if (tempFontAsset != null)
01153                 {
01154                     isUsingFallback = true;
01155                     isUsingAlternativeTypeface = true;
01156                     m_currentFontAsset = tempFontAsset;
01157                     //m_currentMaterialIndex = MaterialReference.AddMaterialReference(m_currentMaterial, tempFontAsset, m_materialReferences, m_materialReferenceIndexLookup);
01158                 }
01159                 #endregion
01160 
01161 
01162                 // Lookup the Glyph data for each character and cache it.
01163                 #region LOOKUP GLYPH
01164                 tempFontAsset = TMP_FontUtilities.SearchForGlyph(m_currentFontAsset, c, out glyph);
01165 
01166                 // Search for the glyph in the Sprite Asset assigned to the text object.
01167                 if (glyph == null)
01168                 {
01169                     TMP_SpriteAsset spriteAsset = this.spriteAsset;
01170 
01171                     if (spriteAsset != null)
01172                     {
01173                         int spriteIndex = -1;
01174 
01175                         // Check Default Sprite Asset and its Fallbacks
01176                         spriteAsset = TMP_SpriteAsset.SearchForSpriteByUnicode(spriteAsset, c, true, out spriteIndex);
01177 
01178                         if (spriteIndex != -1)
01179                         {
01180                             m_textElementType = TMP_TextElementType.Sprite;
01181                             m_textInfo.characterInfo[m_totalCharacterCount].elementType = m_textElementType;
01182 
01183                             m_currentMaterialIndex = MaterialReference.AddMaterialReference(spriteAsset.material, spriteAsset, m_materialReferences, m_materialReferenceIndexLookup);
01184                             m_materialReferences[m_currentMaterialIndex].referenceCount += 1;
01185 
01186                             m_textInfo.characterInfo[m_totalCharacterCount].character = (char)c;
01187                             m_textInfo.characterInfo[m_totalCharacterCount].spriteIndex = spriteIndex;
01188                             m_textInfo.characterInfo[m_totalCharacterCount].fontAsset = m_currentFontAsset;
01189                             m_textInfo.characterInfo[m_totalCharacterCount].spriteAsset = spriteAsset;
01190                             m_textInfo.characterInfo[m_totalCharacterCount].materialReferenceIndex = m_currentMaterialIndex;
01191 
01192                             // Restore element type and material index to previous values.
01193                             m_textElementType = TMP_TextElementType.Character;
01194                             m_currentMaterialIndex = prev_materialIndex;
01195 
01196                             spriteCount += 1;
01197                             m_totalCharacterCount += 1;
01198 
01199                             continue;
01200                         }
01201                     }
01202                 }
01203 
01204                 // Search for the glyph in the list of fallback assigned in the TMP Settings (General Fallbacks).
01205                 if (glyph == null)
01206                 {
01207                     if (TMP_Settings.fallbackFontAssets != null && TMP_Settings.fallbackFontAssets.Count > 0)
01208                         tempFontAsset = TMP_FontUtilities.SearchForGlyph(TMP_Settings.fallbackFontAssets, c, out glyph);
01209                 }
01210 
01211                 // Search for the glyph in the Default Font Asset assigned in the TMP Settings file.
01212                 if (glyph == null)
01213                 {
01214                     if (TMP_Settings.defaultFontAsset != null)
01215                         tempFontAsset = TMP_FontUtilities.SearchForGlyph(TMP_Settings.defaultFontAsset, c, out glyph);
01216                 }
01217 
01218                 // TODO: Add support for using Sprite Assets like a special Emoji only Sprite Asset when UTF16 or UTF32 glyphs are requested.
01219                 // This would kind of mirror native Emoji support.
01220                 if (glyph == null)
01221                 {
01222                     TMP_SpriteAsset spriteAsset = TMP_Settings.defaultSpriteAsset;
01223 
01224                     if (spriteAsset != null)
01225                     {
01226                         int spriteIndex = -1;
01227 
01228                         // Check Default Sprite Asset and its Fallbacks
01229                         spriteAsset = TMP_SpriteAsset.SearchForSpriteByUnicode(spriteAsset, c, true, out spriteIndex);
01230 
01231                         if (spriteIndex != -1)
01232                         {
01233                             m_textElementType = TMP_TextElementType.Sprite;
01234                             m_textInfo.characterInfo[m_totalCharacterCount].elementType = m_textElementType;
01235 
01236                             m_currentMaterialIndex = MaterialReference.AddMaterialReference(spriteAsset.material, spriteAsset, m_materialReferences, m_materialReferenceIndexLookup);
01237                             m_materialReferences[m_currentMaterialIndex].referenceCount += 1;
01238 
01239                             m_textInfo.characterInfo[m_totalCharacterCount].character = (char)c;
01240                             m_textInfo.characterInfo[m_totalCharacterCount].spriteIndex = spriteIndex;
01241                             m_textInfo.characterInfo[m_totalCharacterCount].fontAsset = m_currentFontAsset;
01242                             m_textInfo.characterInfo[m_totalCharacterCount].spriteAsset = spriteAsset;
01243                             m_textInfo.characterInfo[m_totalCharacterCount].materialReferenceIndex = m_currentMaterialIndex;
01244 
01245                             // Restore element type and material index to previous values.
01246                             m_textElementType = TMP_TextElementType.Character;
01247                             m_currentMaterialIndex = prev_materialIndex;
01248 
01249                             spriteCount += 1;
01250                             m_totalCharacterCount += 1;
01251 
01252                             continue;
01253                         }
01254                     }
01255                 }
01256 
01257                 //Check if Lowercase or Uppercase variant of the character is available.
01258                 // Not sure this is necessary anyone as it is very unlikely with recursive search through fallback fonts.
01259                 //if (glyph == null)
01260                 //{
01261                 //    if (char.IsLower((char)c))
01262                 //    {
01263                 //        if (m_currentFontAsset.characterDictionary.TryGetValue(char.ToUpper((char)c), out glyph))
01264                 //            c = chars[i] = char.ToUpper((char)c);
01265                 //    }
01266                 //    else if (char.IsUpper((char)c))
01267                 //    {
01268                 //        if (m_currentFontAsset.characterDictionary.TryGetValue(char.ToLower((char)c), out glyph))
01269                 //            c = chars[i] = char.ToLower((char)c);
01270                 //    }
01271                 //}
01272 
01273                 // Replace missing glyph by the Square (9633) glyph or possibly the Space (32) glyph.
01274                 if (glyph == null)
01275                 {
01276                     // Save the original unicode character
01277                     int srcGlyph = c;
01278 
01279                     // Try replacing the missing glyph character by TMP Settings Missing Glyph or Square (9633) character.
01280                     c = chars[i] = TMP_Settings.missingGlyphCharacter == 0 ? 9633 : TMP_Settings.missingGlyphCharacter;
01281 
01282                     // Check for the missing glyph character in the currently assigned font asset.
01283                     tempFontAsset = TMP_FontUtilities.SearchForGlyph(m_currentFontAsset, c, out glyph);
01284 
01285                     if (glyph == null)
01286                     {
01287                         // Search for the missing glyph character in the TMP Settings Fallback list.
01288                         if (TMP_Settings.fallbackFontAssets != null && TMP_Settings.fallbackFontAssets.Count > 0)
01289                             tempFontAsset = TMP_FontUtilities.SearchForGlyph(TMP_Settings.fallbackFontAssets, c, out glyph);
01290                     }
01291 
01292                     if (glyph == null)
01293                     {
01294                         // Search for the missing glyph in the TMP Settings Default Font Asset.
01295                         if (TMP_Settings.defaultFontAsset != null)
01296                             tempFontAsset = TMP_FontUtilities.SearchForGlyph(TMP_Settings.defaultFontAsset, c, out glyph);
01297                     }
01298 
01299                     if (glyph == null)
01300                     {
01301                         // Use Space (32) Glyph from the currently assigned font asset.
01302                         c = chars[i] = 32;
01303                         tempFontAsset = TMP_FontUtilities.SearchForGlyph(m_currentFontAsset, c, out glyph);
01304                         if (!TMP_Settings.warningsDisabled) Debug.LogWarning("Character with ASCII value of " + srcGlyph + " was not found in the Font Asset Glyph Table. It was replaced by a space.", this);
01305                     }
01306                 }
01307 
01308                 // Determine if the font asset is still the current font asset or a fallback.
01309                 if (tempFontAsset != null)
01310                 {
01311                     if (tempFontAsset.GetInstanceID() != m_currentFontAsset.GetInstanceID())
01312                     {
01313                         isUsingFallback = true;
01314                         isUsingAlternativeTypeface = false;
01315                         m_currentFontAsset = tempFontAsset;
01316                     }
01317                 }
01318                 #endregion
01319 
01320 
01321                 m_textInfo.characterInfo[m_totalCharacterCount].elementType = TMP_TextElementType.Character;
01322                 m_textInfo.characterInfo[m_totalCharacterCount].textElement = glyph;
01323                 m_textInfo.characterInfo[m_totalCharacterCount].isUsingAlternateTypeface = isUsingAlternativeTypeface;
01324                 m_textInfo.characterInfo[m_totalCharacterCount].character = (char)c;
01325                 m_textInfo.characterInfo[m_totalCharacterCount].fontAsset = m_currentFontAsset;
01326 
01327                 if (isUsingFallback)
01328                 {
01329                     // Create Fallback material instance matching current material preset if necessary
01330                     if (TMP_Settings.matchMaterialPreset)
01331                         m_currentMaterial = TMP_MaterialManager.GetFallbackMaterial(m_currentMaterial, m_currentFontAsset.material);
01332                     else
01333                         m_currentMaterial = m_currentFontAsset.material;
01334 
01335                     m_currentMaterialIndex = MaterialReference.AddMaterialReference(m_currentMaterial, m_currentFontAsset, m_materialReferences, m_materialReferenceIndexLookup);
01336                 }
01337 
01338                 if (!char.IsWhiteSpace((char)c) && c != 0x200B)
01339                 {
01340                     // Limit the mesh of the main text object to 65535 vertices and use sub objects for the overflow.
01341                     if (m_materialReferences[m_currentMaterialIndex].referenceCount < 16383)
01342                         m_materialReferences[m_currentMaterialIndex].referenceCount += 1;
01343                     else
01344                     {
01345                         m_currentMaterialIndex = MaterialReference.AddMaterialReference(new Material(m_currentMaterial), m_currentFontAsset, m_materialReferences, m_materialReferenceIndexLookup);
01346                         m_materialReferences[m_currentMaterialIndex].referenceCount += 1;
01347                     }
01348                 }
01349 
01350                 m_textInfo.characterInfo[m_totalCharacterCount].material = m_currentMaterial;
01351                 m_textInfo.characterInfo[m_totalCharacterCount].materialReferenceIndex = m_currentMaterialIndex;
01352                 m_materialReferences[m_currentMaterialIndex].isFallbackMaterial = isUsingFallback;
01353 
01354                 // Restore previous font asset and material if fallback font was used.
01355                 if (isUsingFallback)
01356                 {
01357                     m_materialReferences[m_currentMaterialIndex].fallbackMaterial = prev_material;
01358                     m_currentFontAsset = prev_fontAsset;
01359                     m_currentMaterial = prev_material;
01360                     m_currentMaterialIndex = prev_materialIndex;
01361                 }
01362 
01363                 m_totalCharacterCount += 1;
01364             }
01365 
01366             // Early return if we are calculating the preferred values.
01367             if (m_isCalculatingPreferredValues)
01368             {
01369                 m_isCalculatingPreferredValues = false;
01370                 m_isInputParsingRequired = true;
01371                 return m_totalCharacterCount;
01372             }
01373 
01374             // Save material and sprite count.
01375             m_textInfo.spriteCount = spriteCount;
01376             int materialCount = m_textInfo.materialCount = m_materialReferenceIndexLookup.Count;
01377 
01378             // Check if we need to resize the MeshInfo array for handling different materials.
01379             if (materialCount > m_textInfo.meshInfo.Length)
01380                 TMP_TextInfo.Resize(ref m_textInfo.meshInfo, materialCount, false);
01381 
01382             // Resize SubTextObject array if necessary
01383             if (materialCount > m_subTextObjects.Length)
01384                 TMP_TextInfo.Resize(ref m_subTextObjects, Mathf.NextPowerOfTwo(materialCount + 1));
01385 
01386             // Resize CharacterInfo[] if allocations are excessive
01387             if (m_textInfo.characterInfo.Length - m_totalCharacterCount > 256)
01388                 TMP_TextInfo.Resize(ref m_textInfo.characterInfo, Mathf.Max(m_totalCharacterCount + 1, 256), true);
01389 
01390 
01391             // Iterate through the material references to set the mesh buffer allocations
01392             for (int i = 0; i < materialCount; i++)
01393             {
01394                 // Add new sub text object for each material reference
01395                 if (i > 0)
01396                 {
01397                     if (m_subTextObjects[i] == null)
01398                     {
01399                         m_subTextObjects[i] = TMP_SubMeshUI.AddSubTextObject(this, m_materialReferences[i]);
01400 
01401                         // Not sure this is necessary
01402                         m_textInfo.meshInfo[i].vertices = null;
01403                     }
01404                     //else if (m_subTextObjects[i].gameObject.activeInHierarchy == false)
01405                     //    m_subTextObjects[i].gameObject.SetActive(true);
01406 
01407                     // Make sure the pivots are synchronized
01408                     if (m_rectTransform.pivot != m_subTextObjects[i].rectTransform.pivot)
01409                         m_subTextObjects[i].rectTransform.pivot = m_rectTransform.pivot;
01410 
01411                     // Check if the material has changed.
01412                     if (m_subTextObjects[i].sharedMaterial == null || m_subTextObjects[i].sharedMaterial.GetInstanceID() != m_materialReferences[i].material.GetInstanceID())
01413                     {
01414                         bool isDefaultMaterial = m_materialReferences[i].isDefaultMaterial;
01415 
01416                         m_subTextObjects[i].isDefaultMaterial = isDefaultMaterial;
01417 
01418                         // Assign new material if we are not using the default material or if the font asset has changed.
01419                         if (!isDefaultMaterial || m_subTextObjects[i].sharedMaterial == null || m_subTextObjects[i].sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID() != m_materialReferences[i].material.GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID())
01420                         {
01421                             m_subTextObjects[i].sharedMaterial = m_materialReferences[i].material;
01422                             m_subTextObjects[i].fontAsset = m_materialReferences[i].fontAsset;
01423                             m_subTextObjects[i].spriteAsset = m_materialReferences[i].spriteAsset;
01424                         }
01425                     }
01426 
01427                     // Check if we need to use a Fallback Material
01428                     if (m_materialReferences[i].isFallbackMaterial)
01429                     {
01430                         m_subTextObjects[i].fallbackMaterial = m_materialReferences[i].material;
01431                         m_subTextObjects[i].fallbackSourceMaterial = m_materialReferences[i].fallbackMaterial;
01432                     }
01433 
01434                 }
01435 
01436                 int referenceCount = m_materialReferences[i].referenceCount;
01437 
01438                 // Check to make sure our buffers allocations can accommodate the required text elements.
01439                 if (m_textInfo.meshInfo[i].vertices == null || m_textInfo.meshInfo[i].vertices.Length < referenceCount * 4)
01440                 {
01441                     if (m_textInfo.meshInfo[i].vertices == null)
01442                     {
01443                         if (i == 0)
01444                             m_textInfo.meshInfo[i] = new TMP_MeshInfo(m_mesh, referenceCount + 1);
01445                         else
01446                             m_textInfo.meshInfo[i] = new TMP_MeshInfo(m_subTextObjects[i].mesh, referenceCount + 1);
01447                     }
01448                     else
01449                         m_textInfo.meshInfo[i].ResizeMeshInfo(referenceCount > 1024 ? referenceCount + 256 : Mathf.NextPowerOfTwo(referenceCount));
01450                 }
01451                 else if (m_textInfo.meshInfo[i].vertices.Length - referenceCount * 4 > 1024)
01452                 {
01453                     // Resize vertex buffers if allocations are excessive.
01454                     //Debug.Log("Reducing the size of the vertex buffers.");
01455                     m_textInfo.meshInfo[i].ResizeMeshInfo(referenceCount > 1024 ? referenceCount + 256 : Mathf.Max(Mathf.NextPowerOfTwo(referenceCount), 256));
01456                 }
01457             }
01458 
01459             //TMP_MaterialManager.CleanupFallbackMaterials();
01460 
01461             // Clean up unused SubMeshes
01462             for (int i = materialCount; i < m_subTextObjects.Length && m_subTextObjects[i] != null; i++)
01463             {
01464                 if (i < m_textInfo.meshInfo.Length)
01465                 {
01466                     m_subTextObjects[i].canvasRenderer.SetMesh(null);
01467 
01468                     // TODO: Figure out a way to handle this without running into Unity's Rebuild loop issue.
01469                     //m_subTextObjects[i].gameObject.SetActive(false);
01470                 }
01471             }
01472 
01473             #if TMP_PROFILE_ON
01474             Profiler.EndSample();
01475             #endif
01476 
01477             return m_totalCharacterCount;
01478         }
