01492         public override void ComputeMarginSize()
01493         {
01494             if (this.rectTransform != null)
01495             {
01496                 //Debug.Log("*** ComputeMarginSize() *** Current RectTransform's Width is " + m_rectTransform.rect.width + " and Height is " + m_rectTransform.rect.height); // + " and size delta is "  + m_rectTransform.sizeDelta);
01497 
01498                 m_marginWidth = m_rectTransform.rect.width - m_margin.x - m_margin.z;
01499                 m_marginHeight = m_rectTransform.rect.height - m_margin.y - m_margin.w;
01500 
01501                 // Update the corners of the RectTransform
01502                 m_RectTransformCorners = GetTextContainerLocalCorners();
01503             }
01504         }
