01539         protected override void OnRectTransformDimensionsChange()
01540         {
01541             //Debug.Log("*** OnRectTransformDimensionsChange() *** ActiveInHierarchy: " + this.gameObject.activeInHierarchy + "  Frame: " + Time.frameCount); 
01542 
01543             // Make sure object is active in Hierarchy
01544             if (!this.gameObject.activeInHierarchy)
01545                 return;
01546 
01547             ComputeMarginSize();
01548 
01549             UpdateSubObjectPivot();
01550 
01551             SetVerticesDirty();
01552             SetLayoutDirty();
01553         }
