01555 
01559         void LateUpdate()
01560         {
01561             if (m_rectTransform.hasChanged)
01562             {
01563                 // We need to update the SDF scale or possibly regenerate the text object if lossy scale has changed.
01564                 float lossyScaleY = m_rectTransform.lossyScale.y;
01565                 if (!m_havePropertiesChanged && lossyScaleY != m_previousLossyScaleY && m_text != string.Empty && m_text != null)
01566                 {
01567                     UpdateSDFScale(lossyScaleY);
01568 
01569                     m_previousLossyScaleY = lossyScaleY;
01570                 }
01571                 m_rectTransform.hasChanged = false;
01572             }
01573 
01574             // Added to handle legacy animation mode.
01575             if (m_isUsingLegacyAnimationComponent)
01576             {
01577                 //if (m_havePropertiesChanged)
01578                 m_havePropertiesChanged = true;
01579                 OnPreRenderCanvas();
01580             }
01581         }
