01584 
01585         // Called just before the Canvas is rendered.
01586         void OnPreRenderCanvas()
01587         {
01588             //Debug.Log("*** OnPreRenderCanvas() *** Frame: " + Time.frameCount); 
01589 
01590             // Make sure object is active and that we have a valid Canvas.
01591             if (!m_isAwake || (this.IsActive() == false && m_ignoreActiveState == false)) return;
01592 
01593             if (m_canvas == null) { m_canvas = this.canvas; if (m_canvas == null) return; }
01594 
01595 
01596             // Debug Variables
01597             loopCountA = 0;
01598             //loopCountB = 0;
01599             //loopCountC = 0;
01600             //loopCountD = 0;
01601             //loopCountE = 0;
01602 
01603             // Update Margins
01604             //ComputeMarginSize();
01605 
01606             // Update Mask
01607             //    if (m_isMaskingEnabled)
01608             //    {
01609             //        UpdateMask();
01610             //    }
01611 
01612 
01613             if (m_havePropertiesChanged || m_isLayoutDirty)
01614             {
01615                 //Debug.Log("Properties have changed!"); // Assigned Material is:" + m_sharedMaterial); // New Text is: " + m_text + ".");
01616 
01617                 // Update mesh padding if necessary.
01618                 if (checkPaddingRequired)
01619                     UpdateMeshPadding();
01620 
01621                 // Reparse the text if the input has changed or text was truncated.
01622                 if (m_isInputParsingRequired || m_isTextTruncated)
01623                     ParseInputText();
01624 
01625                 // Reset Font min / max used with Auto-sizing
01626                 if (m_enableAutoSizing)
01627                     m_fontSize = Mathf.Clamp(m_fontSizeBase, m_fontSizeMin, m_fontSizeMax);
01628 
01629                 m_maxFontSize = m_fontSizeMax;
01630                 m_minFontSize = m_fontSizeMin;
01631                 m_lineSpacingDelta = 0;
01632                 m_charWidthAdjDelta = 0;
01633                 //m_recursiveCount = 0;
01634 
01635                 m_isCharacterWrappingEnabled = false;
01636                 m_isTextTruncated = false;
01637 
01638                 m_havePropertiesChanged = false;
01639                 m_isLayoutDirty = false;
01640                 m_ignoreActiveState = false;
01641 
01642                 GenerateTextMesh();
01643             }
01644         }
