01647 
01651         protected override void GenerateTextMesh()
01652         {
01653             //Debug.Log("*** GenerateTextMesh() ***"); // ***** Frame: " + Time.frameCount); // + ". Point Size: " + m_fontSize + ". Margins are (W) " + m_marginWidth + "  (H) " + m_marginHeight); // ". Iteration Count: " + loopCountA + ".  Min: " + m_minFontSize + "  Max: " + m_maxFontSize + "  Delta: " + (m_maxFontSize - m_minFontSize) + "  Font size is " + m_fontSize); //called for Object with ID " + GetInstanceID()); // Assigned Material is " + m_uiRenderer.GetMaterial().name); // IncludeForMasking " + this.m_IncludeForMasking); // and text is " + m_text);
01654             //Profiler.BeginSample("TMP Generate Text - Pre");
01655 
01656             // Early exit if no font asset was assigned. This should not be needed since LiberationSans SDF will be assigned by default.
01657             if (m_fontAsset == null || m_fontAsset.characterDictionary == null)
01658             {
01659                 Debug.LogWarning("Can't Generate Mesh! No Font Asset has been assigned to Object ID: " + this.GetInstanceID());
01660                 return;
01661             }
01662 
01663             // Clear TextInfo
01664             if (m_textInfo != null)
01665                 m_textInfo.Clear();
01666 
01667             // Early exit if we don't have any Text to generate.
01668             if (m_char_buffer == null || m_char_buffer.Length == 0 || m_char_buffer[0] == (char)0)
01669             {
01670                 // Clear mesh and upload changes to the mesh.
01671                 ClearMesh();
01672 
01673                 m_preferredWidth = 0;
01674                 m_preferredHeight = 0;
01675 
01676                 // Event indicating the text has been regenerated.
01677                 TMPro_EventManager.ON_TEXT_CHANGED(this);
01678 
01679                 return;
01680             }
01681 
01682             m_currentFontAsset = m_fontAsset;
01683             m_currentMaterial = m_sharedMaterial;
01684             m_currentMaterialIndex = 0;
01685             m_materialReferenceStack.SetDefault(new MaterialReference(m_currentMaterialIndex, m_currentFontAsset, null, m_currentMaterial, m_padding));
01686 
01687             m_currentSpriteAsset = m_spriteAsset;
01688 
01689             // Stop  all Sprite Animations
01690             if (m_spriteAnimator != null)
01691                 m_spriteAnimator.StopAllAnimations();
01692 
01693             // Total character count is computed when the text is parsed.
01694             int totalCharacterCount = m_totalCharacterCount;
01695 
01696             // Calculate the scale of the font based on selected font size and sampling point size.
01697             // baseScale is calculated using the font asset assigned to the text object.
01698             float baseScale = m_fontScale = (m_fontSize / m_fontAsset.fontInfo.PointSize * m_fontAsset.fontInfo.Scale);
01699             float currentElementScale = baseScale;
01700             m_fontScaleMultiplier = 1;
01701 
01702             m_currentFontSize = m_fontSize;
01703             m_sizeStack.SetDefault(m_currentFontSize);
01704             float fontSizeDelta = 0;
01705 
01706             int charCode = 0; // Holds the character code of the currently being processed character.
01707 
01708             m_style = m_fontStyle; // Set the default style.
01709             m_fontWeightInternal = (m_style & FontStyles.Bold) == FontStyles.Bold ? 700 : m_fontWeight;
01710             m_fontWeightStack.SetDefault(m_fontWeightInternal);
01711             m_fontStyleStack.Clear();
01712 
01713             m_lineJustification = m_textAlignment; // Sets the line justification mode to match editor alignment.
01714             m_lineJustificationStack.SetDefault(m_lineJustification);
01715 
01716             float padding = 0;
01717             float style_padding = 0; // Extra padding required to accommodate Bold style.
01718             float bold_xAdvance_multiplier = 1; // Used to increase spacing between character when style is bold.
01719 
01720             m_baselineOffset = 0; // Used by subscript characters.
01721             m_baselineOffsetStack.Clear();
01722 
01723             // Underline
01724             bool beginUnderline = false;
01725             Vector3 underline_start = Vector3.zero; // Used to track where underline starts & ends.
01726             Vector3 underline_end = Vector3.zero;
01727 
01728             // Strike-through
01729             bool beginStrikethrough = false;
01730             Vector3 strikethrough_start = Vector3.zero;
01731             Vector3 strikethrough_end = Vector3.zero;
01732 
01733             // Text Highlight
01734             bool beginHighlight = false;
01735             Vector3 highlight_start = Vector3.zero;
01736             Vector3 highlight_end = Vector3.zero;
01737 
01738             m_fontColor32 = m_fontColor;
01739             Color32 vertexColor;
01740             m_htmlColor = m_fontColor32;
01741             m_underlineColor = m_htmlColor;
01742             m_strikethroughColor = m_htmlColor;
01743 
01744             m_colorStack.SetDefault(m_htmlColor);
01745             m_underlineColorStack.SetDefault(m_htmlColor);
01746             m_strikethroughColorStack.SetDefault(m_htmlColor);
01747             m_highlightColorStack.SetDefault(m_htmlColor);
01748 
01749             m_colorGradientPreset = null;
01750             m_colorGradientStack.SetDefault(null);
01751 
01752             // Clear the Style stack.
01753             //m_styleStack.Clear();
01754 
01755             // Clear the Action stack.
01756             m_actionStack.Clear();
01757 
01758             m_isFXMatrixSet = false;
01759 
01760             m_lineOffset = 0; // Amount of space between lines (font line spacing + m_linespacing).
01761             m_lineHeight = TMP_Math.FLOAT_UNSET;
01762             float lineGap = m_currentFontAsset.fontInfo.LineHeight - (m_currentFontAsset.fontInfo.Ascender - m_currentFontAsset.fontInfo.Descender);
01763 
01764             m_cSpacing = 0; // Amount of space added between characters as a result of the use of the <cspace> tag.
01765             m_monoSpacing = 0;
01766             float lineOffsetDelta = 0;
01767             m_xAdvance = 0; // Used to track the position of each character.
01768 
01769             tag_LineIndent = 0; // Used for indentation of text.
01770             tag_Indent = 0;
01771             m_indentStack.SetDefault(0);
01772             tag_NoParsing = false;
01773             //m_isIgnoringAlignment = false;
01774 
01775             m_characterCount = 0; // Total characters in the char[]
01776 
01777             // Tracking of line information
01778             m_firstCharacterOfLine = 0;
01779             m_lastCharacterOfLine = 0;
01780             m_firstVisibleCharacterOfLine = 0;
01781             m_lastVisibleCharacterOfLine = 0;
01782             m_maxLineAscender = k_LargeNegativeFloat;
01783             m_maxLineDescender = k_LargePositiveFloat;
01784             m_lineNumber = 0;
01785             m_lineVisibleCharacterCount = 0;
01786             bool isStartOfNewLine = true;
01787             m_firstOverflowCharacterIndex = -1;
01788 
01789             m_pageNumber = 0;
01790             int pageToDisplay = Mathf.Clamp(m_pageToDisplay - 1, 0, m_textInfo.pageInfo.Length - 1);
01791             int previousPageOverflowChar = 0;
01792 
01793             int ellipsisIndex = 0;
01794 
01795             Vector4 margins = m_margin;
01796             float marginWidth = m_marginWidth;
01797             float marginHeight = m_marginHeight;
01798             m_marginLeft = 0;
01799             m_marginRight = 0;
01800             m_width = -1;
01801             float width = marginWidth + 0.0001f - m_marginLeft - m_marginRight;
01802 
01803             // Need to initialize these Extents structures
01804             m_meshExtents.min = k_LargePositiveVector2;
01805             m_meshExtents.max = k_LargeNegativeVector2;
01806 
01807             // Initialize lineInfo
01808             m_textInfo.ClearLineInfo();
01809 
01810             // Tracking of the highest Ascender
01811             m_maxCapHeight = 0;
01812             m_maxAscender = 0;
01813             m_maxDescender = 0;
01814             float pageAscender = 0;
01815             float maxVisibleDescender = 0;
01816             bool isMaxVisibleDescenderSet = false;
01817             m_isNewPage = false;
01818 
01819             // Initialize struct to track states of word wrapping
01820             bool isFirstWord = true;
01821             m_isNonBreakingSpace = false;
01822             bool ignoreNonBreakingSpace = false;
01823             bool isLastBreakingChar = false;
01824             float linebreakingWidth = 0;
01825             int wrappingIndex = 0;
01826 
01827             // Save character and line state before we begin layout.
01828             SaveWordWrappingState(ref m_SavedWordWrapState, -1, -1);
01829             SaveWordWrappingState(ref m_SavedLineState, -1, -1);
01830 
01831             loopCountA += 1;
01832             //Profiler.EndSample();
01833 
01834             #if TMP_PROFILE_PHASES_ON
01835             Profiler.BeginSample("TMP Generate Text - Phase I");
01836             #endif
01837 
01838             int endTagIndex = 0;
01839             // Parse through Character buffer to read HTML tags and begin creating mesh.
01840             for (int i = 0; i < m_char_buffer.Length && m_char_buffer[i] != 0; i++)
01841             {
01842                 charCode = m_char_buffer[i];
01843                 
01844 
01845                 
01846 
01847                 // Parse Rich Text Tag
01848                 #region Parse Rich Text Tag
01849                 if (m_isRichText && charCode == 60)  // '<'
01850                 {
01851                     m_isParsingText = true;
01852                     m_textElementType = TMP_TextElementType.Character;
01853 
01854                     // Check if Tag is valid. If valid, skip to the end of the validated tag.
01855                     if (ValidateHtmlTag(m_char_buffer, i + 1, out endTagIndex))
01856                     {
01857                         i = endTagIndex;
01858 
01859                         // Continue to next character or handle the sprite element
01860                         if (m_textElementType == TMP_TextElementType.Character)
01861                             continue;
01862                     }
01863                 }
01864                 else
01865                 {
01866                     m_textElementType = m_textInfo.characterInfo[m_characterCount].elementType;
01867                     m_currentMaterialIndex = m_textInfo.characterInfo[m_characterCount].materialReferenceIndex;
01868                     m_currentFontAsset = m_textInfo.characterInfo[m_characterCount].fontAsset;
01869                     //m_currentFontAsset = m_materialReferences[m_currentMaterialIndex].fontAsset;
01870                 }
01871                 #endregion End Parse Rich Text Tag
01872 
01873                 int prev_MaterialIndex = m_currentMaterialIndex;
01874                 bool isUsingAltTypeface = m_textInfo.characterInfo[m_characterCount].isUsingAlternateTypeface;
01875 
01876                 m_isParsingText = false;
01877 
01878                 // When using Linked text, mark character as ignored and skip to next character.
01879                 if (m_characterCount < m_firstVisibleCharacter)
01880                 {
01881                     m_textInfo.characterInfo[m_characterCount].isVisible = false;
01882                     m_textInfo.characterInfo[m_characterCount].character = (char)0x200B;
01883                     m_characterCount += 1;
01884                     continue;
01885                 }
01886 
01887                 // Handle Font Styles like LowerCase, UpperCase and SmallCaps.
01888                 #region Handling of LowerCase, UpperCase and SmallCaps Font Styles
01889                 #if TMP_PROFILE_ON
01890                 Profiler.BeginSample("Handle Font Style");
01891                 #endif
01892                 float smallCapsMultiplier = 1.0f;
01893 
01894                 if (m_textElementType == TMP_TextElementType.Character)
01895                 {
01896                     if ((m_style & FontStyles.UpperCase) == FontStyles.UpperCase)
01897                     {
01898                         // If this character is lowercase, switch to uppercase.
01899                         if (char.IsLower((char)charCode))
01900                             charCode = char.ToUpper((char)charCode);
01901 
01902                     }
01903                     else if ((m_style & FontStyles.LowerCase) == FontStyles.LowerCase)
01904                     {
01905                         // If this character is uppercase, switch to lowercase.
01906                         if (char.IsUpper((char)charCode))
01907                             charCode = char.ToLower((char)charCode);
01908                     }
01909                     else if ((m_fontStyle & FontStyles.SmallCaps) == FontStyles.SmallCaps || (m_style & FontStyles.SmallCaps) == FontStyles.SmallCaps)
01910                     {
01911                         if (char.IsLower((char)charCode))
01912                         {
01913                             smallCapsMultiplier = 0.8f;
01914                             charCode = char.ToUpper((char)charCode);
01915                         }
01916                     }
01917                 }
01918                 #if TMP_PROFILE_ON
01919                 Profiler.EndSample();
01920                 #endif
01921                 #endregion
01922 
01923 
01924                 // Look up Character Data from Dictionary and cache it.
01925                 #region Look up Character Data
01926                 #if TMP_PROFILE_ON
01927                 Profiler.BeginSample("Lookup Character Data");
01928                 #endif
01929                 if (m_textElementType == TMP_TextElementType.Sprite)
01930                 {
01931                     // If a sprite is used as a fallback then get a reference to it and set the color to white.
01932                     m_currentSpriteAsset = m_textInfo.characterInfo[m_characterCount].spriteAsset;
01933                     m_spriteIndex = m_textInfo.characterInfo[m_characterCount].spriteIndex;
01934 
01935                     TMP_Sprite sprite = m_currentSpriteAsset.spriteInfoList[m_spriteIndex];
01936                     if (sprite == null) continue;
01937 
01938                     // Sprites are assigned in the E000 Private Area + sprite Index
01939                     if (charCode == 60)
01940                         charCode = 57344 + m_spriteIndex;
01941                     else
01942                         m_spriteColor = s_colorWhite;
01943 
01944                     // The sprite scale calculations are based on the font asset assigned to the text object.
01945                     float spriteScale = (m_currentFontSize / m_currentFontAsset.fontInfo.PointSize * m_currentFontAsset.fontInfo.Scale);
01946                     currentElementScale = m_currentFontAsset.fontInfo.Ascender / sprite.height * sprite.scale * spriteScale;
01947 
01948                     m_cached_TextElement = sprite;
01949 
01950                     m_textInfo.characterInfo[m_characterCount].elementType = TMP_TextElementType.Sprite;
01951                     m_textInfo.characterInfo[m_characterCount].scale = spriteScale;
01952                     m_textInfo.characterInfo[m_characterCount].spriteAsset = m_currentSpriteAsset;
01953                     m_textInfo.characterInfo[m_characterCount].fontAsset = m_currentFontAsset;
01954                     m_textInfo.characterInfo[m_characterCount].materialReferenceIndex = m_currentMaterialIndex;
01955 
01956                     m_currentMaterialIndex = prev_MaterialIndex;
01957 
01958                     padding = 0;
01959                 }
01960                 else if (m_textElementType == TMP_TextElementType.Character)
01961                 {
01962                     m_cached_TextElement = m_textInfo.characterInfo[m_characterCount].textElement;
01963                     if (m_cached_TextElement == null) continue;
01964 
01965                     m_currentFontAsset = m_textInfo.characterInfo[m_characterCount].fontAsset;
01966                     m_currentMaterial = m_textInfo.characterInfo[m_characterCount].material;
01967                     m_currentMaterialIndex = m_textInfo.characterInfo[m_characterCount].materialReferenceIndex;
01968 
01969                     // Re-calculate font scale as the font asset may have changed.
01970                     m_fontScale = m_currentFontSize * smallCapsMultiplier / m_currentFontAsset.fontInfo.PointSize * m_currentFontAsset.fontInfo.Scale;
01971 
01972                     currentElementScale = m_fontScale * m_fontScaleMultiplier * m_cached_TextElement.scale;
01973 
01974                     m_textInfo.characterInfo[m_characterCount].elementType = TMP_TextElementType.Character;
01975                     m_textInfo.characterInfo[m_characterCount].scale = currentElementScale;
01976 
01977                     padding = m_currentMaterialIndex == 0 ? m_padding : m_subTextObjects[m_currentMaterialIndex].padding;
01978                 }
01979                 #if TMP_PROFILE_ON
01980                 Profiler.EndSample();
01981                 #endif
01982                 #endregion
01983 
01984 
01985                 // Handle Soft Hyphen
01986                 #region Handle Soft Hyphen
01987                 float old_scale = currentElementScale;
01988                 if (charCode == 0xAD)
01989                 {
01990                     currentElementScale = 0;
01991                 }
01992                 #endregion
01993 
01994 
01995                 // Store some of the text object's information
01996                 m_textInfo.characterInfo[m_characterCount].character = (char)charCode;
01997                 m_textInfo.characterInfo[m_characterCount].pointSize = m_currentFontSize;
01998                 m_textInfo.characterInfo[m_characterCount].color = m_htmlColor;
01999                 m_textInfo.characterInfo[m_characterCount].underlineColor = m_underlineColor;
02000                 m_textInfo.characterInfo[m_characterCount].strikethroughColor = m_strikethroughColor;
02001                 m_textInfo.characterInfo[m_characterCount].highlightColor = m_highlightColor;
02002                 m_textInfo.characterInfo[m_characterCount].style = m_style;
02003                 m_textInfo.characterInfo[m_characterCount].index = i;
02004                 //m_textInfo.characterInfo[m_characterCount].isIgnoringAlignment = m_isIgnoringAlignment;
02005 
02006 
02007                 // Handle Kerning if Enabled.
02008                 #region Handle Kerning
02009                 GlyphValueRecord glyphAdjustments = new GlyphValueRecord();
02010                 if (m_enableKerning)
02011                 {
02012                     KerningPair adjustmentPair = null;
02013 
02014                     if (m_characterCount < totalCharacterCount - 1)
02015                     {
02016                         uint nextGlyph = m_textInfo.characterInfo[m_characterCount + 1].character;
02017                         KerningPairKey keyValue = new KerningPairKey((uint)charCode, nextGlyph);
02018 
02019                         m_currentFontAsset.kerningDictionary.TryGetValue((int)keyValue.key, out adjustmentPair);
02020                         if (adjustmentPair != null)
02021                             glyphAdjustments = adjustmentPair.firstGlyphAdjustments;
02022                     }
02023 
02024                     if (m_characterCount >= 1)
02025                     {
02026                         uint previousGlyph = m_textInfo.characterInfo[m_characterCount - 1].character;
02027                         KerningPairKey keyValue = new KerningPairKey(previousGlyph, (uint)charCode);
02028 
02029                         m_currentFontAsset.kerningDictionary.TryGetValue((int)keyValue.key, out adjustmentPair);
02030                         if (adjustmentPair != null)
02031                             glyphAdjustments += adjustmentPair.secondGlyphAdjustments;
02032                     }
02033                 }
02034                 #endregion
02035 
02036 
02037                 // Initial Implementation for RTL support.
02038                 #region Handle Right-to-Left
02039                 if (m_isRightToLeft)
02040                 {
02041                     m_xAdvance -= ((m_cached_TextElement.xAdvance * bold_xAdvance_multiplier + m_characterSpacing + m_wordSpacing + m_currentFontAsset.normalSpacingOffset) * currentElementScale + m_cSpacing) * (1 - m_charWidthAdjDelta);
02042 
02043                     if (char.IsWhiteSpace((char)charCode) || charCode == 0x200B)
02044                         m_xAdvance -= m_wordSpacing * currentElementScale;
02045                 }
02046                 #endregion
02047 
02048 
02049                 // Handle Mono Spacing
02050                 #region Handle Mono Spacing
02051                 float monoAdvance = 0;
02052                 if (m_monoSpacing != 0)
02053                 {
02054                     monoAdvance = (m_monoSpacing / 2 - (m_cached_TextElement.width / 2 + m_cached_TextElement.xOffset) * currentElementScale) * (1 - m_charWidthAdjDelta);
02055                     m_xAdvance += monoAdvance;
02056                 }
02057                 #endregion
02058 
02059 
02060                 // Set Padding based on selected font style
02061                 #region Handle Style Padding
02062                 if (m_textElementType == TMP_TextElementType.Character && !isUsingAltTypeface && ((m_style & FontStyles.Bold) == FontStyles.Bold || (m_fontStyle & FontStyles.Bold) == FontStyles.Bold)) // Checks for any combination of Bold Style.
02063                 {
02064                     if (m_currentMaterial.HasProperty(ShaderUtilities.ID_GradientScale))
02065                     {
02066                         float gradientScale = m_currentMaterial.GetFloat(ShaderUtilities.ID_GradientScale);
02067                         style_padding = m_currentFontAsset.boldStyle / 4.0f * gradientScale * m_currentMaterial.GetFloat(ShaderUtilities.ID_ScaleRatio_A);
02068 
02069                         // Clamp overall padding to Gradient Scale size.
02070                         if (style_padding + padding > gradientScale)
02071                             padding = gradientScale - style_padding;
02072                     }
02073                     else
02074                         style_padding = 0;
02075 
02076                     bold_xAdvance_multiplier = 1 + m_currentFontAsset.boldSpacing * 0.01f;
02077                 }
02078                 else
02079                 {
02080                     if (m_currentMaterial.HasProperty(ShaderUtilities.ID_GradientScale))
02081                     {
02082                         float gradientScale = m_currentMaterial.GetFloat(ShaderUtilities.ID_GradientScale);
02083                         style_padding = m_currentFontAsset.normalStyle / 4.0f * gradientScale * m_currentMaterial.GetFloat(ShaderUtilities.ID_ScaleRatio_A);
02084 
02085                         // Clamp overall padding to Gradient Scale size.
02086                         if (style_padding + padding > gradientScale)
02087                             padding = gradientScale - style_padding;
02088                     }
02089                     else
02090                         style_padding = 0;
02091 
02092                     bold_xAdvance_multiplier = 1.0f;
02093                 }
02094                 #endregion Handle Style Padding
02095 
02096 
02097                 // Determine the position of the vertices of the Character or Sprite.
02098                 #region Calculate Vertices Position
02099                 #if TMP_PROFILE_ON
02100                 Profiler.BeginSample("Calculate Vertices Position");
02101                 #endif
02102                 float fontBaseLineOffset = m_currentFontAsset.fontInfo.Baseline * m_fontScale * m_fontScaleMultiplier * m_currentFontAsset.fontInfo.Scale;
02103                 Vector3 top_left;
02104                 top_left.x = m_xAdvance + ((m_cached_TextElement.xOffset - padding - style_padding + glyphAdjustments.xPlacement) * currentElementScale * (1 - m_charWidthAdjDelta));
02105                 top_left.y = fontBaseLineOffset + (m_cached_TextElement.yOffset + padding + glyphAdjustments.yPlacement) * currentElementScale - m_lineOffset + m_baselineOffset;
02106                 top_left.z = 0;
02107 
02108                 Vector3 bottom_left;
02109                 bottom_left.x = top_left.x;
02110                 bottom_left.y = top_left.y - ((m_cached_TextElement.height + padding * 2) * currentElementScale);
02111                 bottom_left.z = 0;
02112 
02113                 Vector3 top_right;
02114                 top_right.x = bottom_left.x + ((m_cached_TextElement.width + padding * 2 + style_padding * 2) * currentElementScale * (1 - m_charWidthAdjDelta));
02115                 top_right.y = top_left.y;
02116                 top_right.z = 0;
02117 
02118                 Vector3 bottom_right;
02119                 bottom_right.x = top_right.x;
02120                 bottom_right.y = bottom_left.y;
02121                 bottom_right.z = 0;
02122 
02123                 #if TMP_PROFILE_ON
02124                 Profiler.EndSample();
02125                 #endif
02126                 #endregion
02127 
02128 
02129                 // Check if we need to Shear the rectangles for Italic styles
02130                 #region Handle Italic & Shearing
02131                 if (m_textElementType == TMP_TextElementType.Character && !isUsingAltTypeface && ((m_style & FontStyles.Italic) == FontStyles.Italic || (m_fontStyle & FontStyles.Italic) == FontStyles.Italic))
02132                 {
02133                     // Shift Top vertices forward by half (Shear Value * height of character) and Bottom vertices back by same amount. 
02134                     float shear_value = m_currentFontAsset.italicStyle * 0.01f;
02135                     Vector3 topShear = new Vector3(shear_value * ((m_cached_TextElement.yOffset + padding + style_padding) * currentElementScale), 0, 0);
02136                     Vector3 bottomShear = new Vector3(shear_value * (((m_cached_TextElement.yOffset - m_cached_TextElement.height - padding - style_padding)) * currentElementScale), 0, 0);
02137 
02138                     top_left = top_left + topShear;
02139                     bottom_left = bottom_left + bottomShear;
02140                     top_right = top_right + topShear;
02141                     bottom_right = bottom_right + bottomShear;
02142                 }
02143                 #endregion Handle Italics & Shearing
02144 
02145 
02146                 // Handle Character Rotation
02147                 #region Handle Character Rotation
02148                 if (m_isFXMatrixSet)
02149                 {
02150                     // Apply scale matrix when simulating Condensed text.
02151                     if (m_FXMatrix.m00 != 1)
02152                     {
02153                         //top_left = m_FXMatrix.MultiplyPoint3x4(top_left);
02154                         //bottom_left = m_FXMatrix.MultiplyPoint3x4(bottom_left);
02155                         //top_right = m_FXMatrix.MultiplyPoint3x4(top_right);
02156                         //bottom_right = m_FXMatrix.MultiplyPoint3x4(bottom_right);
02157                     }
02158 
02159                     Vector3 positionOffset = (top_right + bottom_left) / 2;
02160 
02161                     top_left = m_FXMatrix.MultiplyPoint3x4(top_left - positionOffset) + positionOffset;
02162                     bottom_left = m_FXMatrix.MultiplyPoint3x4(bottom_left - positionOffset) + positionOffset;
02163                     top_right = m_FXMatrix.MultiplyPoint3x4(top_right - positionOffset) + positionOffset;
02164                     bottom_right = m_FXMatrix.MultiplyPoint3x4(bottom_right - positionOffset) + positionOffset;
02165                 }
02166                 #endregion
02167 
02168 
02169                 // Store vertex information for the character or sprite.
02170                 m_textInfo.characterInfo[m_characterCount].bottomLeft = bottom_left;
02171                 m_textInfo.characterInfo[m_characterCount].topLeft = top_left;
02172                 m_textInfo.characterInfo[m_characterCount].topRight = top_right;
02173                 m_textInfo.characterInfo[m_characterCount].bottomRight = bottom_right;
02174 
02175                 m_textInfo.characterInfo[m_characterCount].origin = m_xAdvance;
02176                 m_textInfo.characterInfo[m_characterCount].baseLine = fontBaseLineOffset - m_lineOffset + m_baselineOffset;
02177                 m_textInfo.characterInfo[m_characterCount].aspectRatio = (top_right.x - bottom_left.x) / (top_left.y - bottom_left.y);
02178 
02179 
02180                 // Compute and save text element Ascender and maximum line Ascender.
02181                 float elementAscender = m_currentFontAsset.fontInfo.Ascender * (m_textElementType == TMP_TextElementType.Character ? currentElementScale / smallCapsMultiplier : m_textInfo.characterInfo[m_characterCount].scale) + m_baselineOffset;
02182                 m_textInfo.characterInfo[m_characterCount].ascender = elementAscender - m_lineOffset;
02183                 m_maxLineAscender = elementAscender > m_maxLineAscender ? elementAscender : m_maxLineAscender;
02184 
02185                 // Compute and save text element Descender and maximum line Descender.
02186                 float elementDescender = m_currentFontAsset.fontInfo.Descender * (m_textElementType == TMP_TextElementType.Character ? currentElementScale / smallCapsMultiplier : m_textInfo.characterInfo[m_characterCount].scale) + m_baselineOffset;
02187                 float elementDescenderII = m_textInfo.characterInfo[m_characterCount].descender = elementDescender - m_lineOffset;
02188                 m_maxLineDescender = elementDescender < m_maxLineDescender ? elementDescender : m_maxLineDescender;
02189 
02190                 // Adjust maxLineAscender and maxLineDescender if style is superscript or subscript
02191                 if ((m_style & FontStyles.Subscript) == FontStyles.Subscript || (m_style & FontStyles.Superscript) == FontStyles.Superscript)
02192                 {
02193                     float baseAscender = (elementAscender - m_baselineOffset) / m_currentFontAsset.fontInfo.SubSize;
02194                     elementAscender = m_maxLineAscender;
02195                     m_maxLineAscender = baseAscender > m_maxLineAscender ? baseAscender : m_maxLineAscender;
02196 
02197                     float baseDescender = (elementDescender - m_baselineOffset) / m_currentFontAsset.fontInfo.SubSize;
02198                     elementDescender = m_maxLineDescender;
02199                     m_maxLineDescender = baseDescender < m_maxLineDescender ? baseDescender : m_maxLineDescender;
02200                 }
02201 
02202                 if (m_lineNumber == 0 || m_isNewPage)
02203                 {
02204                     m_maxAscender = m_maxAscender > elementAscender ? m_maxAscender : elementAscender;
02205                     m_maxCapHeight = Mathf.Max(m_maxCapHeight, m_currentFontAsset.fontInfo.CapHeight * currentElementScale / smallCapsMultiplier);
02206                 }
02207                 if (m_lineOffset == 0) pageAscender = pageAscender > elementAscender ? pageAscender : elementAscender;
02208 
02209 
02210                 // Set Characters to not visible by default.
02211                 m_textInfo.characterInfo[m_characterCount].isVisible = false;
02212 
02213                 // Setup Mesh for visible text elements. ie. not a SPACE / LINEFEED / CARRIAGE RETURN.
02214                 #region Handle Visible Characters
02215                 //#if TMP_PROFILE_ON
02216                 //Profiler.BeginSample("Handle Visible Characters");
02217                 //#endif
02218                 if (charCode == 9 || charCode == 0xA0 || charCode == 0x2007 || (!char.IsWhiteSpace((char)charCode) && charCode != 0x200B) || m_textElementType == TMP_TextElementType.Sprite)
02219                 {
02220                     m_textInfo.characterInfo[m_characterCount].isVisible = true;
02221 
02222                     #region Experimental Margin Shaper
02223                     //Vector2 shapedMargins;
02224                     //if (marginShaper)
02225                     //{
02226                     //    shapedMargins = m_marginShaper.GetShapedMargins(m_textInfo.characterInfo[m_characterCount].baseLine);
02227                     //    if (shapedMargins.x < margins.x)
02228                     //    {
02229                     //        shapedMargins.x = m_marginLeft;
02230                     //    }
02231                     //    else
02232                     //    {
02233                     //        shapedMargins.x += m_marginLeft - margins.x;
02234                     //    }
02235                     //    if (shapedMargins.y < margins.z)
02236                     //    {
02237                     //        shapedMargins.y = m_marginRight;
02238                     //    }
02239                     //    else
02240                     //    {
02241                     //        shapedMargins.y += m_marginRight - margins.z;
02242                     //    }
02243                     //}
02244                     //else
02245                     //{
02246                     //    shapedMargins.x = m_marginLeft;
02247                     //    shapedMargins.y = m_marginRight;
02248                     //}
02249                     //width = marginWidth + 0.0001f - shapedMargins.x - shapedMargins.y;
02250                     //if (m_width != -1 && m_width < width)
02251                     //{
02252                     //    width = m_width;
02253                     //}
02254                     //m_textInfo.lineInfo[m_lineNumber].marginLeft = shapedMargins.x;
02255                     #endregion
02256 
02257                     width = m_width != -1 ? Mathf.Min(marginWidth + 0.0001f - m_marginLeft - m_marginRight, m_width) : marginWidth + 0.0001f - m_marginLeft - m_marginRight;
02258                     m_textInfo.lineInfo[m_lineNumber].marginLeft = m_marginLeft;
02259 
02260                     bool isJustifiedOrFlush = ((_HorizontalAlignmentOptions)m_lineJustification & _HorizontalAlignmentOptions.Flush) == _HorizontalAlignmentOptions.Flush || ((_HorizontalAlignmentOptions)m_lineJustification & _HorizontalAlignmentOptions.Justified) == _HorizontalAlignmentOptions.Justified;
02261 
02262                     // Calculate the line breaking width of the text.
02263                     linebreakingWidth = Mathf.Abs(m_xAdvance) + (!m_isRightToLeft ? m_cached_TextElement.xAdvance : 0) * (1 - m_charWidthAdjDelta) * (charCode != 0xAD ? currentElementScale : old_scale);
02264 
02265                     // Check if Character exceeds the width of the Text Container
02266                     #region Handle Line Breaking, Text Auto-Sizing and Horizontal Overflow
02267                     if (linebreakingWidth > width * (isJustifiedOrFlush ? 1.05f : 1.0f))
02268                     {
02269                         ellipsisIndex = m_characterCount - 1; // Last safely rendered character
02270 
02271                         // Word Wrapping
02272                         #region Handle Word Wrapping
02273                         if (enableWordWrapping && m_characterCount != m_firstCharacterOfLine)
02274                         {
02275                             // Check if word wrapping is still possible
02276                             #region Line Breaking Check
02277                             if (wrappingIndex == m_SavedWordWrapState.previous_WordBreak || isFirstWord)
02278                             {
02279                                 // Word wrapping is no longer possible. Shrink size of text if auto-sizing is enabled.
02280                                 if (m_enableAutoSizing && m_fontSize > m_fontSizeMin)
02281                                 {
02282                                     // Handle Character Width Adjustments
02283                                     #region Character Width Adjustments
02284                                     if (m_charWidthAdjDelta < m_charWidthMaxAdj / 100)
02285                                     {
02286                                         loopCountA = 0;
02287                                         m_charWidthAdjDelta += 0.01f;
02288                                         GenerateTextMesh();
02289                                         return;
02290                                     }
02291                                     #endregion
02292 
02293                                     // Adjust Point Size
02294                                     m_maxFontSize = m_fontSize;
02295 
02296                                     m_fontSize -= Mathf.Max((m_fontSize - m_minFontSize) / 2, 0.05f);
02297                                     m_fontSize = (int)(Mathf.Max(m_fontSize, m_fontSizeMin) * 20 + 0.5f) / 20f;
02298 
02299                                     if (loopCountA > 20) return; // Added to debug
02300                                     GenerateTextMesh();
02301                                     return;
02302                                 }
02303 
02304                                 // Word wrapping is no longer possible, now breaking up individual words.
02305                                 if (m_isCharacterWrappingEnabled == false)
02306                                 {
02307                                     if (ignoreNonBreakingSpace == false)
02308                                         ignoreNonBreakingSpace = true;
02309                                     else
02310                                         m_isCharacterWrappingEnabled = true;
02311                                 }
02312                                 else
02313                                     isLastBreakingChar = true;
02314 
02315                                 //m_recursiveCount += 1;
02316                                 //if (m_recursiveCount > 20)
02317                                 //{
02318                                 //    Debug.Log("Recursive count exceeded!");
02319                                 //    continue;
02320                                 //}
02321                             }
02322                             #endregion
02323 
02324                             // Restore to previously stored state of last valid (space character or linefeed)
02325                             i = RestoreWordWrappingState(ref m_SavedWordWrapState);
02326                             wrappingIndex = i;  // Used to detect when line length can no longer be reduced.
02327 
02328                             // Handling for Soft Hyphen
02329                             if (m_char_buffer[i] == 0xAD) // && !m_isCharacterWrappingEnabled) // && ellipsisIndex != i && !m_isCharacterWrappingEnabled)
02330                             {
02331                                 m_isTextTruncated = true;
02332                                 m_char_buffer[i] = 0x2D;
02333                                 GenerateTextMesh();
02334                                 return;
02335                             }
02336 
02337                             //Debug.Log("Last Visible Character of line # " + m_lineNumber + " is [" + m_textInfo.characterInfo[m_lastVisibleCharacterOfLine].character + " Character Count: " + m_characterCount + " Last visible: " + m_lastVisibleCharacterOfLine);
02338 
02339                             // Check if Line Spacing of previous line needs to be adjusted.
02340                             if (m_lineNumber > 0 && !TMP_Math.Approximately(m_maxLineAscender, m_startOfLineAscender) && m_lineHeight == TMP_Math.FLOAT_UNSET && !m_isNewPage)
02341                             {
02342                                 //Debug.Log("(Line Break - Adjusting Line Spacing on line #" + m_lineNumber);
02343                                 float offsetDelta = m_maxLineAscender - m_startOfLineAscender;
02344                                 AdjustLineOffset(m_firstCharacterOfLine, m_characterCount, offsetDelta);
02345                                 m_lineOffset += offsetDelta;
02346                                 m_SavedWordWrapState.lineOffset = m_lineOffset;
02347                                 m_SavedWordWrapState.previousLineAscender = m_maxLineAscender;
02348 
02349                                 // TODO - Add check for character exceeding vertical bounds
02350                             }
02351                             m_isNewPage = false;
02352 
02353                             // Calculate lineAscender & make sure if last character is superscript or subscript that we check that as well.
02354                             float lineAscender = m_maxLineAscender - m_lineOffset;
02355                             float lineDescender = m_maxLineDescender - m_lineOffset;
02356 
02357 
02358                             // Update maxDescender and maxVisibleDescender
02359                             m_maxDescender = m_maxDescender < lineDescender ? m_maxDescender : lineDescender;
02360                             if (!isMaxVisibleDescenderSet)
02361                                 maxVisibleDescender = m_maxDescender;
02362 
02363                             if (m_useMaxVisibleDescender && (m_characterCount >= m_maxVisibleCharacters || m_lineNumber >= m_maxVisibleLines))
02364                                 isMaxVisibleDescenderSet = true;
02365 
02366                             // Track & Store lineInfo for the new line
02367                             m_textInfo.lineInfo[m_lineNumber].firstCharacterIndex = m_firstCharacterOfLine;
02368                             m_textInfo.lineInfo[m_lineNumber].firstVisibleCharacterIndex = m_firstVisibleCharacterOfLine = m_firstCharacterOfLine > m_firstVisibleCharacterOfLine ? m_firstCharacterOfLine : m_firstVisibleCharacterOfLine;
02369                             m_textInfo.lineInfo[m_lineNumber].lastCharacterIndex = m_lastCharacterOfLine = m_characterCount - 1 > 0 ? m_characterCount - 1 : 0;
02370                             m_textInfo.lineInfo[m_lineNumber].lastVisibleCharacterIndex = m_lastVisibleCharacterOfLine = m_lastVisibleCharacterOfLine < m_firstVisibleCharacterOfLine ? m_firstVisibleCharacterOfLine : m_lastVisibleCharacterOfLine;
02371 
02372                             m_textInfo.lineInfo[m_lineNumber].characterCount = m_textInfo.lineInfo[m_lineNumber].lastCharacterIndex - m_textInfo.lineInfo[m_lineNumber].firstCharacterIndex + 1;
02373                             m_textInfo.lineInfo[m_lineNumber].visibleCharacterCount = m_lineVisibleCharacterCount;
02374                             m_textInfo.lineInfo[m_lineNumber].lineExtents.min = new Vector2(m_textInfo.characterInfo[m_firstVisibleCharacterOfLine].bottomLeft.x, lineDescender);
02375                             m_textInfo.lineInfo[m_lineNumber].lineExtents.max = new Vector2(m_textInfo.characterInfo[m_lastVisibleCharacterOfLine].topRight.x, lineAscender);
02376                             m_textInfo.lineInfo[m_lineNumber].length = m_textInfo.lineInfo[m_lineNumber].lineExtents.max.x;
02377                             m_textInfo.lineInfo[m_lineNumber].width = width;
02378 
02379                             //m_textInfo.lineInfo[m_lineNumber].alignment = m_lineJustification;
02380 
02381                             m_textInfo.lineInfo[m_lineNumber].maxAdvance = m_textInfo.characterInfo[m_lastVisibleCharacterOfLine].xAdvance - (m_characterSpacing + m_currentFontAsset.normalSpacingOffset) * currentElementScale - m_cSpacing;
02382 
02383                             m_textInfo.lineInfo[m_lineNumber].baseline = 0 - m_lineOffset;
02384                             m_textInfo.lineInfo[m_lineNumber].ascender = lineAscender;
02385                             m_textInfo.lineInfo[m_lineNumber].descender = lineDescender;
02386                             m_textInfo.lineInfo[m_lineNumber].lineHeight = lineAscender - lineDescender + lineGap * baseScale;
02387 
02388                             m_firstCharacterOfLine = m_characterCount; // Store first character of the next line.
02389                             m_lineVisibleCharacterCount = 0;
02390 
02391                             // Store the state of the line before starting on the new line.
02392                             SaveWordWrappingState(ref m_SavedLineState, i, m_characterCount - 1);
02393 
02394                             m_lineNumber += 1;
02395                             isStartOfNewLine = true;
02396                             isFirstWord = true;
02397 
02398                             // Check to make sure Array is large enough to hold a new line.
02399                             if (m_lineNumber >= m_textInfo.lineInfo.Length)
02400                                 ResizeLineExtents(m_lineNumber);
02401 
02402                             // Apply Line Spacing based on scale of the last character of the line.
02403                             if (m_lineHeight == TMP_Math.FLOAT_UNSET)
02404                             {
02405                                 float ascender = m_textInfo.characterInfo[m_characterCount].ascender - m_textInfo.characterInfo[m_characterCount].baseLine;
02406                                 lineOffsetDelta = 0 - m_maxLineDescender + ascender + (lineGap + m_lineSpacing + m_lineSpacingDelta) * baseScale;
02407                                 m_lineOffset += lineOffsetDelta;
02408 
02409                                 m_startOfLineAscender = ascender;
02410                             }
02411                             else
02412                                 m_lineOffset += m_lineHeight + m_lineSpacing * baseScale;
02413 
02414                             m_maxLineAscender = k_LargeNegativeFloat;
02415                             m_maxLineDescender = k_LargePositiveFloat;
02416 
02417                             m_xAdvance = 0 + tag_Indent;
02418 
02419                             continue;
02420                         }
02421                         #endregion End Word Wrapping
02422 
02423 
02424                         // Text Auto-Sizing (text exceeding Width of container. 
02425                         #region Handle Text Auto-Sizing
02426                         if (m_enableAutoSizing && m_fontSize > m_fontSizeMin)
02427                         {
02428                             // Handle Character Width Adjustments
02429                             #region Character Width Adjustments
02430                             if (m_charWidthAdjDelta < m_charWidthMaxAdj / 100)
02431                             {
02432                                 loopCountA = 0;
02433                                 m_charWidthAdjDelta += 0.01f;
02434                                 GenerateTextMesh();
02435                                 return;
02436                             }
02437                             #endregion
02438 
02439                             // Adjust Point Size
02440                             m_maxFontSize = m_fontSize;
02441 
02442                             m_fontSize -= Mathf.Max((m_fontSize - m_minFontSize) / 2, 0.05f);
02443                             m_fontSize = (int)(Mathf.Max(m_fontSize, m_fontSizeMin) * 20 + 0.5f) / 20f;
02444 
02445                             //m_recursiveCount = 0;
02446                             if (loopCountA > 20) return; // Added to debug 
02447                             GenerateTextMesh();
02448                             return;
02449                         }
02450                         #endregion End Text Auto-Sizing
02451 
02452 
02453                         // Handle Text Overflow
02454                         #region Handle Text Overflow
02455                         switch (m_overflowMode)
02456                         {
02457                             case TextOverflowModes.Overflow:
02458                                 if (m_isMaskingEnabled)
02459                                     DisableMasking();
02460 
02461                                 break;
02462                             case TextOverflowModes.Ellipsis:
02463                                 if (m_isMaskingEnabled)
02464                                     DisableMasking();
02465 
02466                                 m_isTextTruncated = true;
02467 
02468                                 if (m_characterCount < 1)
02469                                 {
02470                                     m_textInfo.characterInfo[m_characterCount].isVisible = false;
02471                                     //m_visibleCharacterCount = 0;
02472                                     break;
02473                                 }
02474 
02475                                 m_char_buffer[i - 1] = 8230;
02476                                 m_char_buffer[i] = (char)0;
02477 
02478                                 if (m_cached_Ellipsis_GlyphInfo != null)
02479                                 {
02480                                     m_textInfo.characterInfo[ellipsisIndex].character = (char)8230;
02481                                     m_textInfo.characterInfo[ellipsisIndex].textElement = m_cached_Ellipsis_GlyphInfo;
02482                                     m_textInfo.characterInfo[ellipsisIndex].fontAsset = m_materialReferences[0].fontAsset;
02483                                     m_textInfo.characterInfo[ellipsisIndex].material = m_materialReferences[0].material;
02484                                     m_textInfo.characterInfo[ellipsisIndex].materialReferenceIndex = 0;
02485                                 }
02486                                 else
02487                                 {
02488                                     Debug.LogWarning("Unable to use Ellipsis character since it wasn't found in the current Font Asset [" + m_fontAsset.name + "]. Consider regenerating this font asset to include the Ellipsis character (u+2026).\nNote: Warnings can be disabled in the TMP Settings file.", this);
02489                                 }
02490 
02491                                 m_totalCharacterCount = ellipsisIndex + 1;
02492 
02493                                 GenerateTextMesh();
02494                                 return;
02495                             case TextOverflowModes.Masking:
02496                                 if (!m_isMaskingEnabled)
02497                                     EnableMasking();
02498                                 break;
02499                             case TextOverflowModes.ScrollRect:
02500                                 if (!m_isMaskingEnabled)
02501                                     EnableMasking();
02502                                 break;
02503                             case TextOverflowModes.Truncate:
02504                                 if (m_isMaskingEnabled)
02505                                     DisableMasking();
02506 
02507                                 m_textInfo.characterInfo[m_characterCount].isVisible = false;
02508                                 break;
02509                             case TextOverflowModes.Linked:
02510                                 //m_textInfo.characterInfo[m_characterCount].isVisible = false;
02511 
02512                                 //if (m_linkedTextComponent != null)
02513                                 //{
02514                                 //    m_linkedTextComponent.text = text;
02515                                 //    m_linkedTextComponent.firstVisibleCharacter = m_characterCount;
02516                                 //    m_linkedTextComponent.ForceMeshUpdate();
02517                                 //}
02518                                 break;
02519                         }
02520                         #endregion End Text Overflow
02521 
02522                     }
02523                     #endregion End Check for Characters Exceeding Width of Text Container
02524 
02525 
02526                     // Special handling of characters that are not ignored at the end of a line.
02527                     if (charCode == 9 || charCode == 0xA0 || charCode == 0x2007)
02528                     {
02529                         m_textInfo.characterInfo[m_characterCount].isVisible = false;
02530                         m_lastVisibleCharacterOfLine = m_characterCount;
02531                         m_textInfo.lineInfo[m_lineNumber].spaceCount += 1;
02532                         m_textInfo.spaceCount += 1;
02533 
02534                         if (charCode == 0xA0)
02535                             m_textInfo.lineInfo[m_lineNumber].controlCharacterCount += 1;
02536                     }
02537                     else
02538                     {
02539                         // Determine Vertex Color
02540                         if (m_overrideHtmlColors)
02541                             vertexColor = m_fontColor32;
02542                         else
02543                             vertexColor = m_htmlColor;
02544 
02545                         // Store Character & Sprite Vertex Information
02546                         if (m_textElementType == TMP_TextElementType.Character)
02547                         {
02548                             // Save Character Vertex Data
02549                             SaveGlyphVertexInfo(padding, style_padding, vertexColor);
02550                         }
02551                         else if (m_textElementType == TMP_TextElementType.Sprite)
02552                         {
02553                             SaveSpriteVertexInfo(vertexColor);
02554                         }
02555                     }
02556 
02557 
02558                     // Increase visible count for Characters.
02559                     if (m_textInfo.characterInfo[m_characterCount].isVisible && charCode != 0xAD)
02560                     {
02561                         if (isStartOfNewLine) { isStartOfNewLine = false; m_firstVisibleCharacterOfLine = m_characterCount; }
02562 
02563                         m_lineVisibleCharacterCount += 1;
02564                         m_lastVisibleCharacterOfLine = m_characterCount;
02565                     }
02566                 }
02567                 else
02568                 {   // This is a Space, Tab, LineFeed or Carriage Return
02569 
02570                     // Track # of spaces per line which is used for line justification.
02571                     if ((charCode == 10 || char.IsSeparator((char)charCode)) && charCode != 0xAD && charCode != 0x200B && charCode != 0x2060)
02572                     {
02573                         m_textInfo.lineInfo[m_lineNumber].spaceCount += 1;
02574                         m_textInfo.spaceCount += 1;
02575                     }
02576                 }
02577                 //#if TMP_PROFILE_ON
02578                 //Profiler.EndSample();
02579                 //#endif
02580                 #endregion Handle Visible Characters
02581 
02582 
02583                 // Check if Line Spacing of previous line needs to be adjusted.
02584                 #region Adjust Line Spacing
02585                 #if TMP_PROFILE_ON
02586                 Profiler.BeginSample("Adjust Line Spacing");
02587                 #endif
02588                 if (m_lineNumber > 0 && !TMP_Math.Approximately(m_maxLineAscender, m_startOfLineAscender) && m_lineHeight == TMP_Math.FLOAT_UNSET && !m_isNewPage)
02589                 {
02590                     //Debug.Log("Inline - Adjusting Line Spacing on line #" + m_lineNumber);
02591                     //float gap = 0; // Compute gap.
02592 
02593                     float offsetDelta = m_maxLineAscender - m_startOfLineAscender;
02594                     AdjustLineOffset(m_firstCharacterOfLine, m_characterCount, offsetDelta);
02595                     elementDescenderII -= offsetDelta;
02596                     m_lineOffset += offsetDelta;
02597 
02598                     m_startOfLineAscender += offsetDelta;
02599                     m_SavedWordWrapState.lineOffset = m_lineOffset;
02600                     m_SavedWordWrapState.previousLineAscender = m_startOfLineAscender;
02601                 }
02602                 #if TMP_PROFILE_ON
02603                 Profiler.EndSample();
02604                 #endif
02605                 #endregion
02606 
02607 
02608                 // Store Rectangle positions for each Character.
02609                 #region Store Character Data
02610                 m_textInfo.characterInfo[m_characterCount].lineNumber = m_lineNumber;
02611                 m_textInfo.characterInfo[m_characterCount].pageNumber = m_pageNumber;
02612 
02613                 if (charCode != 10 && charCode != 13 && charCode != 8230 || m_textInfo.lineInfo[m_lineNumber].characterCount == 1)
02614                     m_textInfo.lineInfo[m_lineNumber].alignment = m_lineJustification;
02615                 #endregion Store Character Data
02616 
02617 
02618                 // Check if text Exceeds the vertical bounds of the margin area.
02619                 #region Check Vertical Bounds & Auto-Sizing
02620                 #if TMP_PROFILE_ON
02621                 Profiler.BeginSample("Check Vertical Bounds");
02622                 #endif
02623                 if (m_maxAscender - elementDescenderII > marginHeight + 0.0001f)
02624                 {
02625                     // Handle Line spacing adjustments
02626                     #region Line Spacing Adjustments
02627                     if (m_enableAutoSizing && m_lineSpacingDelta > m_lineSpacingMax && m_lineNumber > 0)
02628                     {
02629                         loopCountA = 0;
02630 
02631                         m_lineSpacingDelta -= 1;
02632                         GenerateTextMesh();
02633                         return;
02634                     }
02635                     #endregion
02636 
02637 
02638                     // Handle Text Auto-sizing resulting from text exceeding vertical bounds.
02639                     #region Text Auto-Sizing (Text greater than vertical bounds)
02640                     if (m_enableAutoSizing && m_fontSize > m_fontSizeMin)
02641                     {
02642                         m_maxFontSize = m_fontSize;
02643 
02644                         m_fontSize -= Mathf.Max((m_fontSize - m_minFontSize) / 2, 0.05f);
02645                         m_fontSize = (int)(Mathf.Max(m_fontSize, m_fontSizeMin) * 20 + 0.5f) / 20f;
02646 
02647                         //m_recursiveCount = 0;
02648                         if (loopCountA > 20) return; // Added to debug 
02649                         GenerateTextMesh();
02650                         return;
02651                     }
02652                     #endregion Text Auto-Sizing
02653 
02654                     // Set isTextOverflowing and firstOverflowCharacterIndex
02655                     if (m_firstOverflowCharacterIndex == -1)
02656                         m_firstOverflowCharacterIndex = m_characterCount;
02657 
02658                     // Handle Text Overflow
02659                     #region Text Overflow
02660                     switch (m_overflowMode)
02661                     {
02662                         case TextOverflowModes.Overflow:
02663                             if (m_isMaskingEnabled)
02664                                 DisableMasking();
02665 
02666                             break;
02667                         case TextOverflowModes.Ellipsis:
02668                             if (m_isMaskingEnabled)
02669                                 DisableMasking();
02670 
02671                             if (m_lineNumber > 0)
02672                             {
02673                                 m_char_buffer[m_textInfo.characterInfo[ellipsisIndex].index] = 8230;
02674                                 m_char_buffer[m_textInfo.characterInfo[ellipsisIndex].index + 1] = (char)0;
02675 
02676                                 if (m_cached_Ellipsis_GlyphInfo != null)
02677                                 {
02678                                     m_textInfo.characterInfo[ellipsisIndex].character = (char)8230;
02679                                     m_textInfo.characterInfo[ellipsisIndex].textElement = m_cached_Ellipsis_GlyphInfo;
02680                                     m_textInfo.characterInfo[ellipsisIndex].fontAsset = m_materialReferences[0].fontAsset;
02681                                     m_textInfo.characterInfo[ellipsisIndex].material = m_materialReferences[0].material;
02682                                     m_textInfo.characterInfo[ellipsisIndex].materialReferenceIndex = 0;
02683                                 }
02684                                 else
02685                                 {
02686                                     Debug.LogWarning("Unable to use Ellipsis character since it wasn't found in the current Font Asset [" + m_fontAsset.name + "]. Consider regenerating this font asset to include the Ellipsis character (u+2026).\nNote: Warnings can be disabled in the TMP Settings file.", this);
02687                                 }
02688 
02689                                 m_totalCharacterCount = ellipsisIndex + 1;
02690 
02691                                 GenerateTextMesh();
02692                                 m_isTextTruncated = true;
02693                                 return;
02694                             }
02695                             else
02696                             {
02697                                 ClearMesh();
02698                                 return;
02699                             }
02700                         case TextOverflowModes.Masking:
02701                             if (!m_isMaskingEnabled)
02702                                 EnableMasking();
02703                             break;
02704                         case TextOverflowModes.ScrollRect:
02705                             if (!m_isMaskingEnabled)
02706                                 EnableMasking();
02707                             break;
02708                         case TextOverflowModes.Truncate:
02709                             if (m_isMaskingEnabled)
02710                                 DisableMasking();
02711 
02712                             // TODO : Optimize 
02713                             if (m_lineNumber > 0)
02714                             {
02715                                 m_char_buffer[m_textInfo.characterInfo[ellipsisIndex].index + 1] = (char)0;
02716 
02717                                 m_totalCharacterCount = ellipsisIndex + 1;
02718 
02719                                 GenerateTextMesh();
02720                                 m_isTextTruncated = true;
02721                                 return;
02722                             }
02723                             else
02724                             {
02725                                 ClearMesh();
02726                                 return;
02727                             }
02728                         case TextOverflowModes.Page:
02729                             if (m_isMaskingEnabled)
02730                                 DisableMasking();
02731 
02732                             // Ignore Page Break, Linefeed or carriage return
02733                             if (charCode == 13 || charCode == 10)
02734                                 break;
02735 
02736                             // Return if the first character doesn't fit.
02737                             if (i == 0)
02738                             {
02739                                 ClearMesh();
02740                                 return;
02741                             }
02742                             else if (previousPageOverflowChar == i)
02743                             {
02744                                 m_char_buffer[i] = 0;
02745                                 m_isTextTruncated = true;
02746                             }
02747 
02748                             previousPageOverflowChar = i;
02749 
02750                             // Go back to previous line and re-layout 
02751                             i = RestoreWordWrappingState(ref m_SavedLineState);
02752 
02753                             m_isNewPage = true;
02754                             m_xAdvance = 0 + tag_Indent;
02755                             m_lineOffset = 0;
02756                             m_maxAscender = 0;
02757                             pageAscender = 0;
02758                             m_lineNumber += 1;
02759                             m_pageNumber += 1;
02760                             continue;
02761                         case TextOverflowModes.Linked:
02762                             if (m_linkedTextComponent != null)
02763                             {
02764                                 m_linkedTextComponent.text = text;
02765                                 m_linkedTextComponent.firstVisibleCharacter = m_characterCount;
02766                                 m_linkedTextComponent.ForceMeshUpdate();
02767                             }
02768 
02769                             // Truncate remaining text
02770                             if (m_lineNumber > 0)
02771                             {
02772                                 m_char_buffer[i] = (char)0;
02773 
02774                                 m_totalCharacterCount = m_characterCount;
02775 
02776                                 // TODO : Optimize as we should be able to end the layout phase here without having to do another pass.
02777                                 GenerateTextMesh();
02778                                 m_isTextTruncated = true;
02779                                 return;
02780                             }
02781                             else
02782                             {
02783                                 ClearMesh();
02784                                 return;
02785                             }
02786                     }
02787                     #endregion End Text Overflow
02788 
02789                 }
02790                 #if TMP_PROFILE_ON
02791                 Profiler.EndSample();
02792                 #endif
02793                 #endregion Check Vertical Bounds
02794 
02795 
02796                 // Handle xAdvance & Tabulation Stops. Tab stops at every 25% of Font Size.
02797                 #region XAdvance, Tabulation & Stops
02798                 if (charCode == 9)
02799                 {
02800                     float tabSize = m_currentFontAsset.fontInfo.TabWidth * currentElementScale;
02801                     float tabs = Mathf.Ceil(m_xAdvance / tabSize) * tabSize;
02802                     m_xAdvance = tabs > m_xAdvance ? tabs : m_xAdvance + tabSize;
02803                 }
02804                 else if (m_monoSpacing != 0)
02805                 {
02806                     m_xAdvance += (m_monoSpacing - monoAdvance + ((m_characterSpacing + m_currentFontAsset.normalSpacingOffset) * currentElementScale) + m_cSpacing) * (1 - m_charWidthAdjDelta);
02807 
02808                     if (char.IsWhiteSpace((char)charCode) || charCode == 0x200B)
02809                         m_xAdvance += m_wordSpacing * currentElementScale;
02810                 }
02811                 else if (!m_isRightToLeft)
02812                 {
02813                     float scaleFXMultiplier = 1;
02814                     if (m_isFXMatrixSet) scaleFXMultiplier = m_FXMatrix.m00;
02815 
02816                     m_xAdvance += ((m_cached_TextElement.xAdvance * scaleFXMultiplier * bold_xAdvance_multiplier + m_characterSpacing + m_currentFontAsset.normalSpacingOffset + glyphAdjustments.xAdvance) * currentElementScale + m_cSpacing) * (1 - m_charWidthAdjDelta);
02817 
02818                     if (char.IsWhiteSpace((char)charCode) || charCode == 0x200B)
02819                         m_xAdvance += m_wordSpacing * currentElementScale;
02820                 }
02821                 else
02822                 {
02823                     m_xAdvance -= glyphAdjustments.xAdvance * currentElementScale;
02824                 }
02825 
02826 
02827                 // Store xAdvance information
02828                 m_textInfo.characterInfo[m_characterCount].xAdvance = m_xAdvance;
02829 
02830                 #endregion Tabulation & Stops
02831 
02832 
02833                 // Handle Carriage Return
02834                 #region Carriage Return
02835                 if (charCode == 13)
02836                 {
02837                     m_xAdvance = 0 + tag_Indent;
02838                 }
02839                 #endregion Carriage Return
02840 
02841 
02842                 // Handle Line Spacing Adjustments + Word Wrapping & special case for last line.
02843                 #region Check for Line Feed and Last Character
02844                 #if TMP_PROFILE_ON
02845                 Profiler.BeginSample("Process Linefeed");
02846                 #endif
02847                 if (charCode == 10 || m_characterCount == totalCharacterCount - 1)
02848                 {
02849                     // Check if Line Spacing of previous line needs to be adjusted.
02850                     if (m_lineNumber > 0 && !TMP_Math.Approximately(m_maxLineAscender, m_startOfLineAscender) && m_lineHeight == TMP_Math.FLOAT_UNSET && !m_isNewPage)
02851                     {
02852                         //Debug.Log("Line Feed - Adjusting Line Spacing on line #" + m_lineNumber);
02853                         float offsetDelta = m_maxLineAscender - m_startOfLineAscender;
02854                         AdjustLineOffset(m_firstCharacterOfLine, m_characterCount, offsetDelta);
02855                         elementDescenderII -= offsetDelta;
02856                         m_lineOffset += offsetDelta;
02857                     }
02858                     m_isNewPage = false;
02859 
02860                     // Calculate lineAscender & make sure if last character is superscript or subscript that we check that as well.
02861                     float lineAscender = m_maxLineAscender - m_lineOffset;
02862                     float lineDescender = m_maxLineDescender - m_lineOffset;
02863 
02864                     // Update maxDescender and maxVisibleDescender
02865                     m_maxDescender = m_maxDescender < lineDescender ? m_maxDescender : lineDescender;
02866                     if (!isMaxVisibleDescenderSet)
02867                         maxVisibleDescender = m_maxDescender;
02868 
02869                     if (m_useMaxVisibleDescender && (m_characterCount >= m_maxVisibleCharacters || m_lineNumber >= m_maxVisibleLines))
02870                         isMaxVisibleDescenderSet = true;
02871 
02872                     // Save Line Information
02873                     m_textInfo.lineInfo[m_lineNumber].firstCharacterIndex = m_firstCharacterOfLine;
02874                     m_textInfo.lineInfo[m_lineNumber].firstVisibleCharacterIndex = m_firstVisibleCharacterOfLine = m_firstCharacterOfLine > m_firstVisibleCharacterOfLine ? m_firstCharacterOfLine : m_firstVisibleCharacterOfLine;
02875                     m_textInfo.lineInfo[m_lineNumber].lastCharacterIndex = m_lastCharacterOfLine = m_characterCount;
02876                     m_textInfo.lineInfo[m_lineNumber].lastVisibleCharacterIndex = m_lastVisibleCharacterOfLine = m_lastVisibleCharacterOfLine < m_firstVisibleCharacterOfLine ? m_firstVisibleCharacterOfLine : m_lastVisibleCharacterOfLine;
02877 
02878                     m_textInfo.lineInfo[m_lineNumber].characterCount = m_textInfo.lineInfo[m_lineNumber].lastCharacterIndex - m_textInfo.lineInfo[m_lineNumber].firstCharacterIndex + 1;
02879                     m_textInfo.lineInfo[m_lineNumber].visibleCharacterCount = m_lineVisibleCharacterCount;
02880                     m_textInfo.lineInfo[m_lineNumber].lineExtents.min = new Vector2(m_textInfo.characterInfo[m_firstVisibleCharacterOfLine].bottomLeft.x, lineDescender);
02881                     m_textInfo.lineInfo[m_lineNumber].lineExtents.max = new Vector2(m_textInfo.characterInfo[m_lastVisibleCharacterOfLine].topRight.x, lineAscender);
02882                     m_textInfo.lineInfo[m_lineNumber].length = m_textInfo.lineInfo[m_lineNumber].lineExtents.max.x - (padding * currentElementScale);
02883                     m_textInfo.lineInfo[m_lineNumber].width = width;
02884 
02885                     if (m_textInfo.lineInfo[m_lineNumber].characterCount == 1)
02886                         m_textInfo.lineInfo[m_lineNumber].alignment = m_lineJustification;
02887 
02888                     if (m_textInfo.characterInfo[m_lastVisibleCharacterOfLine].isVisible)
02889                         m_textInfo.lineInfo[m_lineNumber].maxAdvance = m_textInfo.characterInfo[m_lastVisibleCharacterOfLine].xAdvance - (m_characterSpacing + m_currentFontAsset.normalSpacingOffset) * currentElementScale - m_cSpacing;
02890                     else
02891                         m_textInfo.lineInfo[m_lineNumber].maxAdvance = m_textInfo.characterInfo[m_lastCharacterOfLine].xAdvance - (m_characterSpacing + m_currentFontAsset.normalSpacingOffset) * currentElementScale - m_cSpacing;
02892 
02893                     m_textInfo.lineInfo[m_lineNumber].baseline = 0 - m_lineOffset;
02894                     m_textInfo.lineInfo[m_lineNumber].ascender = lineAscender;
02895                     m_textInfo.lineInfo[m_lineNumber].descender = lineDescender;
02896                     m_textInfo.lineInfo[m_lineNumber].lineHeight = lineAscender - lineDescender + lineGap * baseScale;
02897 
02898                     m_firstCharacterOfLine = m_characterCount + 1;
02899                     m_lineVisibleCharacterCount = 0;
02900 
02901                     // Add new line if not last line or character.
02902                     if (charCode == 10)
02903                     {
02904                         // Store the state of the line before starting on the new line.
02905                         SaveWordWrappingState(ref m_SavedLineState, i, m_characterCount);
02906                         // Store the state of the last Character before the new line.
02907                         SaveWordWrappingState(ref m_SavedWordWrapState, i, m_characterCount);
02908 
02909                         m_lineNumber += 1;
02910                         isStartOfNewLine = true;
02911                         ignoreNonBreakingSpace = false;
02912                         isFirstWord = true;
02913 
02914                         // Check to make sure Array is large enough to hold a new line.
02915                         if (m_lineNumber >= m_textInfo.lineInfo.Length)
02916                             ResizeLineExtents(m_lineNumber);
02917 
02918                         // Apply Line Spacing
02919                         if (m_lineHeight == TMP_Math.FLOAT_UNSET)
02920                         {
02921                             lineOffsetDelta = 0 - m_maxLineDescender + elementAscender + (lineGap + m_lineSpacing + m_paragraphSpacing + m_lineSpacingDelta) * baseScale;
02922                             m_lineOffset += lineOffsetDelta;
02923                         }
02924                         else
02925                             m_lineOffset += m_lineHeight + (m_lineSpacing + m_paragraphSpacing) * baseScale;
02926 
02927                         m_maxLineAscender = k_LargeNegativeFloat;
02928                         m_maxLineDescender = k_LargePositiveFloat;
02929                         m_startOfLineAscender = elementAscender;
02930 
02931                         m_xAdvance = 0 + tag_LineIndent + tag_Indent;
02932 
02933                         ellipsisIndex = m_characterCount - 1;
02934 
02935                         m_characterCount += 1;
02936                         continue;
02937                     }
02938                 }
02939                 #if TMP_PROFILE_ON
02940                 Profiler.EndSample();
02941                 #endif
02942                 #endregion Check for Linefeed or Last Character
02943 
02944 
02945                 // Store Rectangle positions for each Character.
02946                 #region Save CharacterInfo for the current character.
02947                 #if TMP_PROFILE_ON
02948                 Profiler.BeginSample("Save CharacterInfo & Extents");
02949                 #endif
02950                 // Determine the bounds of the Mesh.
02951                 if (m_textInfo.characterInfo[m_characterCount].isVisible)
02952                 {
02953                     m_meshExtents.min.x = Mathf.Min(m_meshExtents.min.x, m_textInfo.characterInfo[m_characterCount].bottomLeft.x);
02954                     m_meshExtents.min.y = Mathf.Min(m_meshExtents.min.y, m_textInfo.characterInfo[m_characterCount].bottomLeft.y);
02955 
02956                     m_meshExtents.max.x = Mathf.Max(m_meshExtents.max.x, m_textInfo.characterInfo[m_characterCount].topRight.x);
02957                     m_meshExtents.max.y = Mathf.Max(m_meshExtents.max.y, m_textInfo.characterInfo[m_characterCount].topRight.y);
02958 
02959                     //m_meshExtents.min = new Vector2(Mathf.Min(m_meshExtents.min.x, m_textInfo.characterInfo[m_characterCount].bottomLeft.x), Mathf.Min(m_meshExtents.min.y, m_textInfo.characterInfo[m_characterCount].bottomLeft.y));
02960                     //m_meshExtents.max = new Vector2(Mathf.Max(m_meshExtents.max.x, m_textInfo.characterInfo[m_characterCount].topRight.x), Mathf.Max(m_meshExtents.max.y, m_textInfo.characterInfo[m_characterCount].topRight.y));
02961                 }
02962 
02963 
02964                 // Save pageInfo Data
02965                 if (m_overflowMode == TextOverflowModes.Page && charCode != 13 && charCode != 10) // && m_pageNumber < 16)
02966                 {
02967                     // Check if we need to increase allocations for the pageInfo array.
02968                     if (m_pageNumber + 1 > m_textInfo.pageInfo.Length)
02969                         TMP_TextInfo.Resize(ref m_textInfo.pageInfo, m_pageNumber + 1, true);
02970 
02971                     m_textInfo.pageInfo[m_pageNumber].ascender = pageAscender;
02972                     m_textInfo.pageInfo[m_pageNumber].descender = elementDescender < m_textInfo.pageInfo[m_pageNumber].descender ? elementDescender : m_textInfo.pageInfo[m_pageNumber].descender;
02973 
02974                     if (m_pageNumber == 0 && m_characterCount == 0)
02975                         m_textInfo.pageInfo[m_pageNumber].firstCharacterIndex = m_characterCount;
02976                     else if (m_characterCount > 0 && m_pageNumber != m_textInfo.characterInfo[m_characterCount - 1].pageNumber)
02977                     {
02978                         m_textInfo.pageInfo[m_pageNumber - 1].lastCharacterIndex = m_characterCount - 1;
02979                         m_textInfo.pageInfo[m_pageNumber].firstCharacterIndex = m_characterCount;
02980                     }
02981                     else if (m_characterCount == totalCharacterCount - 1)
02982                         m_textInfo.pageInfo[m_pageNumber].lastCharacterIndex = m_characterCount;
02983                 }
02984                 #if TMP_PROFILE_ON
02985                 Profiler.EndSample();
02986                 #endif
02987                 #endregion Saving CharacterInfo
02988 
02989 
02990                 // Save State of Mesh Creation for handling of Word Wrapping
02991                 #region Save Word Wrapping State
02992                 #if TMP_PROFILE_ON
02993                 Profiler.BeginSample("Save Word Wrapping State");
02994                 #endif
02995                 if (m_enableWordWrapping || m_overflowMode == TextOverflowModes.Truncate || m_overflowMode == TextOverflowModes.Ellipsis)
02996                 {
02997                     if ((char.IsWhiteSpace((char)charCode) || charCode == 0x200B || charCode == 0x2D || charCode == 0xAD) && (!m_isNonBreakingSpace || ignoreNonBreakingSpace) && charCode != 0xA0 && charCode != 0x2007 && charCode != 0x2011 && charCode != 0x202F && charCode != 0x2060)
02998                     {
02999                         // We store the state of numerous variables for the most recent Space, LineFeed or Carriage Return to enable them to be restored 
03000                         // for Word Wrapping.
03001                         SaveWordWrappingState(ref m_SavedWordWrapState, i, m_characterCount);
03002                         m_isCharacterWrappingEnabled = false;
03003                         isFirstWord = false;
03004                     }
03005                     // Handling for East Asian languages
03006                     else if ((  charCode > 0x1100 && charCode < 0x11ff || /* Hangul Jamo */
03007                                 charCode > 0x2E80 && charCode < 0x9FFF || /* CJK */
03008                                 charCode > 0xA960 && charCode < 0xA97F || /* Hangul Jame Extended-A */
03009                                 charCode > 0xAC00 && charCode < 0xD7FF || /* Hangul Syllables */
03010                                 charCode > 0xF900 && charCode < 0xFAFF || /* CJK Compatibility Ideographs */
03011                                 charCode > 0xFE30 && charCode < 0xFE4F || /* CJK Compatibility Forms */
03012                                 charCode > 0xFF00 && charCode < 0xFFEF)   /* CJK Halfwidth */
03013                                 && !m_isNonBreakingSpace)
03014                     {
03015                         if (isFirstWord || isLastBreakingChar || TMP_Settings.linebreakingRules.leadingCharacters.ContainsKey(charCode) == false &&
03016                             (m_characterCount < totalCharacterCount - 1 &&
03017                             TMP_Settings.linebreakingRules.followingCharacters.ContainsKey(m_textInfo.characterInfo[m_characterCount + 1].character) == false))
03018                         {
03019                             SaveWordWrappingState(ref m_SavedWordWrapState, i, m_characterCount);
03020                             m_isCharacterWrappingEnabled = false;
03021                             isFirstWord = false;
03022                         }
03023                     }
03024                     else if ((isFirstWord || m_isCharacterWrappingEnabled == true || isLastBreakingChar))
03025                         SaveWordWrappingState(ref m_SavedWordWrapState, i, m_characterCount);
03026 
03027                 }
03028                 #if TMP_PROFILE_ON
03029                 Profiler.EndSample();
03030                 #endif
03031                 #endregion Save Word Wrapping State
03032 
03033                 m_characterCount += 1;
03034             }
03035 
03036             // Check Auto Sizing and increase font size to fill text container.
03037             #region Check Auto-Sizing (Upper Font Size Bounds)
03038             fontSizeDelta = m_maxFontSize - m_minFontSize;
03039             if (!m_isCharacterWrappingEnabled && m_enableAutoSizing && fontSizeDelta > 0.051f && m_fontSize < m_fontSizeMax)
03040             {
03041                 m_minFontSize = m_fontSize;
03042                 m_fontSize += Mathf.Max((m_maxFontSize - m_fontSize) / 2, 0.05f);
03043                 m_fontSize = (int)(Mathf.Min(m_fontSize, m_fontSizeMax) * 20 + 0.5f) / 20f;
03044 
03045                 //Debug.Log(m_fontSize);
03046 
03047                 if (loopCountA > 20) return; // Added to debug
03048                 GenerateTextMesh();
03049                 return;
03050             }
03051             #endregion End Auto-sizing Check
03052 
03053 
03054             m_isCharacterWrappingEnabled = false;
03055 
03056             #if TMP_PROFILE_PHASES_ON
03057                 Profiler.EndSample();
03058             #endif
03059 
03060             //Debug.Log("Iteration Count: " + loopCountA + ". Final Point Size: " + m_fontSize); // + "  B: " + loopCountB + "  C: " + loopCountC + "  D: " + loopCountD);
03061 
03062             // *** PHASE II of Text Generation ***
03063             #if TMP_PROFILE_PHASES_ON
03064                 Profiler.BeginSample("TMP Generate Text - Phase II");
03065             #endif
03066 
03067             // If there are no visible characters... no need to continue
03068             if (m_characterCount == 0) // && m_visibleSpriteCount == 0)
03069             {
03070                 ClearMesh();
03071 
03072                 // Event indicating the text has been regenerated.
03073                 TMPro_EventManager.ON_TEXT_CHANGED(this);
03074                 return;
03075             }
03076 
03077 
03078             // *** PHASE II of Text Generation ***
03079             int last_vert_index = m_materialReferences[0].referenceCount * 4;
03080 
03081             // Partial clear of the vertices array to mark unused vertices as degenerate.
03082             m_textInfo.meshInfo[0].Clear(false);
03083 
03084             // Handle Text Alignment
03085             #region Text Vertical Alignment
03086             #if TMP_PROFILE_ON
03087             Profiler.BeginSample("Vertical Text Alignment");
03088             #endif
03089             Vector3 anchorOffset = Vector3.zero;
03090             Vector3[] corners = m_RectTransformCorners; // GetTextContainerLocalCorners();
03091 
03092             switch (m_textAlignment)
03093             {
03094                 // Top Vertically
03095                 case TextAlignmentOptions.Top:
03096                 case TextAlignmentOptions.TopLeft:
03097                 case TextAlignmentOptions.TopRight:
03098                 case TextAlignmentOptions.TopJustified:
03099                 case TextAlignmentOptions.TopFlush:
03100                 case TextAlignmentOptions.TopGeoAligned:
03101                     if (m_overflowMode != TextOverflowModes.Page)
03102                         anchorOffset = corners[1] + new Vector3(0 + margins.x, 0 - m_maxAscender - margins.y, 0);
03103                     else
03104                         anchorOffset = corners[1] + new Vector3(0 + margins.x, 0 - m_textInfo.pageInfo[pageToDisplay].ascender - margins.y, 0);
03105                     break;
03106 
03107                 // Middle Vertically
03108                 case TextAlignmentOptions.Left:
03109                 case TextAlignmentOptions.Right:
03110                 case TextAlignmentOptions.Center:
03111                 case TextAlignmentOptions.Justified:
03112                 case TextAlignmentOptions.Flush:
03113                 case TextAlignmentOptions.CenterGeoAligned:
03114                     if (m_overflowMode != TextOverflowModes.Page)
03115                         anchorOffset = (corners[0] + corners[1]) / 2 + new Vector3(0 + margins.x, 0 - (m_maxAscender + margins.y + maxVisibleDescender - margins.w) / 2, 0);
03116                     else
03117                         anchorOffset = (corners[0] + corners[1]) / 2 + new Vector3(0 + margins.x, 0 - (m_textInfo.pageInfo[pageToDisplay].ascender + margins.y + m_textInfo.pageInfo[pageToDisplay].descender - margins.w) / 2, 0);
03118                     break;
03119 
03120                 // Bottom Vertically
03121                 case TextAlignmentOptions.Bottom:
03122                 case TextAlignmentOptions.BottomLeft:
03123                 case TextAlignmentOptions.BottomRight:
03124                 case TextAlignmentOptions.BottomJustified:
03125                 case TextAlignmentOptions.BottomFlush:
03126                 case TextAlignmentOptions.BottomGeoAligned:
03127                     if (m_overflowMode != TextOverflowModes.Page)
03128                         anchorOffset = corners[0] + new Vector3(0 + margins.x, 0 - maxVisibleDescender + margins.w, 0);
03129                     else
03130                         anchorOffset = corners[0] + new Vector3(0 + margins.x, 0 - m_textInfo.pageInfo[pageToDisplay].descender + margins.w, 0);
03131                     break;
03132 
03133                 // Baseline Vertically
03134                 case TextAlignmentOptions.Baseline:
03135                 case TextAlignmentOptions.BaselineLeft:
03136                 case TextAlignmentOptions.BaselineRight:
03137                 case TextAlignmentOptions.BaselineJustified:
03138                 case TextAlignmentOptions.BaselineFlush:
03139                 case TextAlignmentOptions.BaselineGeoAligned:
03140                     anchorOffset = (corners[0] + corners[1]) / 2 + new Vector3(0 + margins.x, 0, 0);
03141                     break;
03142 
03143                 // Midline Vertically 
03144                 case TextAlignmentOptions.MidlineLeft:
03145                 case TextAlignmentOptions.Midline:
03146                 case TextAlignmentOptions.MidlineRight:
03147                 case TextAlignmentOptions.MidlineJustified:
03148                 case TextAlignmentOptions.MidlineFlush:
03149                 case TextAlignmentOptions.MidlineGeoAligned:
03150                     anchorOffset = (corners[0] + corners[1]) / 2 + new Vector3(0 + margins.x, 0 - (m_meshExtents.max.y + margins.y + m_meshExtents.min.y - margins.w) / 2, 0);
03151                     break;
03152 
03153                 // Capline Vertically 
03154                 case TextAlignmentOptions.CaplineLeft:
03155                 case TextAlignmentOptions.Capline:
03156                 case TextAlignmentOptions.CaplineRight:
03157                 case TextAlignmentOptions.CaplineJustified:
03158                 case TextAlignmentOptions.CaplineFlush:
03159                 case TextAlignmentOptions.CaplineGeoAligned:
03160                     anchorOffset = (corners[0] + corners[1]) / 2 + new Vector3(0 + margins.x, 0 - (m_maxCapHeight - margins.y - margins.w) / 2, 0);
03161                     break;
03162             }
03163             #if TMP_PROFILE_ON
03164             Profiler.EndSample();
03165             #endif
03166             #endregion
03167 
03168 
03169             // Initialization for Second Pass
03170             Vector3 justificationOffset = Vector3.zero;
03171             Vector3 offset = Vector3.zero;
03172             int vert_index_X4 = 0;
03173             int sprite_index_X4 = 0;
03174 
03175             int wordCount = 0;
03176             int lineCount = 0;
03177             int lastLine = 0;
03178             bool isFirstSeperator = false;
03179 
03180             bool isStartOfWord = false;
03181             int wordFirstChar = 0;
03182             int wordLastChar = 0;
03183 
03184             // Second Pass : Line Justification, UV Mapping, Character & Line Visibility & more.
03185             #region Handle Line Justification & UV Mapping & Character Visibility & More
03186 
03187             // Variables used to handle Canvas Render Modes and SDF Scaling
03188             bool isCameraAssigned = m_canvas.worldCamera == null ? false : true;
03189             float lossyScale = m_previousLossyScaleY = this.transform.lossyScale.y;
03190             RenderMode canvasRenderMode = m_canvas.renderMode;
03191             float canvasScaleFactor = m_canvas.scaleFactor;
03192 
03193             Color32 underlineColor = Color.white;
03194             Color32 strikethroughColor = Color.white;
03195             Color32 highlightColor = new Color32(255, 255, 0, 64);
03196             float xScale = 0;
03197             float underlineStartScale = 0;
03198             float underlineEndScale = 0;
03199             float underlineMaxScale = 0;
03200             float underlineBaseLine = k_LargePositiveFloat;
03201             int lastPage = 0;
03202 
03203             float strikethroughPointSize = 0;
03204             float strikethroughScale = 0;
03205             float strikethroughBaseline = 0;
03206 
03207             TMP_CharacterInfo[] characterInfos = m_textInfo.characterInfo;
03208             #region Handle Line Justification & UV Mapping & Character Visibility & More
03209             for (int i = 0; i < m_characterCount; i++)
03210             {
03211                 TMP_FontAsset currentFontAsset = characterInfos[i].fontAsset;
03212 
03213                 char currentCharacter = characterInfos[i].character;
03214 
03215                 int currentLine = characterInfos[i].lineNumber;
03216                 TMP_LineInfo lineInfo = m_textInfo.lineInfo[currentLine];
03217                 lineCount = currentLine + 1;
03218 
03219                 TextAlignmentOptions lineAlignment = lineInfo.alignment;
03220 
03221                 // Process Line Justification
03222                 #region Handle Line Justification
03223                 #if TMP_PROFILE_ON
03224                 Profiler.BeginSample("Horizontal Text Alignment");
03225                 #endif 
03226                 //if (!characterInfos[i].isIgnoringAlignment)
03227                 //{
03228                 switch (lineAlignment)
03229                 {
03230                     case TextAlignmentOptions.TopLeft:
03231                     case TextAlignmentOptions.Left:
03232                     case TextAlignmentOptions.BottomLeft:
03233                     case TextAlignmentOptions.BaselineLeft:
03234                     case TextAlignmentOptions.MidlineLeft:
03235                     case TextAlignmentOptions.CaplineLeft:
03236                         if (!m_isRightToLeft)
03237                             justificationOffset = new Vector3(0 + lineInfo.marginLeft, 0, 0);
03238                         else
03239                             justificationOffset = new Vector3(0 - lineInfo.maxAdvance, 0, 0);
03240                         break;
03241 
03242                     case TextAlignmentOptions.Top:
03243                     case TextAlignmentOptions.Center:
03244                     case TextAlignmentOptions.Bottom:
03245                     case TextAlignmentOptions.Baseline:
03246                     case TextAlignmentOptions.Midline:
03247                     case TextAlignmentOptions.Capline:
03248                         justificationOffset = new Vector3(lineInfo.marginLeft + lineInfo.width / 2 - lineInfo.maxAdvance / 2, 0, 0);
03249                         break;
03250 
03251                     case TextAlignmentOptions.TopGeoAligned:
03252                     case TextAlignmentOptions.CenterGeoAligned:
03253                     case TextAlignmentOptions.BottomGeoAligned:
03254                     case TextAlignmentOptions.BaselineGeoAligned:
03255                     case TextAlignmentOptions.MidlineGeoAligned:
03256                     case TextAlignmentOptions.CaplineGeoAligned:
03257                         justificationOffset = new Vector3(lineInfo.marginLeft + lineInfo.width / 2 - (lineInfo.lineExtents.min.x + lineInfo.lineExtents.max.x) / 2, 0, 0);
03258                         break;
03259 
03260                     case TextAlignmentOptions.TopRight:
03261                     case TextAlignmentOptions.Right:
03262                     case TextAlignmentOptions.BottomRight:
03263                     case TextAlignmentOptions.BaselineRight:
03264                     case TextAlignmentOptions.MidlineRight:
03265                     case TextAlignmentOptions.CaplineRight:
03266                         if (!m_isRightToLeft)
03267                             justificationOffset = new Vector3(lineInfo.marginLeft + lineInfo.width - lineInfo.maxAdvance, 0, 0);
03268                         else
03269                             justificationOffset = new Vector3(lineInfo.marginLeft + lineInfo.width, 0, 0);
03270                         break;
03271 
03272                     case TextAlignmentOptions.TopJustified:
03273                     case TextAlignmentOptions.Justified:
03274                     case TextAlignmentOptions.BottomJustified:
03275                     case TextAlignmentOptions.BaselineJustified:
03276                     case TextAlignmentOptions.MidlineJustified:
03277                     case TextAlignmentOptions.CaplineJustified:
03278                     case TextAlignmentOptions.TopFlush:
03279                     case TextAlignmentOptions.Flush:
03280                     case TextAlignmentOptions.BottomFlush:
03281                     case TextAlignmentOptions.BaselineFlush:
03282                     case TextAlignmentOptions.MidlineFlush:
03283                     case TextAlignmentOptions.CaplineFlush:
03284                         // Skip Zero Width Characters
03285                         if (currentCharacter == 0xAD || currentCharacter == 0x200B || currentCharacter == 0x2060) break;
03286 
03287                         char lastCharOfCurrentLine = characterInfos[lineInfo.lastCharacterIndex].character;
03288                         bool isFlush = ((_HorizontalAlignmentOptions)lineAlignment & _HorizontalAlignmentOptions.Flush) == _HorizontalAlignmentOptions.Flush;
03289 
03290                         // In Justified mode, all lines are justified except the last one.
03291                         // In Flush mode, all lines are justified.
03292                         if (char.IsControl(lastCharOfCurrentLine) == false && currentLine < m_lineNumber || isFlush || lineInfo.maxAdvance > lineInfo.width)
03293                         {
03294                             // First character of each line.
03295                             if (currentLine != lastLine || i == 0 || i == m_firstVisibleCharacter)
03296                             {
03297                                 if (!m_isRightToLeft)
03298                                     justificationOffset = new Vector3(lineInfo.marginLeft, 0, 0);
03299                                 else
03300                                     justificationOffset = new Vector3(lineInfo.marginLeft + lineInfo.width, 0, 0);
03301 
03302                                 if (char.IsSeparator(currentCharacter))
03303                                     isFirstSeperator = true;
03304                                 else
03305                                     isFirstSeperator = false;
03306                             }
03307                             else
03308                             {
03309                                 float gap = !m_isRightToLeft ? lineInfo.width - lineInfo.maxAdvance : lineInfo.width + lineInfo.maxAdvance;
03310 
03311                                 int visibleCount = lineInfo.visibleCharacterCount - 1 + lineInfo.controlCharacterCount;
03312 
03313                                 // Get the number of spaces for each line ignoring the last character if it is not visible (ie. a space or linefeed).
03314                                 int spaces = (characterInfos[lineInfo.lastCharacterIndex].isVisible ? lineInfo.spaceCount : lineInfo.spaceCount - 1) - lineInfo.controlCharacterCount;
03315 
03316                                 if (isFirstSeperator) { spaces -= 1; visibleCount += 1; }
03317 
03318                                 float ratio = spaces > 0 ? m_wordWrappingRatios : 1;
03319 
03320                                 if (spaces < 1) spaces = 1;
03321 
03322                                 if (currentCharacter != 0xA0 && (currentCharacter == 9 || char.IsSeparator((char)currentCharacter)))
03323                                 {
03324                                     if (!m_isRightToLeft)
03325                                         justificationOffset += new Vector3(gap * (1 - ratio) / spaces, 0, 0);
03326                                     else
03327                                         justificationOffset -= new Vector3(gap * (1 - ratio) / spaces, 0, 0);
03328                                 }
03329                                 else
03330                                 {
03331                                     if (!m_isRightToLeft)
03332                                         justificationOffset += new Vector3(gap * ratio / visibleCount, 0, 0);
03333                                     else
03334                                         justificationOffset -= new Vector3(gap * ratio / visibleCount, 0, 0);
03335                                 }
03336                             }
03337                         }
03338                         else
03339                         {
03340                             if (!m_isRightToLeft)
03341                                 justificationOffset = new Vector3(lineInfo.marginLeft, 0, 0); // Keep last line left justified.
03342                             else
03343                                 justificationOffset = new Vector3(lineInfo.marginLeft + lineInfo.width, 0, 0); // Keep last line right justified.
03344                         }
03345                         //Debug.Log("Char [" + (char)charCode + "] Code:" + charCode + "  Line # " + currentLine + "  Offset:" + justificationOffset + "  # Spaces:" + lineInfo.spaceCount + "  # Characters:" + lineInfo.characterCount);
03346                         break;
03347                 }
03348                 //}
03349                 #if TMP_PROFILE_ON
03350                 Profiler.EndSample();
03351                 #endif
03352                 #endregion End Text Justification
03353 
03354                 offset = anchorOffset + justificationOffset;
03355 
03356                 // Handle UV2 mapping options and packing of scale information into UV2.
03357                 #region Handling of UV2 mapping & Scale packing
03358                 bool isCharacterVisible = characterInfos[i].isVisible;
03359                 if (isCharacterVisible)
03360                 {
03361                     TMP_TextElementType elementType = characterInfos[i].elementType;
03362                     switch (elementType)
03363                     {
03364                         // CHARACTERS
03365                         case TMP_TextElementType.Character:
03366                             Extents lineExtents = lineInfo.lineExtents;
03367                             float uvOffset = (m_uvLineOffset * currentLine) % 1; // + m_uvOffset.x;
03368 
03369                             // Setup UV2 based on Character Mapping Options Selected
03370                             #region Handle UV Mapping Options
03371                             #if TMP_PROFILE_ON
03372                             Profiler.BeginSample("UV MAPPING");
03373                             #endif
03374                             switch (m_horizontalMapping)
03375                             {
03376                                 case TextureMappingOptions.Character:
03377                                     characterInfos[i].vertex_BL.uv2.x = 0; //+ m_uvOffset.x;
03378                                     characterInfos[i].vertex_TL.uv2.x = 0; //+ m_uvOffset.x;
03379                                     characterInfos[i].vertex_TR.uv2.x = 1; //+ m_uvOffset.x;
03380                                     characterInfos[i].vertex_BR.uv2.x = 1; //+ m_uvOffset.x;
03381                                     break;
03382 
03383                                 case TextureMappingOptions.Line:
03384                                     if (m_textAlignment != TextAlignmentOptions.Justified)
03385                                     {
03386                                         characterInfos[i].vertex_BL.uv2.x = (characterInfos[i].vertex_BL.position.x - lineExtents.min.x) / (lineExtents.max.x - lineExtents.min.x) + uvOffset;
03387                                         characterInfos[i].vertex_TL.uv2.x = (characterInfos[i].vertex_TL.position.x - lineExtents.min.x) / (lineExtents.max.x - lineExtents.min.x) + uvOffset;
03388                                         characterInfos[i].vertex_TR.uv2.x = (characterInfos[i].vertex_TR.position.x - lineExtents.min.x) / (lineExtents.max.x - lineExtents.min.x) + uvOffset;
03389                                         characterInfos[i].vertex_BR.uv2.x = (characterInfos[i].vertex_BR.position.x - lineExtents.min.x) / (lineExtents.max.x - lineExtents.min.x) + uvOffset;
03390                                         break;
03391                                     }
03392                                     else // Special Case if Justified is used in Line Mode.
03393                                     {
03394                                         characterInfos[i].vertex_BL.uv2.x = (characterInfos[i].vertex_BL.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03395                                         characterInfos[i].vertex_TL.uv2.x = (characterInfos[i].vertex_TL.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03396                                         characterInfos[i].vertex_TR.uv2.x = (characterInfos[i].vertex_TR.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03397                                         characterInfos[i].vertex_BR.uv2.x = (characterInfos[i].vertex_BR.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03398                                         break;
03399                                     }
03400 
03401                                 case TextureMappingOptions.Paragraph:
03402                                     characterInfos[i].vertex_BL.uv2.x = (characterInfos[i].vertex_BL.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03403                                     characterInfos[i].vertex_TL.uv2.x = (characterInfos[i].vertex_TL.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03404                                     characterInfos[i].vertex_TR.uv2.x = (characterInfos[i].vertex_TR.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03405                                     characterInfos[i].vertex_BR.uv2.x = (characterInfos[i].vertex_BR.position.x + justificationOffset.x - m_meshExtents.min.x) / (m_meshExtents.max.x - m_meshExtents.min.x) + uvOffset;
03406                                     break;
03407 
03408                                 case TextureMappingOptions.MatchAspect:
03409 
03410                                     switch (m_verticalMapping)
03411                                     {
03412                                         case TextureMappingOptions.Character:
03413                                             characterInfos[i].vertex_BL.uv2.y = 0; // + m_uvOffset.y;
03414                                             characterInfos[i].vertex_TL.uv2.y = 1; // + m_uvOffset.y;
03415                                             characterInfos[i].vertex_TR.uv2.y = 0; // + m_uvOffset.y;
03416                                             characterInfos[i].vertex_BR.uv2.y = 1; // + m_uvOffset.y;
03417                                             break;
03418 
03419                                         case TextureMappingOptions.Line:
03420                                             characterInfos[i].vertex_BL.uv2.y = (characterInfos[i].vertex_BL.position.y - lineExtents.min.y) / (lineExtents.max.y - lineExtents.min.y) + uvOffset;
03421                                             characterInfos[i].vertex_TL.uv2.y = (characterInfos[i].vertex_TL.position.y - lineExtents.min.y) / (lineExtents.max.y - lineExtents.min.y) + uvOffset;
03422                                             characterInfos[i].vertex_TR.uv2.y = characterInfos[i].vertex_BL.uv2.y;
03423                                             characterInfos[i].vertex_BR.uv2.y = characterInfos[i].vertex_TL.uv2.y;
03424                                             break;
03425 
03426                                         case TextureMappingOptions.Paragraph:
03427                                             characterInfos[i].vertex_BL.uv2.y = (characterInfos[i].vertex_BL.position.y - m_meshExtents.min.y) / (m_meshExtents.max.y - m_meshExtents.min.y) + uvOffset;
03428                                             characterInfos[i].vertex_TL.uv2.y = (characterInfos[i].vertex_TL.position.y - m_meshExtents.min.y) / (m_meshExtents.max.y - m_meshExtents.min.y) + uvOffset;
03429                                             characterInfos[i].vertex_TR.uv2.y = characterInfos[i].vertex_BL.uv2.y;
03430                                             characterInfos[i].vertex_BR.uv2.y = characterInfos[i].vertex_TL.uv2.y;
03431                                             break;
03432 
03433                                         case TextureMappingOptions.MatchAspect:
03434                                             Debug.Log("ERROR: Cannot Match both Vertical & Horizontal.");
03435                                             break;
03436                                     }
03437 
03438                                     //float xDelta = 1 - (_uv2s[vert_index + 0].y * textMeshCharacterInfo[i].AspectRatio); // Left aligned
03439                                     float xDelta = (1 - ((characterInfos[i].vertex_BL.uv2.y + characterInfos[i].vertex_TL.uv2.y) * characterInfos[i].aspectRatio)) / 2; // Center of Rectangle
03440 
03441                                     characterInfos[i].vertex_BL.uv2.x = (characterInfos[i].vertex_BL.uv2.y * characterInfos[i].aspectRatio) + xDelta + uvOffset;
03442                                     characterInfos[i].vertex_TL.uv2.x = characterInfos[i].vertex_BL.uv2.x;
03443                                     characterInfos[i].vertex_TR.uv2.x = (characterInfos[i].vertex_TL.uv2.y * characterInfos[i].aspectRatio) + xDelta + uvOffset;
03444                                     characterInfos[i].vertex_BR.uv2.x = characterInfos[i].vertex_TR.uv2.x;
03445                                     break;
03446                             }
03447 
03448                             switch (m_verticalMapping)
03449                             {
03450                                 case TextureMappingOptions.Character:
03451                                     characterInfos[i].vertex_BL.uv2.y = 0; // + m_uvOffset.y;
03452                                     characterInfos[i].vertex_TL.uv2.y = 1; // + m_uvOffset.y;
03453                                     characterInfos[i].vertex_TR.uv2.y = 1; // + m_uvOffset.y;
03454                                     characterInfos[i].vertex_BR.uv2.y = 0; // + m_uvOffset.y;
03455                                     break;
03456 
03457                                 case TextureMappingOptions.Line:
03458                                     characterInfos[i].vertex_BL.uv2.y = (characterInfos[i].vertex_BL.position.y - lineInfo.descender) / (lineInfo.ascender - lineInfo.descender); // + m_uvOffset.y;
03459                                     characterInfos[i].vertex_TL.uv2.y = (characterInfos[i].vertex_TL.position.y - lineInfo.descender) / (lineInfo.ascender - lineInfo.descender); // + m_uvOffset.y;
03460                                     characterInfos[i].vertex_TR.uv2.y = characterInfos[i].vertex_TL.uv2.y;
03461                                     characterInfos[i].vertex_BR.uv2.y = characterInfos[i].vertex_BL.uv2.y;
03462                                     break;
03463 
03464                                 case TextureMappingOptions.Paragraph:
03465                                     characterInfos[i].vertex_BL.uv2.y = (characterInfos[i].vertex_BL.position.y - m_meshExtents.min.y) / (m_meshExtents.max.y - m_meshExtents.min.y); // + m_uvOffset.y;
03466                                     characterInfos[i].vertex_TL.uv2.y = (characterInfos[i].vertex_TL.position.y - m_meshExtents.min.y) / (m_meshExtents.max.y - m_meshExtents.min.y); // + m_uvOffset.y;
03467                                     characterInfos[i].vertex_TR.uv2.y = characterInfos[i].vertex_TL.uv2.y;
03468                                     characterInfos[i].vertex_BR.uv2.y = characterInfos[i].vertex_BL.uv2.y;
03469                                     break;
03470 
03471                                 case TextureMappingOptions.MatchAspect:
03472                                     float yDelta = (1 - ((characterInfos[i].vertex_BL.uv2.x + characterInfos[i].vertex_TR.uv2.x) / characterInfos[i].aspectRatio)) / 2; // Center of Rectangle
03473 
03474                                     characterInfos[i].vertex_BL.uv2.y = yDelta + (characterInfos[i].vertex_BL.uv2.x / characterInfos[i].aspectRatio); // + m_uvOffset.y;
03475                                     characterInfos[i].vertex_TL.uv2.y = yDelta + (characterInfos[i].vertex_TR.uv2.x / characterInfos[i].aspectRatio); // + m_uvOffset.y;
03476                                     characterInfos[i].vertex_BR.uv2.y = characterInfos[i].vertex_BL.uv2.y;
03477                                     characterInfos[i].vertex_TR.uv2.y = characterInfos[i].vertex_TL.uv2.y;
03478                                     break;
03479                             }
03480                             #if TMP_PROFILE_ON
03481                             Profiler.EndSample();
03482                             #endif
03483                             #endregion End UV Mapping Options
03484 
03485                             // Pack UV's so that we can pass Xscale needed for Shader to maintain 1:1 ratio.
03486                             #region Pack Scale into UV2
03487                             #if TMP_PROFILE_ON
03488                             Profiler.BeginSample("Pack UV");
03489                             #endif
03490                             xScale = characterInfos[i].scale * (1 - m_charWidthAdjDelta);
03491                             if (!characterInfos[i].isUsingAlternateTypeface && (characterInfos[i].style & FontStyles.Bold) == FontStyles.Bold) xScale *= -1;
03492 
03493                             switch (canvasRenderMode)
03494                             {
03495                                 case RenderMode.ScreenSpaceOverlay:
03496                                     xScale *= lossyScale / canvasScaleFactor;
03497                                     break;
03498                                 case RenderMode.ScreenSpaceCamera:
03499                                     xScale *= isCameraAssigned ? lossyScale : 1;
03500                                     break;
03501                                 case RenderMode.WorldSpace:
03502                                     xScale *= lossyScale;
03503                                     break;
03504                             }
03505 
03506                             // isBold is encoded in the X value and SDF Scale in Y.
03507                             //Vector2 vertexData = new Vector2((characterInfos[i].style & FontStyles.Bold) == FontStyles.Bold ? 1 : 0, xScale);
03508                             //characterInfos[i].vertex_BL.uv2 = vertexData;
03509                             //characterInfos[i].vertex_TL.uv2 = vertexData;
03510                             //characterInfos[i].vertex_TR.uv2 = vertexData;
03511                             //characterInfos[i].vertex_BR.uv2 = vertexData;
03512 
03513                             float x0 = characterInfos[i].vertex_BL.uv2.x;
03514                             float y0 = characterInfos[i].vertex_BL.uv2.y;
03515                             float x1 = characterInfos[i].vertex_TR.uv2.x;
03516                             float y1 = characterInfos[i].vertex_TR.uv2.y;
03517 
03518                             float dx = (int)x0;
03519                             float dy = (int)y0;
03520 
03521                             x0 = x0 - dx;
03522                             x1 = x1 - dx;
03523                             y0 = y0 - dy;
03524                             y1 = y1 - dy;
03525 
03526                             // Optimization to avoid having a vector2 returned from the Pack UV function.
03527                             characterInfos[i].vertex_BL.uv2.x = PackUV(x0, y0); characterInfos[i].vertex_BL.uv2.y = xScale;
03528                             characterInfos[i].vertex_TL.uv2.x = PackUV(x0, y1); characterInfos[i].vertex_TL.uv2.y = xScale;
03529                             characterInfos[i].vertex_TR.uv2.x = PackUV(x1, y1); characterInfos[i].vertex_TR.uv2.y = xScale;
03530                             characterInfos[i].vertex_BR.uv2.x = PackUV(x1, y0); characterInfos[i].vertex_BR.uv2.y = xScale;
03531                             #if TMP_PROFILE_ON
03532                                 Profiler.EndSample();
03533                             #endif
03534                             #endregion
03535                             break;
03536                         
03537                         // SPRITES
03538                         case TMP_TextElementType.Sprite:
03539                             // Nothing right now
03540                             break;
03541                     }
03542 
03543                     // Handle maxVisibleCharacters, maxVisibleLines and Overflow Page Mode.
03544                     #region Handle maxVisibleCharacters / maxVisibleLines / Page Mode
03545                     #if TMP_PROFILE_ON
03546                     Profiler.BeginSample("Process MaxVisible Characters & Lines");
03547                     #endif
03548                     if (i < m_maxVisibleCharacters && wordCount < m_maxVisibleWords && currentLine < m_maxVisibleLines && m_overflowMode != TextOverflowModes.Page)
03549                     {
03550                         characterInfos[i].vertex_BL.position += offset;
03551                         characterInfos[i].vertex_TL.position += offset;
03552                         characterInfos[i].vertex_TR.position += offset;
03553                         characterInfos[i].vertex_BR.position += offset;
03554                     }
03555                     else if (i < m_maxVisibleCharacters && wordCount < m_maxVisibleWords && currentLine < m_maxVisibleLines && m_overflowMode == TextOverflowModes.Page && characterInfos[i].pageNumber == pageToDisplay)
03556                     {
03557                         characterInfos[i].vertex_BL.position += offset;
03558                         characterInfos[i].vertex_TL.position += offset;
03559                         characterInfos[i].vertex_TR.position += offset;
03560                         characterInfos[i].vertex_BR.position += offset;
03561                     }
03562                     else
03563                     {
03564                         characterInfos[i].vertex_BL.position = Vector3.zero;
03565                         characterInfos[i].vertex_TL.position = Vector3.zero;
03566                         characterInfos[i].vertex_TR.position = Vector3.zero;
03567                         characterInfos[i].vertex_BR.position = Vector3.zero;
03568                         characterInfos[i].isVisible = false;
03569                     }
03570                     #if TMP_PROFILE_ON
03571                     Profiler.EndSample();
03572                     #endif
03573                     #endregion
03574 
03575 
03576                     // Fill Vertex Buffers for the various types of element
03577                     if (elementType == TMP_TextElementType.Character)
03578                     {
03579                         FillCharacterVertexBuffers(i, vert_index_X4);
03580                     }
03581                     else if (elementType == TMP_TextElementType.Sprite)
03582                     {
03583                         FillSpriteVertexBuffers(i, sprite_index_X4);
03584                     }
03585                 }
03586                 #endregion
03587 
03588                 // Apply Alignment and Justification Offset
03589                 m_textInfo.characterInfo[i].bottomLeft += offset;
03590                 m_textInfo.characterInfo[i].topLeft += offset;
03591                 m_textInfo.characterInfo[i].topRight += offset;
03592                 m_textInfo.characterInfo[i].bottomRight += offset;
03593 
03594                 m_textInfo.characterInfo[i].origin += offset.x;
03595                 m_textInfo.characterInfo[i].xAdvance += offset.x;
03596 
03597                 m_textInfo.characterInfo[i].ascender += offset.y;
03598                 m_textInfo.characterInfo[i].descender += offset.y;
03599                 m_textInfo.characterInfo[i].baseLine += offset.y;
03600 
03601                 // Update MeshExtents
03602                 if (isCharacterVisible)
03603                 {
03604                     //m_meshExtents.min = new Vector2(Mathf.Min(m_meshExtents.min.x, m_textInfo.characterInfo[i].bottomLeft.x), Mathf.Min(m_meshExtents.min.y, m_textInfo.characterInfo[i].bottomLeft.y));
03605                     //m_meshExtents.max = new Vector2(Mathf.Max(m_meshExtents.max.x, m_textInfo.characterInfo[i].topRight.x), Mathf.Max(m_meshExtents.max.y, m_textInfo.characterInfo[i].topLeft.y));
03606                 }
03607 
03608                 // Need to recompute lineExtent to account for the offset from justification.
03609                 #region Adjust lineExtents resulting from alignment offset
03610                 #if TMP_PROFILE_ON
03611                     Profiler.BeginSample("Adjust LineExtents");
03612                 #endif
03613                 if (currentLine != lastLine || i == m_characterCount - 1)
03614                 {
03615                     // Update the previous line's extents
03616                     if (currentLine != lastLine)
03617                     {
03618                         m_textInfo.lineInfo[lastLine].baseline += offset.y;
03619                         m_textInfo.lineInfo[lastLine].ascender += offset.y;
03620                         m_textInfo.lineInfo[lastLine].descender += offset.y;
03621 
03622                         m_textInfo.lineInfo[lastLine].lineExtents.min = new Vector2(m_textInfo.characterInfo[m_textInfo.lineInfo[lastLine].firstCharacterIndex].bottomLeft.x, m_textInfo.lineInfo[lastLine].descender);
03623                         m_textInfo.lineInfo[lastLine].lineExtents.max = new Vector2(m_textInfo.characterInfo[m_textInfo.lineInfo[lastLine].lastVisibleCharacterIndex].topRight.x, m_textInfo.lineInfo[lastLine].ascender);
03624                     }
03625 
03626                     // Update the current line's extents
03627                     if (i == m_characterCount - 1)
03628                     {
03629                         m_textInfo.lineInfo[currentLine].baseline += offset.y;
03630                         m_textInfo.lineInfo[currentLine].ascender += offset.y;
03631                         m_textInfo.lineInfo[currentLine].descender += offset.y;
03632 
03633                         m_textInfo.lineInfo[currentLine].lineExtents.min = new Vector2(m_textInfo.characterInfo[m_textInfo.lineInfo[currentLine].firstCharacterIndex].bottomLeft.x, m_textInfo.lineInfo[currentLine].descender);
03634                         m_textInfo.lineInfo[currentLine].lineExtents.max = new Vector2(m_textInfo.characterInfo[m_textInfo.lineInfo[currentLine].lastVisibleCharacterIndex].topRight.x, m_textInfo.lineInfo[currentLine].ascender);
03635                     }
03636                 }
03637                 #if TMP_PROFILE_ON
03638                 Profiler.EndSample();
03639                 #endif
03640                 #endregion
03641 
03642 
03643                 // Track Word Count per line and for the object
03644                 #region Track Word Count
03645                 #if TMP_PROFILE_ON
03646                 Profiler.BeginSample("Track Word Count");
03647                 #endif
03648                 if (char.IsLetterOrDigit(currentCharacter) || currentCharacter == 0x2D || currentCharacter == 0xAD || currentCharacter == 0x2010 || currentCharacter == 0x2011)
03649                 {
03650                     if (isStartOfWord == false)
03651                     {
03652                         isStartOfWord = true;
03653                         wordFirstChar = i;
03654                     }
03655 
03656                     // If last character is a word
03657                     if (isStartOfWord && i == m_characterCount - 1)
03658                     {
03659                         int size = m_textInfo.wordInfo.Length;
03660                         int index = m_textInfo.wordCount;
03661 
03662                         if (m_textInfo.wordCount + 1 > size)
03663                             TMP_TextInfo.Resize(ref m_textInfo.wordInfo, size + 1);
03664 
03665                         wordLastChar = i;
03666 
03667                         m_textInfo.wordInfo[index].firstCharacterIndex = wordFirstChar;
03668                         m_textInfo.wordInfo[index].lastCharacterIndex = wordLastChar;
03669                         m_textInfo.wordInfo[index].characterCount = wordLastChar - wordFirstChar + 1;
03670                         m_textInfo.wordInfo[index].textComponent = this;
03671 
03672                         wordCount += 1;
03673                         m_textInfo.wordCount += 1;
03674                         m_textInfo.lineInfo[currentLine].wordCount += 1;
03675                     }
03676                 }
03677                 else if (isStartOfWord || i == 0 && (!char.IsPunctuation(currentCharacter) || char.IsWhiteSpace(currentCharacter) || currentCharacter == 0x200B || i == m_characterCount - 1))
03678                 {
03679                     if (i > 0 && i < characterInfos.Length - 1 && i < m_characterCount && (currentCharacter == 39 || currentCharacter == 8217) && char.IsLetterOrDigit(characterInfos[i - 1].character) && char.IsLetterOrDigit(characterInfos[i + 1].character))
03680                     {
03681 
03682                     }
03683                     else
03684                     {
03685                         wordLastChar = i == m_characterCount - 1 && char.IsLetterOrDigit(currentCharacter) ? i : i - 1;
03686                         isStartOfWord = false;
03687 
03688                         int size = m_textInfo.wordInfo.Length;
03689                         int index = m_textInfo.wordCount;
03690 
03691                         if (m_textInfo.wordCount + 1 > size)
03692                             TMP_TextInfo.Resize(ref m_textInfo.wordInfo, size + 1);
03693 
03694                         m_textInfo.wordInfo[index].firstCharacterIndex = wordFirstChar;
03695                         m_textInfo.wordInfo[index].lastCharacterIndex = wordLastChar;
03696                         m_textInfo.wordInfo[index].characterCount = wordLastChar - wordFirstChar + 1;
03697                         m_textInfo.wordInfo[index].textComponent = this;
03698 
03699                         wordCount += 1;
03700                         m_textInfo.wordCount += 1;
03701                         m_textInfo.lineInfo[currentLine].wordCount += 1;
03702                     }
03703                 }
03704                 #if TMP_PROFILE_ON
03705                 Profiler.EndSample();
03706                 #endif
03707                 #endregion
03708 
03709 
03710                 // Setup & Handle Underline
03711                 #region Underline
03712                 #if TMP_PROFILE_ON
03713                 Profiler.BeginSample("Process Underline & Strikethrough");
03714                 #endif
03715                 // NOTE: Need to figure out how underline will be handled with multiple fonts and which font will be used for the underline.
03716                 bool isUnderline = (m_textInfo.characterInfo[i].style & FontStyles.Underline) == FontStyles.Underline;
03717                 if (isUnderline)
03718                 {
03719                     bool isUnderlineVisible = true;
03720                     int currentPage = m_textInfo.characterInfo[i].pageNumber;
03721 
03722                     if (i > m_maxVisibleCharacters || currentLine > m_maxVisibleLines || (m_overflowMode == TextOverflowModes.Page && currentPage + 1 != m_pageToDisplay))
03723                         isUnderlineVisible = false;
03724 
03725                     // We only use the scale of visible characters.
03726                     if (!char.IsWhiteSpace(currentCharacter) && currentCharacter != 0x200B)
03727                     {
03728                         underlineMaxScale = Mathf.Max(underlineMaxScale, m_textInfo.characterInfo[i].scale);
03729                         underlineBaseLine = Mathf.Min(currentPage == lastPage ? underlineBaseLine : k_LargePositiveFloat, m_textInfo.characterInfo[i].baseLine + font.fontInfo.Underline * underlineMaxScale);
03730                         lastPage = currentPage; // Need to track pages to ensure we reset baseline for the new pages.
03731                     }
03732 
03733                     if (beginUnderline == false && isUnderlineVisible == true && i <= lineInfo.lastVisibleCharacterIndex && currentCharacter != 10 && currentCharacter != 13)
03734                     {
03735                         if (i == lineInfo.lastVisibleCharacterIndex && char.IsSeparator(currentCharacter))
03736                         { }
03737                         else
03738                         {
03739                             beginUnderline = true;
03740                             underlineStartScale = m_textInfo.characterInfo[i].scale;
03741                             if (underlineMaxScale == 0) underlineMaxScale = underlineStartScale;
03742                             underline_start = new Vector3(m_textInfo.characterInfo[i].bottomLeft.x, underlineBaseLine, 0);
03743                             underlineColor = m_textInfo.characterInfo[i].underlineColor;
03744                         }
03745                     }
03746 
03747                     // End Underline if text only contains one character.
03748                     if (beginUnderline && m_characterCount == 1)
03749                     {
03750                         beginUnderline = false;
03751                         underline_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, underlineBaseLine, 0);
03752                         underlineEndScale = m_textInfo.characterInfo[i].scale;
03753 
03754                         DrawUnderlineMesh(underline_start, underline_end, ref last_vert_index, underlineStartScale, underlineEndScale, underlineMaxScale, xScale, underlineColor);
03755                         underlineMaxScale = 0;
03756                         underlineBaseLine = k_LargePositiveFloat;
03757                     }
03758                     else if (beginUnderline && (i == lineInfo.lastCharacterIndex || i >= lineInfo.lastVisibleCharacterIndex))
03759                     {
03760                         // Terminate underline at previous visible character if space or carriage return.
03761                         if (char.IsWhiteSpace(currentCharacter) || currentCharacter == 0x200B)
03762                         {
03763                             int lastVisibleCharacterIndex = lineInfo.lastVisibleCharacterIndex;
03764                             underline_end = new Vector3(m_textInfo.characterInfo[lastVisibleCharacterIndex].topRight.x, underlineBaseLine, 0);
03765                             underlineEndScale = m_textInfo.characterInfo[lastVisibleCharacterIndex].scale;
03766                         }
03767                         else
03768                         {   // End underline if last character of the line.
03769                             underline_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, underlineBaseLine, 0);
03770                             underlineEndScale = m_textInfo.characterInfo[i].scale;
03771                         }
03772 
03773                         beginUnderline = false;
03774                         DrawUnderlineMesh(underline_start, underline_end, ref last_vert_index, underlineStartScale, underlineEndScale, underlineMaxScale, xScale, underlineColor);
03775                         underlineMaxScale = 0;
03776                         underlineBaseLine = k_LargePositiveFloat;
03777                     }
03778                     else if (beginUnderline && !isUnderlineVisible)
03779                     {
03780                         beginUnderline = false;
03781                         underline_end = new Vector3(m_textInfo.characterInfo[i - 1].topRight.x, underlineBaseLine, 0);
03782                         underlineEndScale = m_textInfo.characterInfo[i - 1].scale;
03783 
03784                         DrawUnderlineMesh(underline_start, underline_end, ref last_vert_index, underlineStartScale, underlineEndScale, underlineMaxScale, xScale, underlineColor);
03785                         underlineMaxScale = 0;
03786                         underlineBaseLine = k_LargePositiveFloat;
03787                     }
03788                     else if (beginUnderline && i < m_characterCount - 1 && !underlineColor.Compare(m_textInfo.characterInfo[i + 1].underlineColor))
03789                     {
03790                         // End underline if underline color has changed.
03791                         beginUnderline = false;
03792                         underline_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, underlineBaseLine, 0);
03793                         underlineEndScale = m_textInfo.characterInfo[i].scale;
03794 
03795                         DrawUnderlineMesh(underline_start, underline_end, ref last_vert_index, underlineStartScale, underlineEndScale, underlineMaxScale, xScale, underlineColor);
03796                         underlineMaxScale = 0;
03797                         underlineBaseLine = k_LargePositiveFloat;
03798                     }
03799                 }
03800                 else
03801                 {
03802                     // End Underline
03803                     if (beginUnderline == true)
03804                     {
03805                         beginUnderline = false;
03806                         underline_end = new Vector3(m_textInfo.characterInfo[i - 1].topRight.x, underlineBaseLine, 0);
03807                         underlineEndScale = m_textInfo.characterInfo[i - 1].scale;
03808 
03809                         DrawUnderlineMesh(underline_start, underline_end, ref last_vert_index, underlineStartScale, underlineEndScale, underlineMaxScale, xScale, underlineColor);
03810                         underlineMaxScale = 0;
03811                         underlineBaseLine = k_LargePositiveFloat;
03812                     }
03813                 }
03814                 #endregion
03815 
03816 
03817                 // Setup & Handle Strikethrough
03818                 #region Strikethrough
03819                 // NOTE: Need to figure out how underline will be handled with multiple fonts and which font will be used for the underline.
03820                 bool isStrikethrough = (m_textInfo.characterInfo[i].style & FontStyles.Strikethrough) == FontStyles.Strikethrough;
03821                 float strikethroughOffset = currentFontAsset.fontInfo.strikethrough;
03822 
03823                 if (isStrikethrough)
03824                 {
03825                     bool isStrikeThroughVisible = true;
03826 
03827                     if (i > m_maxVisibleCharacters || currentLine > m_maxVisibleLines || (m_overflowMode == TextOverflowModes.Page && m_textInfo.characterInfo[i].pageNumber + 1 != m_pageToDisplay))
03828                         isStrikeThroughVisible = false;
03829 
03830                     if (beginStrikethrough == false && isStrikeThroughVisible && i <= lineInfo.lastVisibleCharacterIndex && currentCharacter != 10 && currentCharacter != 13)
03831                     {
03832                         if (i == lineInfo.lastVisibleCharacterIndex && char.IsSeparator(currentCharacter))
03833                         { }
03834                         else
03835                         {
03836                             beginStrikethrough = true;
03837                             strikethroughPointSize = m_textInfo.characterInfo[i].pointSize;
03838                             strikethroughScale = m_textInfo.characterInfo[i].scale;
03839                             strikethrough_start = new Vector3(m_textInfo.characterInfo[i].bottomLeft.x, m_textInfo.characterInfo[i].baseLine + strikethroughOffset * strikethroughScale, 0);
03840                             strikethroughColor = m_textInfo.characterInfo[i].strikethroughColor;
03841                             strikethroughBaseline = m_textInfo.characterInfo[i].baseLine;
03842                             //Debug.Log("Char [" + currentCharacter + "] Start Strikethrough POS: " + strikethrough_start);
03843                         }
03844                     }
03845 
03846                     // End Strikethrough if text only contains one character.
03847                     if (beginStrikethrough && m_characterCount == 1)
03848                     {
03849                         beginStrikethrough = false;
03850                         strikethrough_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, m_textInfo.characterInfo[i].baseLine + strikethroughOffset * strikethroughScale, 0);
03851 
03852                         DrawUnderlineMesh(strikethrough_start, strikethrough_end, ref last_vert_index, strikethroughScale, strikethroughScale, strikethroughScale, xScale, strikethroughColor);
03853                     }
03854                     else if (beginStrikethrough && i == lineInfo.lastCharacterIndex)
03855                     {
03856                         // Terminate Strikethrough at previous visible character if space or carriage return.
03857                         if (char.IsWhiteSpace(currentCharacter) || currentCharacter == 0x200B)
03858                         {
03859                             int lastVisibleCharacterIndex = lineInfo.lastVisibleCharacterIndex;
03860                             strikethrough_end = new Vector3(m_textInfo.characterInfo[lastVisibleCharacterIndex].topRight.x, m_textInfo.characterInfo[lastVisibleCharacterIndex].baseLine + strikethroughOffset * strikethroughScale, 0);
03861                         }
03862                         else
03863                         {
03864                             // Terminate Strikethrough at last character of line.
03865                             strikethrough_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, m_textInfo.characterInfo[i].baseLine + strikethroughOffset * strikethroughScale, 0);
03866                         }
03867 
03868                         beginStrikethrough = false;
03869                         DrawUnderlineMesh(strikethrough_start, strikethrough_end, ref last_vert_index, strikethroughScale, strikethroughScale, strikethroughScale, xScale, strikethroughColor);
03870                     }
03871                     else if (beginStrikethrough && i < m_characterCount && (m_textInfo.characterInfo[i + 1].pointSize != strikethroughPointSize || !TMP_Math.Approximately(m_textInfo.characterInfo[i + 1].baseLine + offset.y, strikethroughBaseline)))
03872                     {
03873                         // Terminate Strikethrough if scale changes.
03874                         beginStrikethrough = false;
03875 
03876                         int lastVisibleCharacterIndex = lineInfo.lastVisibleCharacterIndex;
03877                         if (i > lastVisibleCharacterIndex)
03878                             strikethrough_end = new Vector3(m_textInfo.characterInfo[lastVisibleCharacterIndex].topRight.x, m_textInfo.characterInfo[lastVisibleCharacterIndex].baseLine + strikethroughOffset * strikethroughScale, 0);
03879                         else
03880                             strikethrough_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, m_textInfo.characterInfo[i].baseLine + strikethroughOffset * strikethroughScale, 0);
03881 
03882                         DrawUnderlineMesh(strikethrough_start, strikethrough_end, ref last_vert_index, strikethroughScale, strikethroughScale, strikethroughScale, xScale, strikethroughColor);
03883                         //Debug.Log("Char [" + currentCharacter + "] at Index: " + i + "  End Strikethrough POS: " + strikethrough_end + "  Baseline: " + m_textInfo.characterInfo[i].baseLine.ToString("f3"));
03884                     }
03885                     else if (beginStrikethrough && i < m_characterCount && currentFontAsset.GetInstanceID() != characterInfos[i + 1].fontAsset.GetInstanceID())
03886                     {
03887                         // Terminate Strikethrough if font asset changes.
03888                         beginStrikethrough = false;
03889                         strikethrough_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, m_textInfo.characterInfo[i].baseLine + strikethroughOffset * strikethroughScale, 0);
03890 
03891                         DrawUnderlineMesh(strikethrough_start, strikethrough_end, ref last_vert_index, strikethroughScale, strikethroughScale, strikethroughScale, xScale, strikethroughColor);
03892                     }
03893                     else if (beginStrikethrough && !isStrikeThroughVisible)
03894                     {
03895                         // Terminate Strikethrough if character is not visible.
03896                         beginStrikethrough = false;
03897                         strikethrough_end = new Vector3(m_textInfo.characterInfo[i - 1].topRight.x, m_textInfo.characterInfo[i - 1].baseLine + strikethroughOffset * strikethroughScale, 0);
03898 
03899                         DrawUnderlineMesh(strikethrough_start, strikethrough_end, ref last_vert_index, strikethroughScale, strikethroughScale, strikethroughScale, xScale, strikethroughColor);
03900                     }
03901                 }
03902                 else
03903                 {
03904                     // End Strikethrough
03905                     if (beginStrikethrough == true)
03906                     {
03907                         beginStrikethrough = false;
03908                         strikethrough_end = new Vector3(m_textInfo.characterInfo[i - 1].topRight.x, m_textInfo.characterInfo[i - 1].baseLine + strikethroughOffset * strikethroughScale, 0);
03909 
03910                         DrawUnderlineMesh(strikethrough_start, strikethrough_end, ref last_vert_index, strikethroughScale, strikethroughScale, strikethroughScale, xScale, strikethroughColor);
03911                     }
03912                 }
03913                 #endregion
03914 
03915 
03916                 // HANDLE TEXT HIGHLIGHTING
03917                 #region Text Highlighting
03918                 bool isHighlight = (m_textInfo.characterInfo[i].style & FontStyles.Highlight) == FontStyles.Highlight;
03919                 if (isHighlight)
03920                 {
03921                     bool isHighlightVisible = true;
03922                     int currentPage = m_textInfo.characterInfo[i].pageNumber;
03923 
03924                     if (i > m_maxVisibleCharacters || currentLine > m_maxVisibleLines || (m_overflowMode == TextOverflowModes.Page && currentPage + 1 != m_pageToDisplay))
03925                         isHighlightVisible = false;
03926 
03927                     if (beginHighlight == false && isHighlightVisible == true && i <= lineInfo.lastVisibleCharacterIndex && currentCharacter != 10 && currentCharacter != 13)
03928                     {
03929                         if (i == lineInfo.lastVisibleCharacterIndex && char.IsSeparator(currentCharacter))
03930                         { }
03931                         else
03932                         {
03933                             beginHighlight = true;
03934                             highlight_start = k_LargePositiveVector2;
03935                             highlight_end = k_LargeNegativeVector2;
03936                             highlightColor = m_textInfo.characterInfo[i].highlightColor;
03937                         }
03938                     }
03939 
03940                     if (beginHighlight)
03941                     {
03942                         Color32 currentHighlightColor = m_textInfo.characterInfo[i].highlightColor;
03943                         bool isColorTransition = false;
03944 
03945                         // Handle Highlight color changes
03946                         if (!highlightColor.Compare(currentHighlightColor))
03947                         {
03948                             // End drawing at the start of new highlight color to prevent a gap between highlight sections.
03949                             highlight_end.x = (highlight_end.x + m_textInfo.characterInfo[i].bottomLeft.x) / 2;
03950 
03951                             highlight_start.y = Mathf.Min(highlight_start.y, m_textInfo.characterInfo[i].descender);
03952                             highlight_end.y = Mathf.Max(highlight_end.y, m_textInfo.characterInfo[i].ascender);
03953 
03954                             DrawTextHighlight(highlight_start, highlight_end, ref last_vert_index, highlightColor);
03955 
03956                             beginHighlight = true;
03957                             highlight_start = highlight_end;
03958 
03959                             highlight_end = new Vector3(m_textInfo.characterInfo[i].topRight.x, m_textInfo.characterInfo[i].descender, 0);
03960                             highlightColor = m_textInfo.characterInfo[i].highlightColor;
03961 
03962                             isColorTransition = true;
03963                         }
03964 
03965                         if (!isColorTransition)
03966                         {
03967                             // Use the Min / Max Extents of the Highlight area to handle different character sizes and fonts.
03968                             highlight_start.x = Mathf.Min(highlight_start.x, m_textInfo.characterInfo[i].bottomLeft.x);
03969                             highlight_start.y = Mathf.Min(highlight_start.y, m_textInfo.characterInfo[i].descender);
03970 
03971                             highlight_end.x = Mathf.Max(highlight_end.x, m_textInfo.characterInfo[i].topRight.x); 
03972                             highlight_end.y = Mathf.Max(highlight_end.y, m_textInfo.characterInfo[i].ascender);
03973                         }
03974                     }
03975 
03976                     // End Highlight if text only contains one character.
03977                     if (beginHighlight && m_characterCount == 1)
03978                     {
03979                         beginHighlight = false;
03980 
03981                         DrawTextHighlight(highlight_start, highlight_end, ref last_vert_index, highlightColor);
03982                     }
03983                     else if (beginHighlight && (i == lineInfo.lastCharacterIndex || i >= lineInfo.lastVisibleCharacterIndex))
03984                     {
03985                         beginHighlight = false;
03986                         DrawTextHighlight(highlight_start, highlight_end, ref last_vert_index, highlightColor);
03987                     }
03988                     else if (beginHighlight && !isHighlightVisible)
03989                     {
03990                         beginHighlight = false;
03991                         DrawTextHighlight(highlight_start, highlight_end, ref last_vert_index, highlightColor);
03992                     }
03993                 }
03994                 else
03995                 {
03996                     // End Highlight
03997                     if (beginHighlight == true)
03998                     {
03999                         beginHighlight = false;
04000                         DrawTextHighlight(highlight_start, highlight_end, ref last_vert_index, highlightColor);
04001                     }
04002                 }
04003                 #endregion
04004                 #if TMP_PROFILE_ON
04005                 Profiler.EndSample();
04006                 #endif
04007                 #endregion
04008 
04009                 lastLine = currentLine;
04010             }
04011             #endregion
04012 
04013 
04014             // METRICS ABOUT THE TEXT OBJECT
04015             m_textInfo.characterCount = m_characterCount;
04016             m_textInfo.spriteCount = m_spriteCount;
04017             m_textInfo.lineCount = lineCount;
04018             m_textInfo.wordCount = wordCount != 0 && m_characterCount > 0 ? wordCount : 1;
04019             m_textInfo.pageCount = m_pageNumber + 1;
04020 
04021             #if TMP_PROFILE_PHASES_ON
04022                 Profiler.EndSample();
04023             #endif
04024 
04025 
04026             // *** UPLOAD MESH DATA ***
04027             #if TMP_PROFILE_PHASES_ON
04028                 Profiler.BeginSample("TMP Generate Text - Phase III");
04029             #endif
04030             if (m_renderMode == TextRenderFlags.Render && IsActive())
04031             {
04032                 // Clear unused vertices
04033                 //m_textInfo.meshInfo[0].ClearUnusedVertices();
04034 
04035                 // Must ensure the Canvas support the additon vertex attributes used by TMP.
04036                 if (m_canvas.additionalShaderChannels != (AdditionalCanvasShaderChannels)25)
04037                     m_canvas.additionalShaderChannels |= (AdditionalCanvasShaderChannels)25;
04038 
04039                 // Sort the geometry of the text object if needed.
04040                 if (m_geometrySortingOrder != VertexSortingOrder.Normal)
04041                     m_textInfo.meshInfo[0].SortGeometry(VertexSortingOrder.Reverse);
04042 
04043                 // Upload Mesh Data
04044                 m_mesh.MarkDynamic();
04045                 m_mesh.vertices = m_textInfo.meshInfo[0].vertices;
04046                 m_mesh.uv = m_textInfo.meshInfo[0].uvs0;
04047                 m_mesh.uv2 = m_textInfo.meshInfo[0].uvs2;
04048                 //m_mesh.uv4 = m_textInfo.meshInfo[0].uvs4;
04049                 m_mesh.colors32 = m_textInfo.meshInfo[0].colors32;
04050 
04051                 // Compute Bounds for the mesh. Manual computation is more efficient then using Mesh.recalcualteBounds.
04052                 m_mesh.RecalculateBounds();
04053                 //m_mesh.bounds = new Bounds(new Vector3((m_meshExtents.max.x + m_meshExtents.min.x) / 2, (m_meshExtents.max.y + m_meshExtents.min.y) / 2, 0) + offset, new Vector3(m_meshExtents.max.x - m_meshExtents.min.x, m_meshExtents.max.y - m_meshExtents.min.y, 0));
04054 
04055                 m_canvasRenderer.SetMesh(m_mesh);
04056 
04057                 // Cache CanvasRenderer color of the parent text object.
04058                 Color parentBaseColor = m_canvasRenderer.GetColor();
04059 
04060                 for (int i = 1; i < m_textInfo.materialCount; i++)
04061                 {
04062                     // Clear unused vertices
04063                     m_textInfo.meshInfo[i].ClearUnusedVertices();
04064 
04065                     if (m_subTextObjects[i] == null) continue;
04066 
04067                     // Sort the geometry of the sub-text objects if needed.
04068                     if (m_geometrySortingOrder != VertexSortingOrder.Normal)
04069                         m_textInfo.meshInfo[i].SortGeometry(VertexSortingOrder.Reverse);
04070 
04071                     //m_subTextObjects[i].mesh.MarkDynamic();
04072                     m_subTextObjects[i].mesh.vertices = m_textInfo.meshInfo[i].vertices;
04073                     m_subTextObjects[i].mesh.uv = m_textInfo.meshInfo[i].uvs0;
04074                     m_subTextObjects[i].mesh.uv2 = m_textInfo.meshInfo[i].uvs2;
04075                     //m_subTextObjects[i].mesh.uv4 = m_textInfo.meshInfo[i].uvs4;
04076                     m_subTextObjects[i].mesh.colors32 = m_textInfo.meshInfo[i].colors32;
04077 
04078                     m_subTextObjects[i].mesh.RecalculateBounds();
04079 
04080                     m_subTextObjects[i].canvasRenderer.SetMesh(m_subTextObjects[i].mesh);
04081 
04082                     // Set CanvasRenderer color to match the parent text object.
04083                     m_subTextObjects[i].canvasRenderer.SetColor(parentBaseColor);
04084                 }
04085             }
04086 
04087             // Event indicating the text has been regenerated.
04088             TMPro_EventManager.ON_TEXT_CHANGED(this);
04089             //SendOnTextChanged();
04090 
04091             #if TMP_PROFILE_PHASES_ON
04092                 Profiler.EndSample();
04093             #endif
04094 
04095             //Debug.Log("Done Rendering Text.");
04096         }
