04103         protected override Vector3[] GetTextContainerLocalCorners()
04104         {
04105             if (m_rectTransform == null) m_rectTransform = this.rectTransform;
04106 
04107             m_rectTransform.GetLocalCorners(m_RectTransformCorners);
04108 
04109             return m_RectTransformCorners;
04110         }
