04117         protected override void SetActiveSubMeshes(bool state)
04118         {
04119             for (int i = 1; i < m_subTextObjects.Length && m_subTextObjects[i] != null; i++)
04120             {
04121                 if (m_subTextObjects[i].enabled != state)
04122                     m_subTextObjects[i].enabled = state;
04123             }
04124         }
04125 
04126 
04131         protected override Bounds GetCompoundBounds()
04132         {
04133             Bounds mainBounds = m_mesh.bounds;
04134             Vector3 min = mainBounds.min;
04135             Vector3 max = mainBounds.max;
04136 
04137             for (int i = 1; i < m_subTextObjects.Length && m_subTextObjects[i] != null; i++)
04138             {
04139                 Bounds subBounds = m_subTextObjects[i].mesh.bounds;
04140                 min.x = min.x < subBounds.min.x ? min.x : subBounds.min.x;
04141                 min.y = min.y < subBounds.min.y ? min.y : subBounds.min.y;
04142 
04143                 max.x = max.x > subBounds.max.x ? max.x : subBounds.max.x;
04144                 max.y = max.y > subBounds.max.y ? max.y : subBounds.max.y;
04145             }
04146 
04147             Vector3 center = (min + max) / 2;
04148             Vector2 size = max - min;
04149             return new Bounds(center, size);
04150         }
