04158 
04162         void UpdateSDFScale(float lossyScale)
04163         {
04164             //Debug.Log("Updating SDF Scale.");
04165 
04166             // Return if we don't have a valid reference to a Canvas.
04167             if (m_canvas == null)
04168             {
04169                 m_canvas = GetCanvas();
04170                 if (m_canvas == null) return;
04171             }
04172 
04173             lossyScale = lossyScale == 0 ? 1 : lossyScale;
04174 
04175             float xScale = 0;
04176             float canvasScaleFactor = m_canvas.scaleFactor;
04177 
04178             if (m_canvas.renderMode == RenderMode.ScreenSpaceOverlay)
04179                 xScale = lossyScale / canvasScaleFactor;
04180             else if (m_canvas.renderMode == RenderMode.ScreenSpaceCamera)
04181                 xScale = m_canvas.worldCamera != null ? lossyScale : 1;
04182             else
04183                 xScale = lossyScale;
04184 
04185             // Iterate through each of the characters.
04186             for (int i = 0; i < m_textInfo.characterCount; i++)
04187             {
04188                 // Only update scale for visible characters.
04189                 if (m_textInfo.characterInfo[i].isVisible && m_textInfo.characterInfo[i].elementType == TMP_TextElementType.Character)
04190                 {
04191                     float scale = xScale * m_textInfo.characterInfo[i].scale * (1 - m_charWidthAdjDelta);
04192                     if (!m_textInfo.characterInfo[i].isUsingAlternateTypeface && (m_textInfo.characterInfo[i].style & FontStyles.Bold) == FontStyles.Bold) scale *= -1;
04193 
04194                     int index = m_textInfo.characterInfo[i].materialReferenceIndex;
04195                     int vertexIndex = m_textInfo.characterInfo[i].vertexIndex;
04196 
04197                     m_textInfo.meshInfo[index].uvs2[vertexIndex + 0].y = scale;
04198                     m_textInfo.meshInfo[index].uvs2[vertexIndex + 1].y = scale;
04199                     m_textInfo.meshInfo[index].uvs2[vertexIndex + 2].y = scale;
04200                     m_textInfo.meshInfo[index].uvs2[vertexIndex + 3].y = scale;
04201                 }
04202             }
04203 
04204             // Push the updated uv2 scale information to the meshes.
04205             for (int i = 0; i < m_textInfo.materialCount; i++)
04206             {
04207                 if (i == 0)
04208                 {
04209                     m_mesh.uv2 = m_textInfo.meshInfo[0].uvs2;
04210                     m_canvasRenderer.SetMesh(m_mesh);
04211                 }
04212                 else
04213                 {
04214                     m_subTextObjects[i].mesh.uv2 = m_textInfo.meshInfo[i].uvs2;
04215                     m_subTextObjects[i].canvasRenderer.SetMesh(m_subTextObjects[i].mesh);
04216                 }
04217             }
04218         }
04219 
04220 
04221         // Function to offset vertices position to account for line spacing changes.
04222         protected override void AdjustLineOffset(int startIndex, int endIndex, float offset)
04223         {
04224             Vector3 vertexOffset = new Vector3(0, offset, 0);
04225 
04226             for (int i = startIndex; i <= endIndex; i++)
04227             {
04228                 m_textInfo.characterInfo[i].bottomLeft -= vertexOffset;
04229                 m_textInfo.characterInfo[i].topLeft -= vertexOffset;
04230                 m_textInfo.characterInfo[i].topRight -= vertexOffset;
04231                 m_textInfo.characterInfo[i].bottomRight -= vertexOffset;
04232 
04233                 m_textInfo.characterInfo[i].ascender -= vertexOffset.y;
04234                 m_textInfo.characterInfo[i].baseLine -= vertexOffset.y;
04235                 m_textInfo.characterInfo[i].descender -= vertexOffset.y;
04236 
04237                 if (m_textInfo.characterInfo[i].isVisible)
04238                 {
04239                     m_textInfo.characterInfo[i].vertex_BL.position -= vertexOffset;
04240                     m_textInfo.characterInfo[i].vertex_TL.position -= vertexOffset;
04241                     m_textInfo.characterInfo[i].vertex_TR.position -= vertexOffset;
04242                     m_textInfo.characterInfo[i].vertex_BR.position -= vertexOffset;
04243                 }
04244             }
04245         }
04246 
04247     }
