00208         private void CheckForSpawnPoint()
00209         {
00210             foreach ( TeleportMarkerBase teleportMarker in teleportMarkers )
00211             {
00212                 TeleportPoint teleportPoint = teleportMarker as TeleportPoint;
00213                 if ( teleportPoint && teleportPoint.playerSpawnPoint )
00214                 {
00215                     teleportingToMarker = teleportMarker;
00216                     TeleportPlayer();
00217                     break;
00218                 }
00219             }
00220         }
