00233         //-------------------------------------------------
00234         void Update()
00235         {
00236             Hand oldPointerHand = pointerHand;
00237             Hand newPointerHand = null;
00238 
00239             foreach ( Hand hand in player.hands )
00240             {
00241                 if ( visible )
00242                 {
00243                     if ( WasTeleportButtonReleased( hand ) )
00244                     {
00245                         if ( pointerHand == hand ) //This is the pointer hand
00246                         {
00247                             TryTeleportPlayer();
00248                         }
00249                     }
00250                 }
00251 
00252                 if ( WasTeleportButtonPressed( hand ) )
00253                 {
00254                     newPointerHand = hand;
00255                 }
00256             }
00257 
00258             //If something is attached to the hand that is preventing teleport
00259             if ( allowTeleportWhileAttached && !allowTeleportWhileAttached.teleportAllowed )
00260             {
00261                 HidePointer();
00262             }
00263             else
00264             {
00265                 if ( !visible && newPointerHand != null )
00266                 {
00267                     //Begin showing the pointer
00268                     ShowPointer( newPointerHand, oldPointerHand );
00269                 }
00270                 else if ( visible )
00271                 {
00272                     if ( newPointerHand == null && !IsTeleportButtonDown( pointerHand ) )
00273                     {
00274                         //Hide the pointer
00275                         HidePointer();
00276                     }
00277                     else if ( newPointerHand != null )
00278                     {
00279                         //Move the pointer to a new hand
00280                         ShowPointer( newPointerHand, oldPointerHand );
00281                     }
00282                 }
00283             }
00284 
00285             if ( visible )
00286             {
00287                 UpdatePointer();
00288 
00289                 if ( meshFading )
00290                 {
00291                     UpdateTeleportColors();
00292                 }
00293 
00294                 if ( onActivateObjectTransform.gameObject.activeSelf && Time.time - pointerShowStartTime > activateObjectTime )
00295                 {
00296                     onActivateObjectTransform.gameObject.SetActive( false );
00297                 }
00298             }
00299             else
00300             {
00301                 if ( onDeactivateObjectTransform.gameObject.activeSelf && Time.time - pointerHideStartTime > deactivateObjectTime )
00302                 {
00303                     onDeactivateObjectTransform.gameObject.SetActive( false );
00304                 }
00305             }
00306         }
