00309         //-------------------------------------------------
00310         private void UpdatePointer()
00311         {
00312             Vector3 pointerStart = pointerStartTransform.position;
00313             Vector3 pointerEnd;
00314             Vector3 pointerDir = pointerStartTransform.forward;
00315             bool hitSomething = false;
00316             bool showPlayAreaPreview = false;
00317             Vector3 playerFeetOffset = player.trackingOriginTransform.position - player.feetPositionGuess;
00318 
00319             Vector3 arcVelocity = pointerDir * arcDistance;
00320 
00321             TeleportMarkerBase hitTeleportMarker = null;
00322 
00323             //Check pointer angle
00324             float dotUp = Vector3.Dot( pointerDir, Vector3.up );
00325             float dotForward = Vector3.Dot( pointerDir, player.hmdTransform.forward );
00326             bool pointerAtBadAngle = false;
00327             if ( ( dotForward > 0 && dotUp > 0.75f ) || ( dotForward < 0.0f && dotUp > 0.5f ) )
00328             {
00329                 pointerAtBadAngle = true;
00330             }
00331 
00332             //Trace to see if the pointer hit anything
00333             RaycastHit hitInfo;
00334             teleportArc.SetArcData( pointerStart, arcVelocity, true, pointerAtBadAngle );
00335             if ( teleportArc.DrawArc( out hitInfo ) )
00336             {
00337                 hitSomething = true;
00338                 hitTeleportMarker = hitInfo.collider.GetComponentInParent<TeleportMarkerBase>();
00339             }
00340 
00341             if ( pointerAtBadAngle )
00342             {
00343                 hitTeleportMarker = null;
00344             }
00345 
00346             HighlightSelected( hitTeleportMarker );
00347 
00348             if ( hitTeleportMarker != null ) //Hit a teleport marker
00349             {
00350                 if ( hitTeleportMarker.locked )
00351                 {
00352                     teleportArc.SetColor( pointerLockedColor );
00353 #if (UNITY_5_4)
00354                     pointerLineRenderer.SetColors( pointerLockedColor, pointerLockedColor );
00355 #else
00356                     pointerLineRenderer.startColor = pointerLockedColor;
00357                     pointerLineRenderer.endColor = pointerLockedColor;
00358 #endif
00359                     destinationReticleTransform.gameObject.SetActive( false );
00360                 }
00361                 else
00362                 {
00363                     teleportArc.SetColor( pointerValidColor );
00364 #if (UNITY_5_4)
00365                     pointerLineRenderer.SetColors( pointerValidColor, pointerValidColor );
00366 #else
00367                     pointerLineRenderer.startColor = pointerValidColor;
00368                     pointerLineRenderer.endColor = pointerValidColor;
00369 #endif
00370                     destinationReticleTransform.gameObject.SetActive( hitTeleportMarker.showReticle );
00371                 }
00372 
00373                 offsetReticleTransform.gameObject.SetActive( true );
00374 
00375                 invalidReticleTransform.gameObject.SetActive( false );
00376 
00377                 pointedAtTeleportMarker = hitTeleportMarker;
00378                 pointedAtPosition = hitInfo.point;
00379 
00380                 if ( showPlayAreaMarker )
00381                 {
00382                     //Show the play area marker if this is a teleport area
00383                     TeleportArea teleportArea = pointedAtTeleportMarker as TeleportArea;
00384                     if ( teleportArea != null && !teleportArea.locked && playAreaPreviewTransform != null )
00385                     {
00386                         Vector3 offsetToUse = playerFeetOffset;
00387 
00388                         //Adjust the actual offset to prevent the play area marker from moving too much
00389                         if ( !movedFeetFarEnough )
00390                         {
00391                             float distanceFromStartingOffset = Vector3.Distance( playerFeetOffset, startingFeetOffset );
00392                             if ( distanceFromStartingOffset < 0.1f )
00393                             {
00394                                 offsetToUse = startingFeetOffset;
00395                             }
00396                             else if ( distanceFromStartingOffset < 0.4f )
00397                             {
00398                                 offsetToUse = Vector3.Lerp( startingFeetOffset, playerFeetOffset, ( distanceFromStartingOffset - 0.1f ) / 0.3f );
00399                             }
00400                             else
00401                             {
00402                                 movedFeetFarEnough = true;
00403                             }
00404                         }
00405 
00406                         playAreaPreviewTransform.position = pointedAtPosition + offsetToUse;
00407 
00408                         showPlayAreaPreview = true;
00409                     }
00410                 }
00411 
00412                 pointerEnd = hitInfo.point;
00413             }
00414             else //Hit neither
00415             {
00416                 destinationReticleTransform.gameObject.SetActive( false );
00417                 offsetReticleTransform.gameObject.SetActive( false );
00418 
00419                 teleportArc.SetColor( pointerInvalidColor );
00420 #if (UNITY_5_4)
00421                 pointerLineRenderer.SetColors( pointerInvalidColor, pointerInvalidColor );
00422 #else
00423                 pointerLineRenderer.startColor = pointerInvalidColor;
00424                 pointerLineRenderer.endColor = pointerInvalidColor;
00425 #endif
00426                 invalidReticleTransform.gameObject.SetActive( !pointerAtBadAngle );
00427 
00428                 //Orient the invalid reticle to the normal of the trace hit point
00429                 Vector3 normalToUse = hitInfo.normal;
00430                 float angle = Vector3.Angle( hitInfo.normal, Vector3.up );
00431                 if ( angle < 15.0f )
00432                 {
00433                     normalToUse = Vector3.up;
00434                 }
00435                 invalidReticleTargetRotation = Quaternion.FromToRotation( Vector3.up, normalToUse );
00436                 invalidReticleTransform.rotation = Quaternion.Slerp( invalidReticleTransform.rotation, invalidReticleTargetRotation, 0.1f );
00437 
00438                 //Scale the invalid reticle based on the distance from the player
00439                 float distanceFromPlayer = Vector3.Distance( hitInfo.point, player.hmdTransform.position );
00440                 float invalidReticleCurrentScale = Util.RemapNumberClamped( distanceFromPlayer, invalidReticleMinScaleDistance, invalidReticleMaxScaleDistance, invalidReticleMinScale, invalidReticleMaxScale );
00441                 invalidReticleScale.x = invalidReticleCurrentScale;
00442                 invalidReticleScale.y = invalidReticleCurrentScale;
00443                 invalidReticleScale.z = invalidReticleCurrentScale;
00444                 invalidReticleTransform.transform.localScale = invalidReticleScale;
00445 
00446                 pointedAtTeleportMarker = null;
00447 
00448                 if ( hitSomething )
00449                 {
00450                     pointerEnd = hitInfo.point;
00451                 }
00452                 else
00453                 {
00454                     pointerEnd = teleportArc.GetArcPositionAtTime( teleportArc.arcDuration );
00455                 }
00456 
00457                 //Debug floor
00458                 if ( debugFloor )
00459                 {
00460                     floorDebugSphere.gameObject.SetActive( false );
00461                     floorDebugLine.gameObject.SetActive( false );
00462                 }
00463             }
00464 
00465             if ( playAreaPreviewTransform != null )
00466             {
00467                 playAreaPreviewTransform.gameObject.SetActive( showPlayAreaPreview );
00468             }
00469 
00470             if ( !showOffsetReticle )
00471             {
00472                 offsetReticleTransform.gameObject.SetActive( false );
00473             }
00474 
00475             destinationReticleTransform.position = pointedAtPosition;
00476             invalidReticleTransform.position = pointerEnd;
00477             onActivateObjectTransform.position = pointerEnd;
00478             onDeactivateObjectTransform.position = pointerEnd;
00479             offsetReticleTransform.position = pointerEnd - playerFeetOffset;
00480 
00481             reticleAudioSource.transform.position = pointedAtPosition;
00482 
00483             pointerLineRenderer.SetPosition( 0, pointerStart );
00484             pointerLineRenderer.SetPosition( 1, pointerEnd );
00485         }
