00488         //-------------------------------------------------
00489         void FixedUpdate()
00490         {
00491             if ( !visible )
00492             {
00493                 return;
00494             }
00495 
00496             if ( debugFloor )
00497             {
00498                 //Debug floor
00499                 TeleportArea teleportArea = pointedAtTeleportMarker as TeleportArea;
00500                 if ( teleportArea != null )
00501                 {
00502                     if ( floorFixupMaximumTraceDistance > 0.0f )
00503                     {
00504                         floorDebugSphere.gameObject.SetActive( true );
00505                         floorDebugLine.gameObject.SetActive( true );
00506 
00507                         RaycastHit raycastHit;
00508                         Vector3 traceDir = Vector3.down;
00509                         traceDir.x = 0.01f;
00510                         if ( Physics.Raycast( pointedAtPosition + 0.05f * traceDir, traceDir, out raycastHit, floorFixupMaximumTraceDistance, floorFixupTraceLayerMask ) )
00511                         {
00512                             floorDebugSphere.transform.position = raycastHit.point;
00513                             floorDebugSphere.material.color = Color.green;
00514 #if (UNITY_5_4)
00515                             floorDebugLine.SetColors( Color.green, Color.green );
00516 #else
00517                             floorDebugLine.startColor = Color.green;
00518                             floorDebugLine.endColor = Color.green;
00519 #endif
00520                             floorDebugLine.SetPosition( 0, pointedAtPosition );
00521                             floorDebugLine.SetPosition( 1, raycastHit.point );
00522                         }
00523                         else
00524                         {
00525                             Vector3 rayEnd = pointedAtPosition + ( traceDir * floorFixupMaximumTraceDistance );
00526                             floorDebugSphere.transform.position = rayEnd;
00527                             floorDebugSphere.material.color = Color.red;
00528 #if (UNITY_5_4)
00529                             floorDebugLine.SetColors( Color.red, Color.red );
00530 #else
00531                             floorDebugLine.startColor = Color.red;
00532                             floorDebugLine.endColor = Color.red;
00533 #endif
00534                             floorDebugLine.SetPosition( 0, pointedAtPosition );
00535                             floorDebugLine.SetPosition( 1, rayEnd );
00536                         }
00537                     }
00538                 }
00539             }
00540         }
