00616         //-------------------------------------------------
00617         private void HidePointer()
00618         {
00619             if ( visible )
00620             {
00621                 pointerHideStartTime = Time.time;
00622             }
00623 
00624             visible = false;
00625             if ( pointerHand )
00626             {
00627                 if ( ShouldOverrideHoverLock() )
00628                 {
00629                     //Restore the original hovering interactable on the hand
00630                     if ( originalHoverLockState == true )
00631                     {
00632                         pointerHand.HoverLock( originalHoveringInteractable );
00633                     }
00634                     else
00635                     {
00636                         pointerHand.HoverUnlock( null );
00637                     }
00638                 }
00639 
00640                 //Stop looping sound
00641                 loopingAudioSource.Stop();
00642                 PlayAudioClip( pointerAudioSource, pointerStopSound );
00643             }
00644             teleportPointerObject.SetActive( false );
00645 
00646             teleportArc.Hide();
00647 
00648             foreach ( TeleportMarkerBase teleportMarker in teleportMarkers )
00649             {
00650                 if ( teleportMarker != null && teleportMarker.markerActive && teleportMarker.gameObject != null )
00651                 {
00652                     teleportMarker.gameObject.SetActive( false );
00653                 }
00654             }
00655 
00656             destinationReticleTransform.gameObject.SetActive( false );
00657             invalidReticleTransform.gameObject.SetActive( false );
00658             offsetReticleTransform.gameObject.SetActive( false );
00659 
00660             if ( playAreaPreviewTransform != null )
00661             {
00662                 playAreaPreviewTransform.gameObject.SetActive( false );
00663             }
00664 
00665             if ( onActivateObjectTransform.gameObject.activeSelf )
00666             {
00667                 onActivateObjectTransform.gameObject.SetActive( false );
00668             }
00669             onDeactivateObjectTransform.gameObject.SetActive( true );
00670 
00671             pointerHand = null;
00672         }
