00675         //-------------------------------------------------
00676         private void ShowPointer( Hand newPointerHand, Hand oldPointerHand )
00677         {
00678             if ( !visible )
00679             {
00680                 pointedAtTeleportMarker = null;
00681                 pointerShowStartTime = Time.time;
00682                 visible = true;
00683                 meshFading = true;
00684 
00685                 teleportPointerObject.SetActive( false );
00686                 teleportArc.Show();
00687 
00688                 foreach ( TeleportMarkerBase teleportMarker in teleportMarkers )
00689                 {
00690                     if ( teleportMarker.markerActive && teleportMarker.ShouldActivate( player.feetPositionGuess ) )
00691                     {
00692                         teleportMarker.gameObject.SetActive( true );
00693                         teleportMarker.Highlight( false );
00694                     }
00695                 }
00696 
00697                 startingFeetOffset = player.trackingOriginTransform.position - player.feetPositionGuess;
00698                 movedFeetFarEnough = false;
00699 
00700                 if ( onDeactivateObjectTransform.gameObject.activeSelf )
00701                 {
00702                     onDeactivateObjectTransform.gameObject.SetActive( false );
00703                 }
00704                 onActivateObjectTransform.gameObject.SetActive( true );
00705 
00706                 loopingAudioSource.clip = pointerLoopSound;
00707                 loopingAudioSource.loop = true;
00708                 loopingAudioSource.Play();
00709                 loopingAudioSource.volume = 0.0f;
00710             }
00711 
00712 
00713             if ( oldPointerHand )
00714             {
00715                 if ( ShouldOverrideHoverLock() )
00716                 {
00717                     //Restore the original hovering interactable on the hand
00718                     if ( originalHoverLockState == true )
00719                     {
00720                         oldPointerHand.HoverLock( originalHoveringInteractable );
00721                     }
00722                     else
00723                     {
00724                         oldPointerHand.HoverUnlock( null );
00725                     }
00726                 }
00727             }
00728 
00729             pointerHand = newPointerHand;
00730 
00731             if ( visible && oldPointerHand != pointerHand )
00732             {
00733                 PlayAudioClip( pointerAudioSource, pointerStartSound );
00734             }
00735 
00736             if ( pointerHand )
00737             {
00738                 pointerStartTransform = GetPointerStartTransform( pointerHand );
00739 
00740                 if ( pointerHand.currentAttachedObject != null )
00741                 {
00742                     allowTeleportWhileAttached = pointerHand.currentAttachedObject.GetComponent<AllowTeleportWhileAttachedToHand>();
00743                 }
00744 
00745                 //Keep track of any existing hovering interactable on the hand
00746                 originalHoverLockState = pointerHand.hoverLocked;
00747                 originalHoveringInteractable = pointerHand.hoveringInteractable;
00748 
00749                 if ( ShouldOverrideHoverLock() )
00750                 {
00751                     pointerHand.HoverLock( null );
00752                 }
00753 
00754                 pointerAudioSource.transform.SetParent( pointerStartTransform );
00755                 pointerAudioSource.transform.localPosition = Vector3.zero;
00756 
00757                 loopingAudioSource.transform.SetParent( pointerStartTransform );
00758                 loopingAudioSource.transform.localPosition = Vector3.zero;
00759             }
00760         }
