00763         //-------------------------------------------------
00764         private void UpdateTeleportColors()
00765         {
00766             float deltaTime = Time.time - pointerShowStartTime;
00767             if ( deltaTime > meshFadeTime )
00768             {
00769                 meshAlphaPercent = 1.0f;
00770                 meshFading = false;
00771             }
00772             else
00773             {
00774                 meshAlphaPercent = Mathf.Lerp( 0.0f, 1.0f, deltaTime / meshFadeTime );
00775             }
00776 
00777             //Tint color for the teleport points
00778             foreach ( TeleportMarkerBase teleportMarker in teleportMarkers )
00779             {
00780                 teleportMarker.SetAlpha( fullTintAlpha * meshAlphaPercent, meshAlphaPercent );
00781             }
00782         }
