00810         //-------------------------------------------------
00811         private void TryTeleportPlayer()
00812         {
00813             if ( visible && !teleporting )
00814             {
00815                 if ( pointedAtTeleportMarker != null && pointedAtTeleportMarker.locked == false )
00816                 {
00817                     //Pointing at an unlocked teleport marker
00818                     teleportingToMarker = pointedAtTeleportMarker;
00819                     InitiateTeleportFade();
00820 
00821                     CancelTeleportHint();
00822                 }
00823             }
00824         }
