00827         //-------------------------------------------------
00828         private void InitiateTeleportFade()
00829         {
00830             teleporting = true;
00831 
00832             currentFadeTime = teleportFadeTime;
00833 
00834             TeleportPoint teleportPoint = teleportingToMarker as TeleportPoint;
00835             if ( teleportPoint != null && teleportPoint.teleportType == TeleportPoint.TeleportPointType.SwitchToNewScene )
00836             {
00837                 currentFadeTime *= 3.0f;
00838                 Teleport.ChangeScene.Send( currentFadeTime );
00839             }
00840 
00841             SteamVR_Fade.Start( Color.clear, 0 );
00842             SteamVR_Fade.Start( Color.black, currentFadeTime );
00843 
00844             headAudioSource.transform.SetParent( player.hmdTransform );
00845             headAudioSource.transform.localPosition = Vector3.zero;
00846             PlayAudioClip( headAudioSource, teleportSound );
00847 
00848             Invoke( "TeleportPlayer", currentFadeTime );
00849         }
