00851 
00852         //-------------------------------------------------
00853         private void TeleportPlayer()
00854         {
00855             teleporting = false;
00856 
00857             Teleport.PlayerPre.Send( pointedAtTeleportMarker );
00858 
00859             SteamVR_Fade.Start( Color.clear, currentFadeTime );
00860 
00861             TeleportPoint teleportPoint = teleportingToMarker as TeleportPoint;
00862             Vector3 teleportPosition = pointedAtPosition;
00863 
00864             if ( teleportPoint != null )
00865             {
00866                 teleportPosition = teleportPoint.transform.position;
00867 
00868                 //Teleport to a new scene
00869                 if ( teleportPoint.teleportType == TeleportPoint.TeleportPointType.SwitchToNewScene )
00870                 {
00871                     teleportPoint.TeleportToScene();
00872                     return;
00873                 }
00874             }
00875 
00876             // Find the actual floor position below the navigation mesh
00877             TeleportArea teleportArea = teleportingToMarker as TeleportArea;
00878             if ( teleportArea != null )
00879             {
00880                 if ( floorFixupMaximumTraceDistance > 0.0f )
00881                 {
00882                     RaycastHit raycastHit;
00883                     if ( Physics.Raycast( teleportPosition + 0.05f * Vector3.down, Vector3.down, out raycastHit, floorFixupMaximumTraceDistance, floorFixupTraceLayerMask ) )
00884                     {
00885                         teleportPosition = raycastHit.point;
00886                     }
00887                 }
00888             }
00889 
00890             if ( teleportingToMarker.ShouldMovePlayer() )
00891             {
00892                 Vector3 playerFeetOffset = player.trackingOriginTransform.position - player.feetPositionGuess;
00893                 player.trackingOriginTransform.position = teleportPosition + playerFeetOffset;
00894             }
00895             else
00896             {
00897                 teleportingToMarker.TeleportPlayer( pointedAtPosition );
00898             }
00899 
00900             Teleport.Player.Send( pointedAtTeleportMarker );
00901         }
