00904         //-------------------------------------------------
00905         private void HighlightSelected( TeleportMarkerBase hitTeleportMarker )
00906         {
00907             if ( pointedAtTeleportMarker != hitTeleportMarker ) //Pointing at a new teleport marker
00908             {
00909                 if ( pointedAtTeleportMarker != null )
00910                 {
00911                     pointedAtTeleportMarker.Highlight( false );
00912                 }
00913 
00914                 if ( hitTeleportMarker != null )
00915                 {
00916                     hitTeleportMarker.Highlight( true );
00917 
00918                     prevPointedAtPosition = pointedAtPosition;
00919                     PlayPointerHaptic( !hitTeleportMarker.locked );
00920 
00921                     PlayAudioClip( reticleAudioSource, goodHighlightSound );
00922 
00923                     loopingAudioSource.volume = loopingAudioMaxVolume;
00924                 }
00925                 else if ( pointedAtTeleportMarker != null )
00926                 {
00927                     PlayAudioClip( reticleAudioSource, badHighlightSound );
00928 
00929                     loopingAudioSource.volume = 0.0f;
00930                 }
00931             }
00932             else if ( hitTeleportMarker != null ) //Pointing at the same teleport marker
00933             {
00934                 if ( Vector3.Distance( prevPointedAtPosition, pointedAtPosition ) > 1.0f )
00935                 {
00936                     prevPointedAtPosition = pointedAtPosition;
00937                     PlayPointerHaptic( !hitTeleportMarker.locked );
00938                 }
00939             }
00940         }
