00952         //-------------------------------------------------
00953         public void CancelTeleportHint()
00954         {
00955             if ( hintCoroutine != null )
00956             {
00957                 ControllerButtonHints.HideTextHint(player.leftHand, teleportAction);
00958                 ControllerButtonHints.HideTextHint(player.rightHand, teleportAction);
00959 
00960                 StopCoroutine( hintCoroutine );
00961                 hintCoroutine = null;
00962             }
00963 
00964             CancelInvoke( "ShowTeleportHint" );
00965         }
