00967 
00968         //-------------------------------------------------
00969         private IEnumerator TeleportHintCoroutine()
00970         {
00971             float prevBreakTime = Time.time;
00972             float prevHapticPulseTime = Time.time;
00973 
00974             while ( true )
00975             {
00976                 bool pulsed = false;
00977 
00978                 //Show the hint on each eligible hand
00979                 foreach ( Hand hand in player.hands )
00980                 {
00981                     bool showHint = IsEligibleForTeleport( hand );
00982                     bool isShowingHint = !string.IsNullOrEmpty( ControllerButtonHints.GetActiveHintText( hand, teleportAction) );
00983                     if ( showHint )
00984                     {
00985                         if ( !isShowingHint )
00986                         {
00987                             ControllerButtonHints.ShowTextHint( hand, teleportAction, "Teleport" );
00988                             prevBreakTime = Time.time;
00989                             prevHapticPulseTime = Time.time;
00990                         }
00991 
00992                         if ( Time.time > prevHapticPulseTime + 0.05f )
00993                         {
00994                             //Haptic pulse for a few seconds
00995                             pulsed = true;
00996 
00997                             hand.TriggerHapticPulse( 500 );
00998                         }
00999                     }
01000                     else if ( !showHint && isShowingHint )
01001                     {
01002                         ControllerButtonHints.HideTextHint( hand, teleportAction);
01003                     }
01004                 }
01005 
01006                 if ( Time.time > prevBreakTime + 3.0f )
01007                 {
01008                     //Take a break for a few seconds
01009                     yield return new WaitForSeconds( 3.0f );
01010 
01011                     prevBreakTime = Time.time;
01012                 }
01013 
01014                 if ( pulsed )
01015                 {
01016                     prevHapticPulseTime = Time.time;
01017                 }
01018 
01019                 yield return null;
01020             }
01021         }
01022 
01023 
01024         //-------------------------------------------------
01025         public bool IsEligibleForTeleport( Hand hand )
01026         {
01027             if ( hand == null )
01028             {
01029                 return false;
01030             }
01031 
01032             if ( !hand.gameObject.activeInHierarchy )
01033             {
01034                 return false;
01035             }
01036 
01037             if ( hand.hoveringInteractable != null )
01038             {
01039                 return false;
01040             }
01041 
01042             if ( hand.noSteamVRFallbackCamera == null )
01043             {
01044                 if ( hand.isActive == false)
01045                 {
01046                     return false;
01047                 }
01048 
01049                 //Something is attached to the hand
01050                 if ( hand.currentAttachedObject != null )
01051                 {
01052                     AllowTeleportWhileAttachedToHand allowTeleportWhileAttachedToHand = hand.currentAttachedObject.GetComponent<AllowTeleportWhileAttachedToHand>();
01053 
01054                     if ( allowTeleportWhileAttachedToHand != null && allowTeleportWhileAttachedToHand.teleportAllowed == true )
01055                     {
01056                         return true;
01057                     }
01058                     else
01059                     {
01060                         return false;
01061                     }
01062                 }
01063             }
01064 
01065             return true;
01066         }
01067 
01068 
01069         //-------------------------------------------------
01070         private bool ShouldOverrideHoverLock()
01071         {
01072             if ( !allowTeleportWhileAttached || allowTeleportWhileAttached.overrideHoverLock )
01073             {
01074                 return true;
01075             }
01076 
01077             return false;
01078         }
