00229         public override void Rebuild(CanvasUpdate update)
00230         {
00231             if (this == null) return;
00232 
00233             if (update == CanvasUpdate.Prelayout)
00234             {
00235                 if (m_autoSizeTextContainer)
00236                 {
00237                     m_rectTransform.sizeDelta = GetPreferredValues(Mathf.Infinity, Mathf.Infinity);
00238                 }
00239             }
00240             else if (update == CanvasUpdate.PreRender)
00241             {
00242                 this.OnPreRenderObject();
00243                 m_verticesAlreadyDirty = false;
00244                 m_layoutAlreadyDirty = false;
00245 
00246                 if (!m_isMaterialDirty) return;
00247 
00248                 UpdateMaterial();
00249                 m_isMaterialDirty = false;
00250             }
00251         }
